CREATE TABLE `schiri_ergebnis` ( `schiri_test_id` INT NOT NULL AUTO_INCREMENT , `spieler_id` INT NOT NULL , `gestellte_fragen` VARCHAR(500) NULL , `gesetzte_antworten` VARCHAR(500) NULL , `level` ENUM('junior','basis','fortgeschritten') NULL , `bestanden` ENUM('Ja','Nein') NULL , `kommentar` VARCHAR(500) NULL , `timestamp` DATETIME NULL , `saison` INT NOT NULL , `schiri_test_version` INT NOT NULL , PRIMARY KEY (`schiri_test_id`)) ENGINE = InnoDB