INSERT INTO schiri_test VALUES
    (1,'1','LJB','Ansgar Pölking
Max: ist mir zu trivial','Welche Teile gehören zum Einrad?','Ball','Speiche','Sattel','Reifen','Schläger','Tor','2#3#4',NULL,'Einradteile sind im Einrad verbaut.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (2,'8','L','Ansgar Pölking','Welches Fehlverhalten lag im gezeigten Video vor?','Nicht-regelkonforme Trikots der Teams','SUB des gelben Spielers','Hoher Schläger des gelben Spielers',NULL,NULL,NULL,'3','8.10',NULL,1,NULL,'frage_0002.mp4',NULL,NULL,'nein',NULL),
    (3,'8','L','Ansgar Pölking','Wie entscheidest du im Video richtig?','Da ich nicht absolut sicher sein kann, ob der Spieler vom Schläger getroffen wurde, lasse ich weiterspielen.','Ich kann sicher davon ausgehen, dass der Spieler getroffen wurde, pfeife ab und gebe einen Freistoß.','Ich kann sicher davon ausgehen, dass der Spieler getroffen wurde, rufe laut Vorteil und lasse den Vorteil ausspielen. Sollte das blaue Team am Ende des Videos den Ball verlieren, pfeife ich das ursprüngliche Vergehen ab.','Ich kann davon ausgehen, dass der Spieler getroffen wurde. Aber da die Situation das weitere Spielgeschehen nicht beeinflusst, lasse ich das Spiel weiterlaufen.',NULL,NULL,'3',NULL,'Spieler dürfen nicht vom Schläger getroffen werden. Es ist mit gesunden Menschenverstand davon auszugehen, dass der Spieler getroffen wurde. Da der gefoulte Spieler weiter im Ballbesitz ist, gilt die Vorteilsregel.',1,NULL,'frage_0003.mp4',NULL,NULL,'nein',NULL),
    (4,'5','LJBF','Ansgar Pölking','Wie entscheidest du auf Grundlage dieses Bildes richtig?','Ich pfeife ab und gebe einen Bully.','Ich gebe einen Freistoß für das rote Team.','Ich gebe einen Freistoß für das blaue Team.','Da der Körperkontakt von beiden hervorgerufen wurde, lasse ich das Spiel weiterlaufen.',NULL,NULL,'1',NULL,'Einradhockey ist ein kontaktloser Sport. Damit harte Spiele nicht eskalieren, pfeife ich ab. Ich gebe einen Bully, da ich nicht klar erkennen konnte, wer den Körperkontakt herbeigeführt hatte.',1,'frage_0004.jpg',NULL,NULL,NULL,'nein',NULL),
    (5,'10','LJBF',NULL,'Ein Spieler, der nach 2 min Feldverweis wieder am Spiel teilnimmt, wird sofort wieder durch unsportliches Verhalten auffällig. Wie entscheidest Du?','Erneuter Feldverweis. Diesmal für 5 min.','Da er gerade schon eine Strafe hatte, kann er sich jetzt wieder einige Fehlverhalten erlauben, bevor er erneut bestraft wird.','Es reicht aus, den Spieler zu bitten, sich besser zu benehmen.','Bei gewalttätigem Verhalten: Spieldauersperre.',NULL,NULL,'1#4','9.5ef',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (6,'11','LJBF',NULL,'Der blaue Torwart hält den Ball,  den ein roter Spieler Richtung Tor schießt, während er mit beiden Füßen auf dem Boden steht. Du bist dir nicht absolut sicher, dass der Ball ins Tor gegangen wäre. Was entscheidest Du?','6,50 m für das angreifende Team.','Straftor.','Tor ab.','Weiterspielen, da ich mir nicht sicher bin.',NULL,NULL,'1','9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (7,'8','LJBF','Larissa: Hier haben wir eine gewisse Interpreatation drin, da in den Regeln nichts von anderen Spielern in der Nähre steht.   
Max: ja, sehe ich auch so. Besser: und kein anderer Spieler gefährdet wird.
Max: Wenn wir bei der richtigen eine Begründung in der Antwort schreiben sollten wir das auch bei den anderen machen','Der Torwart hält den Ball mit dem Schläger direkt unter der Latte, die Kelle ist dabei über seiner Sattelhöhe. In direkter Tornähe befindet sich kein anderer Spieler. Was entscheidest Du?','Kein Foul. Weiterspielen.','Hoher Schläger. Es gibt Straftor.','Hoher Schläger. Es gibt 6,50 m.','Hoher Schläger. Es gibt Eckball.',NULL,NULL,'1','8.10',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Stimmt. Die Sicherheit der Spieler hat Vorrang. Könnte man als Anregung für einen Ergänzung aufnehmen. '),
    (8,'8','LJBF','Max: wenn der Ball evtl. wieder vor das Tor gedotzt wäre?','Der Torwart hält den Ball mit dem Schläger über der Latte. Der Ball wäre nicht ins Tor gegangen. Was entscheidest Du?','Hoher Schläger. Torab.','Kein Foul.','6,50m.','Ecke.',NULL,NULL,'1#4','8.10','Auch in Tornähe darf der Schläger nicht über die Höhe der Querlatte gehoben werden.',1,NULL,NULL,NULL,NULL,'nein','Vorteil möglich? '),
    (9,'11','LJBF','Max: immer Begründung oder garnicht','Ein roter Spieler wird beim Torschuss von einem blauen Spieler gefoult und fällt. Der rote Spieler schießt den Ball, bevor er den Boden berührt. Der Ball geht ins Tor. Wie entscheidest Du?','Vorteil. Das Tor zählt.','Foul bei klarer Torchance. 6,50m. ','Tor zählt nicht. Freistoß. ','Bully am Mittelkreis.',NULL,NULL,'1','9a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (10,'11','LJBF','Larissa: Ist ein Torschuss im 6,5 Bereich nicht immer eine klare Torchance? Rolf: Wenn der Stürmer in der Nähe des Eckpunktes gefoult wurde, hatte er vermutlich keine gute Torchance.','Ein roter Spieler wird beim Torschuss von einem blauen Spieler etwa am blauen 6,50 m Punkt gefoult. Es wird keine klare Torchance verhindert. Wie entscheidest Du?','Ecke für Rot.','6,50 m für Rot, da Foul im 6,50m Bereich. ','Weiterspielen.','Vorteilentscheidung möglich.',NULL,NULL,'1#4','9.2abc',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Ich finde den Einwand von Larissa berechtigt. Es ist sicher nicht 100% abzugrenzen was ein Torschuß ist und was ein "fester Schuß vors Tor." Dahinter steht die Frage wie eine "direkte Torchance" zu interpretieren ist? Jeder Schuß aufs Tor ist eine direkte Torchance? Hier hat die Regel eine große Schwäche. Die Frage finde ich so zu unklar. @Rolf: Auch aus spitzen Winkel kann man ins Tor schießen. Hinter der Torlinie ist es kaum ein Torschuß. '),
    (11,'11','LJB',NULL,'Ein roter Spieler wird beim Torschuss kurz hinter der Mittellinie (auf der Seite von Team Blau) von einem blauen Spieler gefoult. Dieser verhindert dadurch eine klare Torchance. Wie entscheidest Du?','6,50 m für Rot. Immer bei klarer Torchance.','Freistoß für Rot. Das Foul war nicht in der Torzone.','Penalty für Rot. Klare Torchance aber nicht in der Torzone.','Wenn kein Spieler mehr zwischen gefoultem Angreifer und Torwart stand 6,50 m. Sonst Ecke.',NULL,NULL,'1','9.2abc',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Siehe Frage oben. '),
    (12,'7','LBF',NULL,'Ein roter Spieler schießt auf das Tor. Der Ball wird jedoch von einem auf dem Boden liegenden Einrad des blauen Teams direkt vor dem Tor, in dem noch der Torwart steht, abgefangen. Wie entscheidest Du?','6,50 m für Rot. Verhindern einer klaren Torchance.','Straftor für Rot. Der Ball wäre wahrscheinlich ins Tor gegangen.','Bully.','Eckball.',NULL,NULL,'1','9.3','In diesem Fall hat das auf dem Boden liegende Einrad kein sicheres Tor verhindert, da der Torwart den Ball noch hätte halten können. Verhindert wurde aber eine klare Torchance, daher 6,50m.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (13,'7','LJB',NULL,'Ein roter Spieler schießt auf das blaue Tor. Der Ball wird jedoch von einem am Boden liegenden Einrad des blauen Teams abgefangen. Der Ball wäre sicher nicht ins Tor gegangen. Wie entscheidest Du?','Eckball für Rot. Behinderung.','6,50 m für Rot. Verhindern einer klaren Torchance durch Foulspiel.','Weiterspielen. Kein Foul.','Vorteilsentscheidung ist möglich.',NULL,NULL,'1#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (14,'7','LB','Larissa: für mich ist das situationsabhängig, ob das ein Vorteil ist. Kann der blaue Spieler direkt einen Konter fahren, dann ja. Nimmt ihm ein roter Spieler direkt wieder den Ball ab und macht ein Tor, dann nein.','Nach einem Torschuss eines roten Spielers prallt der Ball gegen ein auf dem Boden liegendes Einrad des gleichen Teams. Das blaue Team kommt daraufhin in Ballbesitz. Wie entscheidest Du?','Immer Freistoß für Blau. Behinderung.','Immer Vorteil.','Vorteil für Blau, wenn sich tatsächlich ein Vorteil ergibt. Sonst Freistoß für Blau.','Wenn durch das am Boden liegende Rad eine direkte Torchance für Rot verhindert wird, Bully.',NULL,NULL,'3','9a',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Habs umformuliert'),
    (15,'7','LJB','Larissa: Ich finde die Regel hier etwas uneindeutig, da sie nur von "Hindernis für das gegnerische Team" spricht.','Nach einem Torschuss eines roten Spielers prallt der Ball gegen ein vor dem Tor liegendes Einrad des gleichen Teams. Ein roter Spieler kommt danach in Ballbesitz und schießt ein Tor. Wie entscheidest Du?','Torab für das blaue Team. Hindernis. ','6,50m. Verhindern einer klaren Torchance.','Straftor für Blau. Erzielen eines Tores trotz klarer Regelwidrigkeit.','Das Tor von Rot zählt nicht.',NULL,NULL,'1#4',NULL,'Da das auf dem Boden liegende Einrad vom gleichen Team ist wie der Torschütze gibt es hier keinen Vorteil!',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (16,'8','LJB',NULL,'Ein roter Spieler hat bei einem Torschuss außerhalb der blauen Torzone einen hohen Schläger. Wie entscheidest Du?','Weiterspielen. Beim Torschuss ist hoher Schläger erlaubt.','Hoher Schläger. Freistoß für das blaue Team am Ort des Schusses.','Hoher Schläger. Torab.','Vorteil. Falls der Ball ins Tor geht, zählt das Tor.',NULL,NULL,'2','8.10#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (17,'8','LJB',NULL,'Ein roter Spieler hat bei einem Torschuss innerhalb der blauen Torzone einen hohen Schläger. Wie entscheidest Du?','Hoher Schläger. Torab.','Weiterspielen. Beim Torschuss ist hoher Schläger erlaubt.','Hoher Schläger. Freistoß am Ort des Schusses.','Vorteil. Falls der Ball ins Tor geht, zählt das Tor.',NULL,NULL,'1','8.10#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (18,'11','LJB','Rolf: Wirklich immer nur Ecke, wenn das bei einem Torab passiert? Was ist, wenn ein gegnerischer Stürmer nach der 1. Berührung eine gute Torchance hat? Ist dann auch 6.50m möglich?','Eine rote Spielerin will einen Freistoß (auch Torab oder Ecke) ausführen. Nach dem Anpfiff berührt sie den Ball zunächst leicht und schießt ihn mit einer zweiten Berührung zu ihrem Mitspieler. Wie entscheidest Du?','Freistoß für das blaue Team.','Weiterspielen.','Passiert dies bei einem Tor ab, gibt es Eckball für das blaue Team.','Wenn die erste Berührung sehr leicht war, kann man hier weiterspielen lassen.',NULL,NULL,'1#3','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein','@rolf (Christian). Interessanter Aspekt. In den Regeln nicht geklärt. Da der Stürmer ja noch nicht am Ball gewesen sein kann (sonst keine doppelte Berührung) würde ich aber keine klare Torchance zuerkennen. '),
    (19,'8','LBF',NULL,'Ein roter Spieler hält den Schläger, ohne das Ende abzudecken, während er nicht in der Nähe des Spielgeschehens ist. Wie entscheidest Du?','Weiterspielen, ggf. bei nächster Unterbrechung den Spieler darauf hinweisen.','Spiel sofort unterbrechen. Freistoß für Blau.','Dies ist erlaubt, sofern es nicht in der Nähe des Balles passiert.','Freistoß für Blau und 2 Minuten Zeitstrafe für den roten Spieler.',NULL,NULL,'1','8.9','Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (20,'8','LJBF',NULL,'Eine blaue Spielerin hält den Schläger, ohne das obere Ende abzudecken, während er sich im Zweikampf um den Ball befindet. Wie entscheidest Du?','Gefährliches Spiel. Freistoß für Rot.','Nächste Spielunterbrechung abwarten. Spielerin dann ermahnen.','Gefährliches Spiel. 6,50 m, wenn es eine direkte Torchance verhindert.','Vorteil für Rot möglich.',NULL,NULL,'1#2#3#4','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (21,'11','LJB',NULL,'Der blaue Torwart hält einen Schuss aufs Tor, während er sich am Tor festhält. Der Ball wäre mit Sicherheit ins Tor gegangen. Wie entscheidest Du?','Straftor für Rot.','6,50 m für Rot.','Weiterspielen. Kein Foul.','Bully am Eckpunkt.',NULL,NULL,'1','5.3#9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (22,'7','LJB',NULL,'Der blaue Torwart spielt den Ball neben dem Tor, während er sich am Tor festhält. Es war kein roter Spieler in der Nähe. Wie entscheidest Du?','Ecke für Rot.','Straftor.','Bully am Eckpunkt.','Weiterspielen. Kein Foul.',NULL,NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (23,'8','LJBF',NULL,'Ein roter Spieler hebt im Kampf um den Ball seinen Schläger über Hüfthöhe. Der blaue Spieler bleibt in Ballbesitz. Wie entscheidest Du?','Wenn es für den roten Spieler einen Vorteil darstellt, rufe Ich laut "Vorteil" und lasse das Spiel laufen. ','Hoher Schläger. Da es gefährliches Spiel ist, unterbreche ich sofort und gebe Freistoß für Blau.','Bei Anfängern kann dies akzeptiert werden.','Weiterspielen. Im Kampf um den Ball ist hoher Schläger erlaubt. ',NULL,NULL,'1','8.10#9a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (24,'9','LJB',NULL,'Der blaue Torwart berührt einen gegnerischen Fernschuss, bevor dieser ins Tor geht. Wie entscheidest Du?','Das Tor zählt nicht. Fernschuss.','Eigentor. Tor für Rot.','Bully an der Mittellinie.','Es geht weiter mit Torab.',NULL,NULL,'2','7.2','Wird ein Fernschuss in der gegnerischen Hälfte abgefälscht, zählt dies nicht mehr als Fernschuss. Von welchem Team der Ball dabei berührt wurde, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (25,'8','LJB',NULL,'Ein roter wird von einem blauen Spieler gesubt, sein Einrad fällt auf den Ball. Der Ball ist nicht mehr spielbar. Wie entscheidest Du?','Es gibt Freistoß für Rot. Nur der erste Verstoß (Sub) wird bestraft.','Hier liegen zwei Verstöße gleichzeitig vor. Es gibt Bully.','Hier liegen zwei Verstöße gleichzeitig vor. Als Schiedsrichter muss ich entscheiden, welcher davon schwerwiegender ist.','Die beiden Regelverstöße heben sich auf. Weiterspielen.',NULL,NULL,'1','10.6d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (26,'6','LBF',NULL,'In einem Zweikampf schlagen sich zwei Gegner, die vorher noch nicht negativ aufgefallen sind, übertrieben hart auf die Schläger. Wie entscheidest Du?','Spielunterbrechung. Beide Spieler verwarnen.','Weiterspielen.','Zeitstrafen für beide Spieler.','Weiter mit Bully.',NULL,NULL,'1#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (27,'6','LJB',NULL,'Eine blaue Spielerin schlägt in der eigenen Torzone im Zweikampf den Schläger einer roten Spielerin so hoch, dass dieser über Hüfthöhe ist. Wie entscheidest Du?','Übertriebene Härte. Ecke für Rot.','Kein Foul, da es sich um zwei Frauen/Mädchen handelt. Weiterspielen.','Hoher Schläger. Torab für Blau.','Übertriebene Härte. Freistoß am Ort des Fouls.',NULL,NULL,'1','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (28,'10','LBF','Rolf: Da die 5 min hierfür nicht in den Regeln stehen, sollten wir das auch nicht in die Prüfung nehmen.','Ein roter Spieler beleidigt dich. Wie entscheidest Du?','Feldverweis für den roten Spieler für mindestens 5 Minuten.','Weiterspielen. Bei der nächsten Unterbrechung den Spieler verwarnen.','Freistoß für Blau. Evtl. zunächst Vorteil laufen lassen.','Wenn Ich vorher eine Fehlentscheidung getroffen habe, habe ich es auch verdient.',NULL,NULL,'1#3','8.5','Das Strafmaß für eine Beleidigung ist in den Regeln nicht explizit aufgeführt. Da es sich um eine grobe Unsportlichkeit handelt, sind im Ermessen der Schiedsrichter 5 min angemessen. ',1,NULL,NULL,NULL,NULL,'nein','@rolf Anregunge für die Regelkommission? "Ein Spieler darf den Schiedsrichter nicht Beleidigen" (8.5) Ok. Aber eine Konsequenz ist nirgens formuliert??'),
    (29,'10','LJBF','Rolf: Da die 5 min hierfür nicht in den Regeln stehen, sollten wir das auch nicht in die Prüfung nehmen.','Ein roter Spieler beleidigt einen blauen Spieler. Du hast es gehört. Wie entscheidest Du?','Feldverweis für den roten Spieler für mindestens 5 Minuten.','Weiterspielen. Bei der nächsten Unterbrechung den Spieler verwarnen.','Freistoß für Blau. Evtl. zunächst Vorteil laufen lassen.','Das sollen sie selber aushandeln, da halte ich mich raus.',NULL,NULL,'1#3','8.5','Das Strafmaß für eine Beleidigung ist in den Regeln nicht explizit aufgeführt. Da es sich um eine grobe Unsportlichkeit handelt, sind im Ermessen der Schiedsrichter 5 min angemessen. ',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (30,'8','LJBF',NULL,'Eine rote Spielerin bekommt den Ball an die Brust und schlägt ihn sofort mit der Hand weg. Wie entscheidest Du?','Freistoß für Blau. Doppelte Körperberührung.','Kein Foul. Nur eine aktive Berührung.','Bully. Erst aktive und danach passive Berührung.','Vorteilsentscheidung möglich.',NULL,NULL,'2','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (31,'10','LJBF','Larissa: hier ist wieder die Frage, wann ein Foul gefährlich ist. Dann wäre 3 auch richitg. 5 min wäre nur bei Absichtliches UND gefährliches Foul
Max: Frage viel zu ungenau. entweder vereinfachen oder genauer erklären','Der rote ballführende Spieler wird von einer blauen Spielerin verfolgt. Der rote Ballführende drängt die blaue Spielerin an die Bande ab. Die blaue Spielerin prallt gegen die Bande.','Weiterspielen. Der Ballführende hat Vorfahrt.','Freistoß für die blaue Spielerin. Kein Spieler darf durch Erzwingen der Vorfahrt eine/n andere/n Spieler/in gefährden.','Wegen gefährlichen Spiels bekommt der rote Spieler eine 2 min Zeitstrafe.','Geschieht dies mit Absicht, bekommt der rote Spieler eine 5 min Zeitstrafe.',NULL,NULL,'2#4','8.2b#9.5de',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian:  Richtig. 3 ist auch richtig. Auch hier könnte die Regel eindeutiger sein, dass "Absichtliches, gefährliches Foul" absichtliches UND gefährliches Foul bedeutet. '),
    (32,'1','LBF',NULL,'Ein roter Spieler schießt auf das Tor und trifft. Während der Ball in der Luft ist, ertönt der Schlusspfiff und endet erst, als der Ball im Tor ist. Zählt das Tor und warum?','Ja, da der Torschuss noch vor dem Schlusspfiff abgegeben wurde.','Nein, da der Ball bei Spielende nicht im Tor war.','Ja, da das Spiel erst mit dem Ende des Signals endet.','Das ist Auslegungssache des Schiris.',NULL,NULL,'2',NULL,'Da die Regeln nichts anderes festlegen, endet das Spiel sofort, wenn die Zeit abgelaufen ist. Ist der Ball in diesem Moment nicht im Tor, zählt ein Tor nicht mehr.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (33,'6','LJBF',NULL,'Ein roter Spieler ist nicht in Ballnähe. Ein blauer Spieler blockiert dessen Schläger hart, sodass der Spieler am Weiterfahren gehindert wird. Wie entscheidest Du?','Freistoß bzw. Vorteil für Rot.','Der Freistoß wird dort ausgeführt, wo sich der Ball befunden hat.','Weiterspielen. Kein Foul.','Der Freistoß wird am Ort des Fouls ausgeführt.',NULL,NULL,'1#4','8.1#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (34,'8','LJB','Max: richtige Antwort Eckball für Rot','Nach einem Schuss von rot aufs Tor steckt der Ball im Rad des blauen Torwarts. Wie entscheidest Du?','Straftor für Rot.','Torab für Blau.','6,50 m für Rot.','Freistoß für Rot vom Ort des Schusses.',NULL,NULL,NULL,'5.8',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (35,'8','LJB',NULL,'Rot schießt auf das blaue Tor. Der Ball bleibt bei einem eigenen roten Mitspieler innerhalb der blauen Torzone im Rad stecken. Der Ball wäre sonst auf das Tor gegangen. Wie entscheidest Du?','Ecke für Rot.','Torab für Blau.','Bully am Eckpunkt.','6,50 m für Rot.',NULL,NULL,'2','5.8','Merke: Ball im Rad bedeutet immer Freistoß für das andere Team. Wer geschossen hat spielt keine Rolle!',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (36,'8','LJB',NULL,'Mehrere Spieler sind ohne erkennbares Foul in der Torzone gestürzt. Der Ball liegt unter den Rädern und ist nicht mehr spielbar. Wie entscheidest Du?','Das Spiel geht weiter.','Ich unterbreche das Spiel. Es gibt Bully am Eckpunkt.','Torabstoß.','Wenn mehrere Spieler gestürzt sind, wird das Spiel grundsätzlich unterbrochen.',NULL,NULL,'2','9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (37,'8','LJB',NULL,'Mehrere Spieler sind ohne erkennbares Foul gestürzt. Der Ball liegt direkt neben den gestürzten Spielern und ist weiterhin spielbar. Wie entscheidest Du?','Das Spiel geht weiter, solange der Ball spielbar ist und keine Verletzungen vorliegen.','Ich unterbreche das Spiel. Es gibt Bully.','Wenn mehr als zwei Spieler vom Einrad abgestiegen sind, wird das Spiel immer unterbrochen.','Wenn mehr als vier Spieler vom Einrad absteigen, wird das Spiel grundsätzlich unterbrochen.',NULL,NULL,'1',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (38,'1','LJB',NULL,'Nach einem Tor von Blau fährt das rote Team mit dem Ball über die Mittellinie. Die Torschützin des blauen Teams befindet sich noch nicht wieder in der eigenen Hälfte. Wie entscheides Du?','Das Spiel geht weiter. Vorteil für das ballführende rote Team.','Ich pfeife mehrfach laut und schicke die Spieler in ihre Hälften. Sobald alle spielbereit sind pfeife ich an.','Solange sich kein blauer Spieler beschwert lasse ich das Spiel laufen. ','Sofern kein Nachteil entsteht, kann das Spiel weiterlaufen.',NULL,NULL,'2','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (39,'1','LJB',NULL,'Nach einem Tor sind alle Spieler wieder in ihrer Spielhälfte. Du hast das Spiel noch nicht angepfiffen. Der ballführende Spieler fährt über die Mittellinie. Was musst Du tun?','Da alle Spieler bereit waren, kann das Spiel weiter gehen. Auch ohne Anpfiff.','Ich pfeife mehrmals und unterbreche das Spiel. Alle Spieler müssen zurück in die eigene Hälfte und das Spiel wird neu eröffnet mit einem Anpfiff.','Wenn sich niemand beschwert, kann das Spiel weiter gehen. Andernfalls wird es unterbrochen.','Prinzipiell sollte das Spiel zügig angepfiffen werden, sobald alle Spieler in ihrer Hälfte und spielbereit sind.',NULL,NULL,'2#4','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (40,'1','LJB',NULL,'Worauf musst du achten, wenn ein Tor gefallen ist und die Spieler auf dem Weg zurück in die eigene Hälfte sind?','Ich pfeife das Spiel  an, sobald alle Spieler in ihrer eigenen Hälfte und spielbereit sind, damit das Spiel zügig weitergehen kann.','Ich achte darauf, dass es zu keiner übermäßigen und unsportlichen Spielverzögerung kommt.','Nach jedem Tor warte ich eine Pause von etwa 20 Sekunden ab.','Ich pfeife das Spiel sofort an, sobald alle Spieler in ihrer eigenen Hälfte sind.',NULL,NULL,'1#2','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (41,'10','LBF','Max: Antwort 3 richtig?','Nach einem Führungstor kurz vor Spielschluss fährt der Torschütze oder seine Mitspieler sehr langsam zurück in die eigene Hälfte, offensichtlich um das Spiel zu verzögern. Wie entscheidest Du?','Das ist eine erlaubte Taktik. Ich pfeife an, sobald alle spielbereit in ihrer Hälfte sind.','Ich veranlasse einen Zeitstop.','Es geht weiter mit Freistoß für Blau am Mittelpunkt.','Je nach vorherigem Verhalten verwarne ich den Spieler oder gebe ihm eine Zeitstrafe wegen absichtlicher Spielverzögerung.',NULL,NULL,'2#4','9.5d#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Das kann ich mir nicht so richtig vorstellen. Wenn die angreifende Mannschaft im Ballbesitz ist, kann die verteidigende Mannschaft schlecht Zeitspiel machen. Das geht ja nur bei ruhendem Ball. '),
    (42,'1','LJBF',NULL,'Ein roter Spieler liegt seit einiger Zeit verletzt am Boden, ohne dass ein Foulspiel vorlag. Das blaue Team ist in Ballbesitz. Wie entscheidest Du?','Ich warte, bis es eine Spielunterbrechung (z.B. Ausball) gibt. Dann unterbreche ich und sorge für eine Behandlung des verletzten Spielers.','Ich unterbreche das Spiel sofort und halte die Zeit an.','Nach der Unterbrechung geht es mit einem Bully weiter.','Nach der Unterbrechung geht es mit einem Freistoß für Blau weiter.',NULL,NULL,'2#4','10.6h',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (43,'2','LJB',NULL,'Du hast ein Foul von Blau gesehen und pfeifst. Dann merkst du, dass es einen Vorteil für das rote Team gibt und rufst "weiter". Ist das zulässig und warum?','Ja. Es wäre unfair für das rote Team, den Vorteil nicht zu gewähren.','Nein. Einen Vorteil sage ich nicht mit "weiter" an.','Ja. Ich kann mich entscheiden, ob ich Vorteil geben möchte, oder nicht.','Nein. Wenn ich gepfiffen habe ist das Spiel unterbrochen.',NULL,NULL,'4','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (44,'2','LJB',NULL,'Du pfeifst ein Foul, erkennst dann aber, dass du dich geirrt hast und lässt das Spiel weiterlaufen, da die meisten Spieler eh nicht auf den Pfiff reagiert haben. Ist das zulässig und warum?','Ja, es war schließlich kein Foul.','Nein, durch den Pfiff ist das Spiel unterbrochen.','Ja, es kann als Vorteil für das in Ballbesitz befindliche Team ausgelegt werden.','Wenn wirklich alle weiterspielen, ist es zulässig. Dann hat es wohl keiner gehört.',NULL,NULL,'2','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (45,'8','LJB',NULL,'Vom angreifenden roten Team springt der Ball auf Höhe der Mittellinie ins Aus. Von einer Wand im Seitenaus prallt er wieder zurück ins Spielfeld. Das verteidigende Team ist nun in Ballbesitz. Wie entscheidest Du?','Der Ball war im Aus. Freistoß für Blau.','Blau hätte einen Freistoß, deshalb kann ich auch auf Vorteil entscheiden.','Der Ball ist nur im Aus, wenn er außerhalb des Spielfeldes den Boden berührt. Weiterspielen.','In den Regeln ist es unklar, ob das als Aus gilt. Ich gebe einen Bully.',NULL,NULL,'1#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (46,'8','LJB',NULL,'Dem roten Team springt der Ball in der blauen Torzone ins Aus. Von einer Wand (über der schrägen Bank in der Ecke) prallt er wieder zurück ins Spielfeld. Das rote Team kommt sofort wieder in Ballbesitz. Wie entscheidest Du?','Torab für Blau.','Vorteil für Rot.','Ecke für Rot.','Weiterspielen. Kein Foul.',NULL,NULL,'3','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (47,'8','LJB',NULL,'Der Ball springt ins Aus. Keiner der Schiedsrichter konnte erkennen, welches Team zuletzt am Ball war. Wie entscheidest Du?','Ich frage die beteiligten Spieler. Gibt es keine sofortige Einigung, entscheide ich auf Bully.','Ich entscheide nach eigenem Ermessen ohne die Spieler zu fragen.','Im Zweifel wird für das angreifende Team entschieden.','Ich frage die beteiligten Spieler und warte solange, bis sie sich einigen. Zur Not sorge ich für einen Zeitstop. ',NULL,NULL,'1','10.6b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (48,'9','LJB',NULL,'Ein roter Spieler schießt aus der eigenen Hälfte ein Tor. Der Ball wird von keinem Spieler mehr berührt. Wie entscheidest Du?','Kein Tor. ','Torab für Blau.','Bully am Mittelpunkt.','Tor für Rot.',NULL,NULL,'1#2','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (49,'9','LJB',NULL,'Ein Spieler setzt zum Torschuss an. Worauf musst Du in dieser Situation achten?','Von wo geschossen wird.','Wie hoch die Kelle des Schlägers gehoben wird.','Ob sich ein anderer Spieler in der Nähe befindet.','Wie alt der Spieler ist.',NULL,NULL,'1#2#3',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (50,'4','LJBF','Max: Modusfrage, keine Regelfrage. Würde ich ganz streichen.','Ein Finalspiel (nur beim 8er Turnier) endet unentschieden. Wie geht es weiter?','Es gibt zwei Sieger.','Sofortiges Penaltyschießen.','5 min Verlängerung ohne golden Goal. Anschließend Penaltyschießen. falls weiterhin unentschieden.','10 min Verlängerung ohne golden Goal. Anschließend Penaltyschießen. falls weiter unentschieden.',NULL,NULL,'3','5.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (51,'8','LJBF',NULL,'Ein roter Spieler spielt den Ball mit der Hand. Der Ball fliegt nach oben und trifft ihn direkt wieder auf der Schulter. Danach schlägt der Spieler den Ball mit dem Schläger weg. Wie entscheidest Du?','Doppelte Körperberührung mit der zweiten aktiv. Freistoß für Blau.','Doppelte Körperberührung mit der ersten aktiv. Freistoß für Blau.','Doppelte Körperberührung, aber eine passiv. Bully.','Doppelte Körperberührung, aber nicht die zweite aktiv. Weiterspielen',NULL,NULL,'4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (52,'7','LJBF',NULL,'Wann darf ein Spieler den Ball mit Schläger, Rad oder Körper spielen?','Er sitzt auf dem Einrad und berührt weder Wand noch Tor.','Er fällt von dem Einrad und berührt mit einem Fuß den Boden, mit dem anderen Fuß aber noch die Pedale.','Während er vom Boden auf das Einrad springt und den Boden verlassen hat, aber noch nicht mit den Füßen auf den Pedalen ist, solange er danach nicht direkt wieder absteigt.','Er sitzt auf dem Einrad und lehnt am Tor.',NULL,NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (53,'10','LJBF',NULL,'Eine rote Spielerin verletzt absichtlich einen Gegenspieler, indem er ihm mit dem Schläger gegen das Bein schlägt. Welche Möglichkeiten hast Du?','Ich sorge dafür, dass der Vorfall im Turnierbereicht auftaucht.','Feldverweis für 2 Minuten. Wenn die Spielerin vorher schon auffällig geworden ist, sind auch 5 Minuten möglich.','Feldverweis für 5 Minuten oder den Rest des Spiels, je nachdem wie sehr der verletzte Spieler gefährdet wurde.','Ich versuche direkt zu deeskalieren. Wenn das gelingt verwarne ich die Spielerin für eine Zeitstrafe beim nächsten Vergehen. ',NULL,NULL,'3','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (54,'6','LJBF','siehe auch Nr. 197','Ein blauer Spieler hakt seinen Schläger mit nach unten gedrehter Kelle beim Schläger eines vorbeifahrenden roten Spielers ein, so dass der im Fahren behindert wird und sich gerade noch auf dem Rad halten kann. Der Ball ist nicht in der Nähe. Wie entscheidest Du?','Absichtliches Foulspiel. Feldverweis für 2 Minuten gegen den blauen Spieler.','Foulspiel abseits des Balles, es sollte daher nicht geahndet werden. Ich verwarne den blauen Spieler bei der nächsten Unterbrechung.','Wenn der Ball in der Nähe wäre, wäre es ein Foul. Der beschriebene Fall ist in den Regeln aber nicht beschrieben, deshalb kein Foulspiel.','Wenn der Ball nicht in der Nähe ist, ist es ein erlaubtes Mittel, um den Fahrtweg des Gegners zu behindern. Solange der Spieler nicht fällt, weiterspielen.',NULL,NULL,'1','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (55,'8','LJBF','Rolf: Warum ist Antwort 3 falsch?','Ein roter Spieler wird von einem blauen Spieler “ge- SUBt, das Sub beeinträchtigt aber nicht seine Fahrweise. Wie entscheidest Du?','Ein SUB ist immer ein Foul und muss bestraft werden. Ich unterbreche das Spiel und gebe einen Freistoß für Rot.','Ich rufe laut "Vorteil" und mache das entsprechende Zeichen. Sollte sich kein Vorteil ergeben, kann ich zeitnah nachträglich auf Freistoß entscheiden.','Ich warte den laufenden Angriff von roten Team ab, auch wenn er länger dauert. Sollte kein Tor erzielt werden, pfeife ich ab und gebe einen Freistoß für Rot.','Der Spieler ist nicht gefallen, also tue ich nichts. Weiterspielen. ',NULL,NULL,'2','8.3#10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Ich glaube sowohl Antwort 2 als auch 3 sind falsch: Punkt 8.1. "Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden." Wenn das Sub also wirklich seine Fahrweise nicht beeinträchtigt, war es auch KEIN Foul? Also auch kein Vorteil sondern weiterspielen. '),
    (56,'1','LJB',NULL,'Wie viele Spieler gehören mindestens pro Team auf das Spielfeld, wenn das Spiel beginnt?','5 Spieler','4 Spieler und ein Torwart','3 Spieler','4 Spieler',NULL,NULL,'3','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (57,'1','LJB',NULL,'Wie viele Spieler gehören normalerweise pro Team auf das Spielfeld?','5 Spieler','4 Spieler','3 Spieler','6 Spieler',NULL,NULL,'1','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (58,'10','LJB',NULL,'Ein roter Spieler wirft absichtlich seinen Schläger. Welche Konsequenzen hat das für den Spieler und sein Team?','Dies ist ein absichtliches Foulspiel und muss mit einem 2 Minuten Feldverweis geahndet werden. ','Wenn dabei ein Spieler zusätzlich konkret gefährdet wird, muss es mit einem Feldverweis von mindestens 5 Minuten bestraft werden.','Wenn die Situation in den letzten 3 Spielminuten passiert, gibt es einen 6,50m für Blau.','Das rote Team darf keinen Ersatzspieler spielen lass und ist jetzt einer weniger.',NULL,NULL,'1#2#3#4','8.8#9.5de',NULL,1,NULL,NULL,NULL,NULL,'nein','Christian: Wenn es Absichtlich (steht in der Frage) UND Gefährlich (beim Schlägerwurf immer zu unterstellen?) ist, sollte es immer 5 min. geben. Frage ist nicht eindeutig'),
    (59,'10','LJBF',NULL,'Ein roter Spieler ist allein auf dem Weg zum Tor. Von hinten wirft ein blauer Spieler seinen Schläger. Dies hat aber keine Auswirkung auf den roten Spieler. Wie entscheidest Du?','Ich unterbreche das Spiel, erteile dem blauen Spieler einen Feldverweis von 2 Minuten und gebe dem roten Team einen Freistoß.','Ich unterbreche das Spiel, erteile dem blauen Spieler einen Feldverweis von 5 Minuten und gebe dem roten Team einen 6,50m.','Ich lasse Vorteil laufen, sage es deutlich an und nutze das Ende des Vorteils, um das Spiel zu unterbrechen und dem blauen Spieler einen Feldverweis von 2 Minuten zu erteilen.','Ich unterbreche das Spiel, erteile den blauen Spieler einen Feldverweis von 2 Minuten und gebe dem roten Team einen 6,50 m.',NULL,NULL,'3','8.8#10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (60,'10','LB',NULL,'Ein blauer Spieler fährt hinter einem ballführenden roten Spieler her und foult diesen. Der Ball ist klar außerhalb seiner Reichweite. Der rote Spieler war in einer aussichtsreichen Situation, ein Tor zu erzielen (direkte Schussbahn zum Tor). Wie entscheidest Du?','Ich verwarne den blauen Spieler und gebe dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen 6,50 m.','Ich ermahne den blauen Spieler und gebe dem roten Team einen 6,50 m.',NULL,NULL,'3','9.5d#9.2a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (61,'10','LJBF','Max: aus meiner Sicht kann ein Foul von hinten auch als gefährliches Foul gewertet werden (Spielerin ist komplett unvorbereitet, das kann zu bösen Stürzen führen)','Ein blauer Spieler fährt hinter einer ballführenden roten Spielerin her. Er hat keine Chance an den Ball zu kommen und foult die rote Spielerin mit Absicht. Zwischen der roten Spielerin und dem blauen Tor standen noch zwei blaue Spieler.  Wie handelst Du?','Ich ermahne den blauen Spieler und gebe dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen 6,50m.','Ich gebe dem blauen Spieler einen 5 min Feldverweis und dem roten Team einen Freistoß.',NULL,NULL,'2','9.5d#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (62,'5','LJB',NULL,'Ein blauer Spieler pendelt auf der Stelle und wird vom ballführenden roten Spieler umgefahren. Beide stürzen. Wie entscheidest Du?','Bully. Beide müssen einen Zusammenstoß vermeiden.','Freistoß für Rot. Der Ballführende hat Vorfahrt.','Freistoß für Blau. Stehende Spieler müssen umfahren werden.','Vorteilentscheidung möglich.',NULL,NULL,'3#4','8.2c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (63,'5','LJBF','Max: Hier fehlt noch eine realistische falsche Antwort.','Ein roter ballführender und ein blauer Spieler fahren parallel nebeneinander her. Sie sind auf gleicher Höhe. Der rote Spieler drängt den blauen Spieler  ab, so dass dieser abbremsen muss, um einen Zusammenstoß zu vermeiden. Wie entscheidest Du und warum?','Keiner der Spieler ist gestürzt, also kein Foul. Weiterspielen.','Freistoß für Blau. Der rote Spieler hat sich einen regelwidrigen Vorteil verschafft.','Der rote Spieler hatte als Ballführender Vorfahrt, also kein Foul. Weiterspielen. ','Irgendwas mit Bully',NULL,NULL,'2','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (64,'5','LJBF',NULL,'Der rote, ballführende Spieler und eine blaue Spielerin fahren parallel nebeneinander her. Sie sind auf gleicher Höhe. Die blaue Spielerin drängt den roten Spieler ab, so dass dieser abbremsen muss, um einen Zusammenstoß zu vermeiden. Wie entscheidest Du und warum?','Keiner der Spieler ist gestürzt, also kein Foul. Weiterspielen.','Freistoß für Rot. Die blaue Spielerin hat sich einen regelwidrigen Vorteil verschafft.','Freistoß für Rot. Der rote Spieler hatte als Ballführender Vorfahrt.','Als verteidigender Spieler hatte der blaue Spieler Vorfahrt, also kein Foul. Weiterspielen.',NULL,NULL,'2','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (65,'5','LJBF',NULL,'Der rote, ballführende Spieler und eine blaue Spielerin fahren parallel nebeneinander her. Die blaue Spielerin ist schneller und in Fahrtrichtung gesehen vor dem roten Spieler und ändert so die Richtung, dass der rote Spieler seinen Fahrtweg anpassen muss, um einen Zusammenstoß zu vermeiden. Es liegt keine Gefährdung vor.  Wie entscheidest Du und warum?','Keiner der Spieler ist gestürzt, also kein Foul. Weiterspielen.','Freistoß für Rot. Die blaue Spielerin hat sich einen regelwidrigen Vorteil verschafft.','Freistoß für Rot. Der rote Spieler hatte als Ballführender Vorfahrt.','Da die blaue Spielerin voranfahrend war und durch ihre Richtungsänderung keine Gefährdung entstanden ist, liegt kein Foul vor. Weiterspielen.',NULL,NULL,'4','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (66,'5','LJBF',NULL,'Der Ballführende (rot) und seine Gegenspielerin (blau) fahren parallel nebeneinander her. Der rote Spieler (mit Ball) ist in Fahrrichtung gesehen klar vorne und drängt die blaue Spielerin ab.  Wie entscheidest Du und warum?','Keiner der Spieler ist gestürzt, also kein Foul. Weiterspielen.','Freistoß für Blau. Der rote Spieler hat sich einen regelwidrigen Vorteil verschafft.','Der rote Spieler hatte als Ballführender Vorfahrt, also kein Foul. Weiterspielen.','Gefährliches Spiel von Rot. Freistoß für Blau und 2 Minuten Feldverweis für den roten Spieler.',NULL,NULL,'3','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (67,'9','LJBF',NULL,'Womit kann ein gültiges Tor erzielt werden?','Schläger.','Hand oder Arm.','Kopf.','Jedem Teil des Einrads.',NULL,NULL,'1#3#4','7.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (68,'11','L','Max: Diese Frage hat meiner Meinung nach nur zwei Antwortmöglichkeiten. Das macht ein Raten zu erfolgsversprechend. -> nur Lehrgang','Ein 6,5m wird grundsätzlich immer bei einem Foul gegeben, wenn das Foul innerhalb der Torzone (6,50 m Bereich) vor dem Tor stattgefunden hat. Richtig oder Falsch?','Richtig.','Falsch.','Ein 6,50 m wird nur bei einem unsportlichen Foul gegeben.','Ein 6,50 m wird nur bei einem besonders "harten" Foul gegeben.',NULL,NULL,'2','9.2a','Ein Foul bei einer <i>direkten Torchance</i> gibt immer 6,50m, unabhängig davon wo das Foul erfolgte. Hier unterscheidet sich die Einradhockeyregel vom Fußball, wo es einen Elfmeter nur bei Foul innerhalb des Strafraums geben kann. Fouls ohne klare Torchance innerhalb des 6,50m Bereichs haben keinen 6,50m zur Folge. Die 6,50m Linie ist also kein "Strafraum".Ob das Foul "hart" war oder nicht, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (69,'11','L','siehe oben','Einen 6,50m kann es nur geben, wenn nur noch der Torwart vor dem gefoulten Spieler steht.  Richtig oder Falsch?','Richtig.','Falsch.',NULL,NULL,NULL,NULL,'2','9.2a','Eine <i>direkte Torchance</i> kann auch gegeben sein, wenn noch ein Spieler vor dem gefoulten steht, dieser jedoch eine direkte Torchance hatte. ',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (70,'10','LJBF',NULL,'Bei einem Tempogegenstoß fährt ein roter Spieler alleine auf das blaue Tor zu. Der blaue Torwart verlässt das Tor und stoppt den Angreifer mit einem Foul, noch bevor dieser in der Torzone ist. Wie entscheidest Du?','Es gibt Freistoß, da das Foul noch vor der Torzone erfolgt ist.','Es gibt 6,50m, da das Foul eine klare Torchance verhindert hat.','Wenn das Foul absichtlich erfolgt ist ("Notbremse") bekommt der Torwart einen Feldverweis, dessen Dauer sich nach der Gefährdung richtet.','Der Torwart hat das Sonderrecht, alle Mittel einzusetzen, außer Gewalt. Kein Foul.',NULL,NULL,'2#3','9.2a','Ein Foul bei einer <i>direkten Torchance</i> gibt immer 6,50m, unabhängig davon wo das Foul erfolgte. Hier unterscheidet sich die Einradhockeyregel vom Fußball, wo es einen Elfmeter nur bei Foul innerhalb des Strafraums geben kann. Fouls ohne klare Torchance innerhalb des 6,50m Bereichs haben keinen 6,50m zur Folge. Die 6,50m Linie ist also kein "Strafraum".Ob das Foul "hart" war oder nicht, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (71,'7','LJB','Max: hier fehlt eine sinnvolle Antwort.','Nach eine Foul von Rot entscheidest Du auf Vorteil, weil das blaue Team in Ballbesitz kommt. Der ballführende blaue Spieler ist allerdings in einer bedrängten Situation vor dem eigenen Tor und kann den Ball nicht kontrollieren und verliert ihn sofort wieder. Wie entscheidest Du?','Ich habe auf Vorteil entschieden. Ich lasse weiterlaufen.','Es ist keine echte Vorteilssituation entstanden. Ich gebe den Freistoß für Blau. ','Ich habe den Vorteil schon angesagt, es ist aber kein echter Vorteil. Ich gebe Bully.',NULL,NULL,NULL,'2','10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (72,'8','LBF','Rolf: Schwierige Frage. Was ist, wenn der Torschütze NACH dem Abpfiff den Schläger vor Freude über Hüfthöhe hält? Wird das Tor dann auch aberkannt?
 Christian: Ja, schwierig. Die Regeln helfen hier echt nicht weiter, siehe der Konkurrenz die ich oben beschrieben habe. Gilt 8.1. auch für hoher Schläger? Das sollte dringend geklärt werden. Siehe die Erklärung
Max: ist für mich mit 8.1 und 8.10 klar geregelt, auch wenn es nicht explizit beschrieben ist (siehe Erklärung). Wäre wah gut es unter 7 aufzunehmen. ','Ein Spieler schießt den Ball aus nächster Nähe ins Tor. Beim Torschuss schwingt er den Schläger über Hüfthöhe. Der Ball geht ins Tor. Der Ball ist im Tor bevor der Schläger über Hüfthöhe geschwungen wird.','Das Tor zählt, da der Ball zuerst im Tor war.','Das Tor zählt nicht, da der Torschuss illegitim war (hoher Schläger).','Wenn ich nicht sicher bin, kann ich 6,50 m geben.','Es geht weiter mit Torab.',NULL,NULL,'2#4','8.1a#8.10','Obwohl der Fall nicht explizit in den Regeln beschrieben ist, handelt es sich bei einem hohen Schläger beim Torschuss um ein Foulspiel. Hoher Schläger ist eine Gefährdung anderer und sich selbst. Da ohne den REgelverstoß das Tor nicht erzielt worden wäre, kann es auch nicht gelten.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (73,'9','LJB',NULL,'Eine rote Spielerin schießt aus der eigenen Hälfte auf das Tor. Der Schuss wird von einem Spieler des eigenen Teams abgefälscht, der in der gegnerischen Hälfte steht. Der Ball geht ins Tor. Wie entscheidest Du?','Das Tor zählt.','Das Tor zählt nicht, Fernschuss.','Wenn ich mir unsicher bin, kann ich 6,50 m geben.','Es geht weiter mit Torab.',NULL,NULL,'1','7.2','Entscheidend ist der <i>letzte Ballkontakt</i>, in diesem Fall also das Abfälschen des Schusses.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (74,'11','LJBF',NULL,'Du erkennst auf 6,50m kurz vor Spielende. Während der Vorbereitungen zum 6,50m läuft die Spielzeit ab. Wie entscheidest Du?','Die Spielzeit ist abgelaufen. Der 6,50m wird nicht mehr ausgeführt.','Der 6,50m wird noch ausgeführt. Wird er gehalten, läuft das Spiel noch für ca. 20 sec. weiter.','Der 6,50m wird noch ausgeführt. Anschließend ist das Spiel beendet. Ein Nachschuss zählt nicht mehr.','Ich hätte direkt einen Zeitstop anordnen können, da es sich um eine unvorhergesehene Verzögerung handelt.',NULL,NULL,'3#4','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (75,'11','LJB','siehe auch Nr. 198','Was gilt für einen Freistoß in der deutschen Einradhockeyliga?','Ein Freistoß ist grundsätzlich indirekt, darf also nicht direkt ins Tor geschossen werden.','Ein Freistoß darf nur geschlagen ausgeführt werden, also nicht geschlenzt bzw. gelupft werden.','Die Ecke und der Torabstoß sind Varianten des Freistoß.','Das nicht ausführende Team muss einen Mindestabstand von 2m einhalten (außer mit dem Schläger).',NULL,NULL,'1#3','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',' '),
    (76,'11','LJB',NULL,'Ein Freistoß von Rot wird direkt aufs Tor geschossen. Der Ball wird von einem Spieler vor dem Tor abgefälscht und geht ins Tor. Ich kann nicht erkennen, ob er von einem roten oder blauen Spieler abgefälscht wurde. Wie entscheidest Du?','Ich gebe das Tor. Es spielt keine Rolle, wer den Schuss abgefälscht hat.','Ich versuche, durch Befragung der Spieler herauszufinden, wer den Ball abgelenkt hat.','Ich entscheide auf Bully.','Im Zweifel für das abwehrende Team. Das Tor zählt dann nicht, wenn es nicht eindeutig ist.',NULL,NULL,'1',NULL,'Ein Freistoß gilt, wenn er von <i>irgendeinem </i>Spieler abgefälscht wird. Er ist dann nicht mehr "Indirekt".',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (77,'9','LJBF',NULL,'Ein Fernschuss des roten Teams wird in der gegnerischen Spielhälfte vom Schiedsrichter abgefälscht und geht ins Tor. Was nun?','Das Tor zählt, da die letzte Ballberührung in der richtigen Hälfte war.','Das Tor zählt nicht. Das rote Team hätte durch die Berührung des Schiedsrichters einen klaren Vorteil.','Es geht weiter mit Bully.','Es war offensichtlich eine klare Torchance. 6,50 m.',NULL,NULL,'2#3','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (78,'8','LJB','Max: das ist mir eindeutig zu unklar beschrieben.','Der Ball geht im Mittelfeld über die Bande gegen den Tisch des Zeitnehmers und von dort wieder ins Spielfeld. Was tust du?','Das Spiel geht weiter. Der Tisch gehört zur Bande.','Sobald der Ball über die Bande geflogen ist, ist er im Aus. Das Spiel wird unterbrochen. Freistoß.','Wenn die Spieler ohne Probleme weiterspielen, kann das Spiel weiterlaufen. Sonst unterbreche ich das Spiel.','Wenn die Spieler trotz dem Pfiff weiter spielen, unterbreche ich das Spiel energisch mit mehreren Pfiffen.','Vorteil Entscheidung ist möglich.',NULL,'2#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (79,'8','LJB','Larissa: das ist in den Regeln nicht klar definiert. Daher vielleicht nur als Lehrgangsfrage? Rolf: Sehe ich auch so. Christian: Leider fehlt auch hier in den Regeln eine genaue Beschreibung wo die Auslinie ist. ','Kunstschuss: Der Ball geht im Mittelfeld über die Bande und rollt auf der Bande ein paar Meter. Dann fällt er wieder ins Spielfeld. Die Spieler spielen weiter. Wie entscheidest Du?','Der Ball war im Aus. Ich unterbreche das Spiel. Es gibt Freistoß.','Wenn alle weiterspielen, kann das Spiel weiterlaufen.','Das Spiel geht weiter. Die Oberseite der Bande gehört noch zum Spielfeld.','Da der Ball nicht im Aus war, geht das Spiel weiter.',NULL,NULL,'1','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (80,'9','LBF',NULL,'Wilhelm Tell wäre stolz: Ein Torschuss von Rot geht gegen die Latte, von dort aus von hinten gegen den blauen Torwart und dann ins Tor. Allerdings hat der blaue Torwart bei der Ballberührung auf dem Boden gestanden. Wie entscheidest du?','Das Tor zählt nicht. Die Berührung des Torwarts war ein Hindernis, da er auf dem Boden stand. Es gibt Eckball für das rote Team.','Da der Ball ohne den Regelverstoß des Torwarts nicht ins Tor gegangen wäre gibt es Eckball für das rote Team.','Das Tor zählt. Vorteil.','Das Tor zählt nicht. Es gibt 6,50 m für Rot, weil eine Torchance durch das Hindernis des Torwarts verhindert wurde.',NULL,NULL,'3','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (81,'2','L',NULL,'Was ist richtig?','Ich muss immer aufmerksam das Spiel verfolgen.','Ich soll möglichst immer auf Ballhöhe sein.','Ich soll immer möglichst dicht an der Feldbegrenzung (Bande oder Wand) stehen und darf mich nicht ins Feld bewegen.','Ich sollte während des Spiels immer mal wieder auf mein Handy gucken, falls ich von der Ligaleitung per Whatsapp über wichtige Regeländerungen informiert werde.',NULL,NULL,'1#2','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (82,'2','LJB',NULL,'Rot will den Ball nach vorne spielen. Der Ball trifft Dich und wird dabei so abgefälscht, dass Blau in Ballbesitz kommt und eine Torchance entsteht. Wie entscheidest Du?','Weiterspielen. Ich bin "Luft".','Das Spiel wird unterbrochen.','Freistoß für Rot.','Es geht weiter mit Bully am Ort, wo ich getroffen wurde.',NULL,NULL,'2#4','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (83,'9','LJB',NULL,'Worauf musst Du bei einem Torschuss achten?','Ob der Ball vor oder hinter der Mittellinie getroffen wird.','Ob der Schläger beim Ausholen über Hüfthöhe gehalten wird.','Ob der Schläger beim Ausschwingen über Hüfthöhe geführt wird.','Ob der Schuss vor oder hinter der 6,50m Linie abgegeben wird.',NULL,NULL,'1#2#3',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (84,'2','LJB',NULL,'Woran musst Du als erster Schiedsrichter direkt vor Spieleröffnung denken?','Ich muss mich vergewissern, dass die Zeitnehmer und Anzeigentafel bereit sind.','Jedes Team hat genau 5 Spieler auf dem Feld.','Ich vergewissere mich, dass der zweite Schiedsrichter bereit ist.','Es sind mindestens 3 unf maximal 5 Spieler pro Team auf dem Feld.',NULL,NULL,'1#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (85,'2','LJB',NULL,'Ich sehe ein SUB, der gefoulte Spieler ruft laut "SUB!". Was ist jetzt zu tun?','Ich pfeife laut und unterbreche das Spiel sofort.','Ich zeige in die Spielrichtung des gefoulten Teams. Wird kein Vorteil erreicht, unterbreche ich das Spiel mit einem Pfiff und mache das Handzeichen für SUB.','Ich rufe auch sofort laut "SUB!!"','Da der gefoulte Spieler das Foul angezeigt hat, kann es keinen Vorteil mehr geben. Ich unterbreche das Spiel sofort.',NULL,NULL,'2','10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (86,'2','LJB',NULL,'Du als zweiter Schiedsrichter hast ein Foul gesehen, es gibt keinen Vorteil. Der erste Schiedsrichter hat das Spiel jedoch nicht unterbrochen. Was tust Du?','Da nur der erste Schiedsrichter Fouls pfeift, läuft das Spiel weiter. Nach Spielende kann ich den ersten Schiedsrichter auf den Fehler hinweisen.','Ich pfeife das Foul. Als zweiter Schiedsrichter kann ich dies genauso wie der erste Schiedsrichter tun.','Ich als zweiter Schiedsrichter pfeife besser keine Fouls, da der erste Schiedsrichter sich in der Regel besser auskennt.','Nach der Unterbrechung kann ich mich mit dem ersten Schiedsrichter kurz abstimmen.',NULL,NULL,'2#4','10.2','Beide Schiedsrichter können das Spiel bei Regelverstößen unterbrechen. ',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (87,'2','LJB',NULL,'Du als zweite Schiedsrichterin hast ein Foul gepfiffen. Der erste Schiedsrichter entscheidet jedoch auf Vorteil und signalisiert und ruft "Weiterspielen! Vorteil!".  Was hast du zu tun?','Hier überstimmt der erste Schiedsrichter dich. Das Spiel läuft weiter wegen Vorteilsentscheidung.','Der Pfiff von mir hat das Spiel unterbrochen. Damit ist keine Vorteilsentscheidung mehr möglich.','Ich pfeife mehrfach laut und deutlich und unterbreche das Spiel. Ich erkläre, dass der Pfiff das Spiel unterbrochen hat. Der Foulpfiff zählt.','Es geht weiter mit Bully.',NULL,NULL,'2#3','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (88,'2','LJB',NULL,'Der erste Schiedsrichter hat einen Regelverstoß (Foul) gesehen und pfeift. Der zweite Schiedsrichter sieht dies jedoch anderes und ruft "Kein Foul! Weiterspielen!".','Es zählen grundsätzlich nur die Entscheidungen des 1. Schiedsrichters.','Der Pfiff des ersten Schiedsrichters hat das Spiel unterbrochen. Es ist kein Vorteil möglich.','Der erste Schiedsrichter unterbricht das Spiel mit mehrfachem Pfeifen. Einigen sich die Schiedsrichter darauf dass es keine Foul war, gibt es Bully.','Der erste Schiedsrichter kann nun entscheiden ob er das Spiel weiterlaufen lässt oder nicht.',NULL,NULL,'2#3','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (89,'2','LJBF',NULL,'Welche Aussage stimmt?','Der erste Schiedsrichter trifft grundsätzlich alle Entscheidungen, da er die Regeln besser kennt.','Der erste Schiedsrichter trifft alle wichtigen Entscheidungen. Weniger wichtige kann auch der zweite Schiedsrichter treffen.','Ausnahmsweise kann der zweite Schiedsrichter auch einmal eine Entscheidung treffen. Er sollte aber nicht so laut pfeifen, damit das Spiel weitergehen kann, falls er falsch gelegen hat.','Erster und zweiter Schiedsrichter sind Partner. Beide können alle Entscheidungen treffen und pfeifen. Bei schwierigen oder unklaren Entscheidungen sprechen sie sich ab und finden eine gemeinsame Entscheidung.','Der zweite Schiedsrichter ist nur ein Berater des ersten Schiedsrichters.',NULL,'4','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (90,'2','LJB',NULL,'Was bedeutet "Fair Play" für die Spieler im Umgang mit den Schiedsrichtern?','Tatsachenentscheidungen des Schiedsrichters sind zu akzeptieren.','Spieler dürfen grundsätzlich nicht mit dem Schiedsrichter sprechen.','Als Spieler Regelverstöße zuzugeben und auf falsche Entscheidung zu den eigenen Gunsten (z.B. bei Ausbällen) hinzuweisen, erleichtert dem Schiedsrichter die Aufgabe.','Spieler und Schiedsrichter sind Partner im Spiel. Diskussionen über strittige Entscheidungen sind respektvoll zu führen. Die Schiedsrichter dürfen nicht angeschrien oder beleidigt werden.','Nach dem Spiel: Kritik nur in angemessener und respektvoller Art. Niemals persönlich verletzend sprechen.','Immer daran denken: Auch Schiedsrichter dürfen (wie Spieler) auch Fehler machen.','1#3#4#5#6',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (91,'2','L',NULL,'Warum sollte die Aufgabe von einem Schiedsrichter nicht von zwei oder drei Personen als Team übernommen werden?','Da sie sich gegenseitig ablenken können.','Entscheidungen (Pfiff oder nicht) müssen in kurzer Zeit getroffen werden, da bleibt keine Zeit zur Diskussion.','Gruppenschiedsrichter neigen dazu überhaupt keine Entscheidungen zu treffen, da es unklar ist wer die Entscheidung trifft.','Neulinge können erfahrene Schiedsrichter begleiten, es sollte aber klar sein wer der Schiedsrichter ist. Der begleitende Schiedsrichter soll außerhalb des Feldes stehen.',NULL,NULL,'1#2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (92,'9','LJB',NULL,'Das rote Team hat ein Tor per Fernschuss erzielt. Worauf hast du zu achten?','Es geht weiter mit Torab für Blau.','Ich muss darauf achten, dass das Tor auch wirklich nicht auf der Anzeige erscheint.','Ich ermahne den Torschützen wegen unfairem Spiel.','Ich kommuniziere die Entscheidung deutlich mit Handzeichen und Verbal ("kein Tor! Fernschuss").','Es geht weiter mit Bully am Mittelkreis.',NULL,'1#2#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (93,'7','LJB','Larissa: für mich liegt die Betonung bei der Regel auf "gegenerisch", was hier nicht der Fall ist. Antwort 3 und 5 sind für mich gleich. Rolf: Antwort 4 ist auch richtig. Antwort 3 und 5 sind gleich, darum kann 5 entfernt werden. Christian: Ja','Ein roter Spieler schießt den Ball gegen ein am Boden liegendes Einrad des eigenen Teams im Mittelfeld. Der Ball kommt zu ihm zurück und er spielt weiter. Du hast es gesehen, aber was entscheidest du?','Das Spiel läuft weiter. Vorteil für das ballführende Team.','Ich unterbriche das Spiel. Wegen Hindernis gibt es Freistoß für Blau.','Wenn alle weiterspielen kann das Spiel ohne Unterbrechung weitergehen.','Der Freistoß findet dort statt wo der Ball das am Boden liegende Einrad getroffen hat.',NULL,NULL,'2#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (94,'7','LJBF',NULL,'Ein roter Spieler schießt den Ball gegen ein am Boden liegendes Einrad des gegnerischen Teams im Mittelfeld. Der Ball kommt zu einem Spieler seines Teams zurück und er ist in aussichtsreicher Position.','Ich unterbreiche das Spiel. Wegen Hindernis gibt es Freistoß für das ballführende rote Team.','Ich entscheide auf Vorteil und lasse das Spiel laufen.','Ich muss in diesem Fall nichts machen.','Ich zeige in Spielrichtung des roten Teams und rufe laut "Vorteil".',NULL,NULL,'2#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (95,'1','LJBF',NULL,'Absatz 1 des Ligamodus: Oberstes Ziel der Deutschen Einradhockeyliga ist:','Jedem Einradhockeybegeisterten die Ausübung seiner Sportart in einem sportlichen Wettkampf zu ermöglichen, sowie das Einradhockey in Deutschland weiterzuentwickeln.','Auf die exakte Einhaltung der internationalen Einradhockeyregeln zu achten.','Einen sportlichen Wettkampf nach den Richtlinien der olympischen Sportverbände zu gewährleisten.','Einradhockey als Profisport zu etablieren.',NULL,NULL,'1',NULL,'Ligamodus. 1 Allgemeines<BR> Ihr oberstes Ziel ist es, jedem Einradhockeybegeisterten die Ausübung seiner Sportart in einem sportlichen Wettkampf zu ermöglichen sowie das Einradhockey in Deutschland weiterzuentwickeln.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (96,'2','LJ',NULL,'Der Schiedsrichter ist ....','... der Gegner der Spieler.','... der Partner der Spieler.','... der Konkurrent der Spieler.','...halt irgendjemand der gerade Lust hat die Pfeife zu halten und die Tore anzuzeigen.',NULL,NULL,'2',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (97,'2','LJB',NULL,'Bei einem Spiel stehen sich zwei Anfängerteams gegenüber. Worauf musst du besonders achten?','Auf die strikte und genaue Einhaltung aller Regeln.','Die Regeln dürfen je nach Fähigkeitsstand weniger strikt ausgelegt werden.','Ich erkläre den Spielern meine Entscheidungen und helfe so, das Spiel zu verstehen.','Der Schutz von jungen Spielern ist mir besonders wichtig.',NULL,NULL,'2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (98,'11','LJB',NULL,'Der blaue Torwart hält den Ball, den eine rote Spielerin auf das Tor schießt. Der blaue Torwart steht dabei direkt vor dem Tor, mit den Füßen auf dem Boden. Der Ball wäre sicher ins Tor gegangen.','Ich entscheide auf Straftor für Rot.','Es gibt einen 6,50 m für das rote Team.','Weiterspielen.','Eckball für das rote Team.',NULL,NULL,'1','9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (99,'8','LJBF',NULL,'Der rote Torwart hält den Ball mit dem Schläger über der Latte.','Hoher Schläger. Ecke für das angreifende Team.','Weiterspielen, in Tornähe ist hoher Schläger erlaubt (sofern niemand gefährdet wird).','6,50m für das blaue Team.','Wenn niemand direkt gefährdet wurde, kann weitergespielt werden.',NULL,NULL,'1','8.10','Auch in unmittelbarer Nähe zum eigenen Tor darf der Schläger nicht über die Querlatte des Tors gehoben werden.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (100,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','SUB (Stick under Bike)','Kein Foul - weiterspielen','Vorteil läuft','Kein Tor!',NULL,NULL,'2','10.7d',NULL,1,'frage_0100.png',NULL,NULL,NULL,'nein',NULL),
    (101,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Zeitstopp','Foul','Weiterspielen','Bully',NULL,NULL,'1','10.7e',NULL,1,'frage_0101.png',NULL,NULL,NULL,'nein',NULL),
    (102,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Weiterspielen','Kein Tor','Tor','Zeitstopp',NULL,NULL,'3','10.7f',NULL,1,'frage_0102.png',NULL,NULL,NULL,'nein',NULL),
    (103,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Tor','Bully','Zeitstopp','Kein Tor',NULL,NULL,'4','10.7g',NULL,1,'frage_0103.png',NULL,NULL,NULL,'nein',NULL),
    (104,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','SUB','Hindernis','Hoher Schläger','Zeitstopp',NULL,NULL,'3','10.7h',NULL,1,'frage_0104.png',NULL,NULL,NULL,'nein',NULL),
    (105,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Körperkontakt','Hindernis','SUB und SIB','Kein Tor',NULL,NULL,'3','10.7i',NULL,1,'frage_0105.png',NULL,NULL,NULL,'nein',NULL),
    (106,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Hindernis','Kein Foul','Zeitstopp','Körperkontakt',NULL,NULL,'1','10.7j',NULL,1,'frage_0106.png',NULL,NULL,NULL,'nein',NULL),
    (107,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Zeitstopp','2  min Zeitstrafe','6,50 m','Hindernis','Körperkontakt',NULL,'5','10.7k',NULL,1,'frage_0107.png',NULL,NULL,NULL,'nein',NULL),
    (108,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Spielbeginn in 2 min','2 min Pause','SUB','2 min Zeitstrafe','Zweifaches Handspiel',NULL,'4#5','10.7l',NULL,1,'frage_0108.png',NULL,NULL,NULL,'nein',NULL),
    (109,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','5 min Pause','Mehr als 5 Spieler auf dem Feld','Fünffaches Handspiel','Hoher Schläger',NULL,'1','10.7m',NULL,1,'frage_0109.png',NULL,NULL,NULL,'nein',NULL),
    (110,'11','LJBF',NULL,'Ein roter Spieler wird beim Torschuss von einem blauen Spieler gefoult. Er schießt den Ball, nachdem er mit dem Fuß den Boden berührt hat. Der Ball geht ins Tor.','Ich erkenne auf Vorteil. Das Tor zählt.','6.50 m für das Team des gefoulten Spielers, sofern es sich um eine klare (direkte) Torchance handelte.','Das Tor zählt nicht, da der Schuss nicht regelgerecht war.','Ein Foul um ein Torschuss zu stoppen kann eine Unsportlichkeit sind und mit 2 min Zeitstrafe geahndet werden.',NULL,NULL,'2#3#4','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (111,'7','LJB',NULL,'Ein roter Spieler ist ohne Fremdeinwirkung vom Einrad gefallen. Beim Aufsteigen steht er einem blauen Spieler im Weg, der zum Ball fahren möchte.','Kein Hindernis, der rote Spieler darf an der Stelle aufsteigen wo er gefallen ist.','Hindernis! Der rote Spieler muss dem Spielgeschehen zuerst ausweichen.','Freistoß für das blaue Team, sofern kein Vorteil.','Wenn eine klare Torchance verhindert wurde: 6,50 m.',NULL,NULL,'2#3#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (112,'8','LJBF',NULL,'Ein Spieler spielt den Ball mit der Hand. Dazu nimmt er die obere Hand kurzzeitig vom Schläger um den Ball zu spielen. Es wird niemand gefährdet.','Das obere Schlägerende muss immer bedeckt sein. Freistoß für den Gegner.','Weiterspielen. Dies ist kurzzeitig erlaubt, um den Ball mit der Hand zu spielen.','Verwarnung für den Spieler. Bei Wiederholung 2 min Feldverweis.',NULL,NULL,NULL,'2','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (113,'8','LJBF',NULL,'Ein roter Spieler spielt den Ball mit der Hand. Dazu nimmt er die obere Hand kurzzeitig vom Schläger. Mit dem freien Schlägerende kommt er in die Nähe des Kopfes eines Gegenspielers.','Weiterspielen. Dies ist erlaubt um den Ball mit der Hand zu spielen.','Freistoß für das gegnerische Team, da dies eine Gefährdung darstellt.','Weiterspielen und den Spieler bei nächster Gelegenheit ermahnen.',NULL,NULL,NULL,'2','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (114,'7','LJBF','Rolf: Es gibt keine Regel, die das im Anfängerbereich erlaubt. Christian: richtig. Das ist informell und würde ich hier löschen. Vielleicht in die Erklärung aufnehmen?','Der Torwart hält sich am Tor fest. Es kommt ein Schuss aufs Tor, der Torwart lässt kurz das Tor los um den Ball zu spielen. Der Ball wäre mit Sicherheit ins Tor gegangen.','Straftor','6,50 m','Torab','Im Anfängerbereich kann dies (nach vorheriger Absprache) zugelassen werden.',NULL,NULL,'1#4','5.3','Es reicht nicht aus, das Tor nur schnell für die Zeit loszulassen, während der Torwart am Spiel teilnimmt.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (115,'9','LJBF',NULL,'Der Torwart berührt einen gegnerischen Fernschuss, bevor er ins Tor geht.','Tor ab.','Tor zählt.','Bully an der Mittellinie.','6,50 m.',NULL,NULL,'2','7.2','Wird ein Fernschuss in der gegnerischen Hälfte abgefälscht, zählt dies nicht mehr als Fernschuss. Von welchem Team er dabei berührt wird, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (116,'7','LJBF',NULL,'Ein verteidigender blauer Spieler steht auf dem Boden und wird angeschossen. Der Ball geht von ihm direkt ins blaue Tor (Eigentor).','Vorteil! Das Eigentor zählt, auch wenn der Spieler auf dem Boden stand.','Das Tor zählt nicht, da es irregulär erzielt wurde (Spieler stand auf dem Boden).','Es gibt Bully.','Das Tor zählt nicht. Bei Eigentoren gibt es keinen Vorteil.',NULL,NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (117,'9','LJBF',NULL,'Ein verteidigender Spieler spielt den Ball mit der Hand und schlägt in aus Versehen ins eigene Tor. Pech gehabt, oder illegales Tor? Was entscheidest Du?','Das Eigentor zählt, auch wenn es mit der Hand erzielt wurde.','Illegales Tor, da es mit der Hand erzielt wurde.','Ich kann das Tor gelten lassen, wenn es keine Absicht war.','Es geht weiter mit Torab.',NULL,NULL,'1','7.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (118,'9','LJBF',NULL,'Ein Rückpass des roten Teams aus der gegnerischen Hälfte landet im eigenen Tor, ohne dass ein weiterer Spieler den Ball berührt hat. Die roten Spieler monieren, dass dieses Eigentor nicht gewertet werden darf, weil es ein Fernschuss war. Was entscheidest Du?','Fernschuss, da das Tor aus der gegnerischen Hälfte erzielt wurde. Das Tor zählt nicht.','Das Tor zählt. Die Fernschussregel gilt nicht für Eigentore.','Es geht weiter mit Torab.','Es geht weiter mit Bully am Mittelkreis.',NULL,NULL,'2','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (119,'8','LJBF',NULL,'Ein roter Spieler wird gesubt, sein Einrad fällt auf den Ball und berührt diesen. Hindernis oder Foul, was entscheidest Du?','Foul! Es gibt Freistoß für das rote Team. Der erste Verstoß zählt.','Hier sind zwei Verstöße gleichzeitig. Weiterspielen oder Bully.','Ich entscheide, was wichtiger ist.','Hindernis. Freistoß für das blaue Team.',NULL,NULL,'1','8.3#10.6d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (120,'10','LJBF','Rolf: Evtl. Vorteil? Christian: Ja. Antwort 2 umformulieren?','Ein Spieler ist wegen unsportlichen Verhaltens aufgefallen. Wie verhalte ich mich?','Erstmal abwarten, ob er sich in Zukunft besser verhält.','Ich unterbreche das Spiel und entscheide gemeinsam mit dem anderen Schiedsrichter über eine angemessene Strafe.','Während der Besprechung die Zeit anhalten.','Dem Spieler die Strafe und den Grund dafür erklären.','Dem Spieler und dem Anschreibetisch den Ablauf der Strafe erklären.',NULL,'2#3#4#5','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (121,'1','LJB',NULL,'Und los geht es! Was hast du beim Bully zu Spielbeginn zu beachten?','Den Ball nicht über Hüfthöhe halten.','Den Ball gerade ins Spiel fallen lassen, nicht werfen.','Ich vergewissere mich, dass alle Spieler und der Anschreibetisch bereit sind.','Ich schaue schon vor dem Bully, wie ich mich am sichersten aus der Mitte des Spielfeldes zurückziehen kann.','Das Spiel beginnt mit meinem Pfiff, wenn der Ball den Boden berührt.','Alle nicht am Bully beteiligten Spieler müssen 2 m Mindestabstand haben.','1#2#3#4#5#6','9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (122,'8','LJB',NULL,'Ein Ball steckt zwischen den Speichen eines Einrades des roten Teams. Was entscheidest Du?','Wenn ich nicht weiß wer geschossen hat, entscheide ich auf Bully.','Entscheidend ist wer den Ball geschossen hat. Freistoß für das andere Team.','Egal wer geschossen hat: Es gibt Freistoß (Torab, Ecke) für das blaue Team.','Es gibt Freistoß für das rote Team, egal wer geschossen hat.',NULL,NULL,'3','5.8',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (123,'1','LJBF',NULL,'Ein roter Spieler liegt seit einiger Zeit verletzt am Boden. Um den Ball wird weiter gekämpft. Was machst Du?','Ich warte bis ein Team klar in Ballbesitz ist oder der Ball im Aus ist. Dann unterbreche ich das Spiel und kümmere mich um den verletzten Spieler.','Ich unterbreche das Spiel sofort und kümmere mich um den verletzten Spieler.','Wenn der Ballbesitz im Moment der Unterbrechung unklar ist, wird das Spiel mit Bully fortgesetzt.','Nach der Unterbrechung gibt es Freistoß für Blau, da ein roter Spieler verletzt war.',NULL,NULL,'2#3','10.6h',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (124,'2','LJB',NULL,'Wie solltest du im Spiel auftreten?','Ich sollte durch bestimmtes und ruhiges Auftreten überzeugen.','Entscheidungen werden auf Anfrage erläutert, sie werden jedoch nicht mit den Spielern ausdiskutiert.','In einer unklaren Situation kann ich die Spieler kurz befragen, bevor ich eine endgültige Entscheidung treffe.','Ich mache den Spielern klar, dass ich immer Recht habe und nie einen Fehler machen werde.',NULL,NULL,'1#2#3','10.6b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (125,'8','LJB',NULL,'Du hast einen Regelverstoß (SUB) gesehen und das Spiel unterbrochen. Du entscheidest auf Freistoß für das blaue Team. Wie gehst du jetzt weiter vor?','Ich mache kurz das Handzeichen für SUB. Die Spieler wissen dann schon wie es weiter geht.','Ich mache klar und deutlich das Handzeichen für SUB und erkläre, wenn nötig, den Regelverstoß.','Anschließend zeige ich mit der einen Hand auf den Punkt von wo aus der Freistoß ausgeführt wird und mit der anderen Hand in die Spielrichtung des Freistoßes. Diese Position halte ich, bis das Spiel wieder läuft.','Wenn alle Spieler bereit sind, pfeife ich das Spiel zügig an, damit es zu keinen unnötigen Verzögerungen kommt.',NULL,NULL,'2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (126,'8','LJB','Larissa: Antwort 4 passt hier irgendwie nicht so zur Frage, vielleicht findet man da noch was passenderes Christian: Ich finde es ok zu sagen wie es weitergeht. (auch wieder die Vorteilsfrage). ','Eine roter Spielerin will den Ball mit der Hand spielen. Dazu fängt sie den Ball kurzzeitig und wirft ihn weiter weg. Darf sie das?','Ja, eine aktive Ballberührung ist erlaubt.','Nein. Der Ball darf nicht gefangen oder eingeklemmt werden. Auch nicht kurzzeitig.','Wenn der Ball nur kurz gehalten wird, ist es erlaubt.','Ich unterbreche das Spiel. Es geht weiter mit Freistoß für das blaue Team.',NULL,NULL,'2#4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (127,'1','LJB',NULL,'Wie viele Spieler dürfen maximal gleichzeitig für ein Team auf dem Feld spielen?','3 Spieler','4 Spieler','5 Spieler','6 Spieler',NULL,NULL,'3','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (128,'1','LJBF',NULL,'Ein Team hat das Spiel mit nur drei Spielern begonnen. Nun verletzt sich einer dieser drei Spieler und kann nicht weiter spielen. Kann das Team mit zwei Spielern weiter spielen?','Nein, es müssen immer mindestens drei Spieler auf dem Feld stehen. Das Spiel muss abgebrochen werden.','Ja, es reicht wenn zu Beginn des Spiels drei Spieler auf dem Feld sind.','Es ist egal wie viele Spieler auf dem Feld stehen.','Es kann nur weitergehen, falls ein Ersatzspieler von einem anderen Team ausgeliehen werden kann.',NULL,NULL,'2','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (129,'1','LJBF',NULL,'Ein Team möchte einen Spieler auswechseln. Worauf hast du zu achten?','Der eingewechselte Spieler darf das Spielfeld überall betreten.','Der eingewechselte Spieler muss das Spielfeld in der eigenen Spielhälfte betreten.','Der eingewechselte Spieler muss das Spielfeld an der gleichen Stelle betreten, wo ausgewechselte Spieler es verlässt.','Der eingewechselte Spieler darf das Spielfeld erst betreten, wenn der ausgewechselte Spieler das Feld verlassen hat.','Auswechslungen müssen dem Schiedsrichter angezeigt werden.',NULL,'2#3#4','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (130,'10','LJBF','Larissa: 9.5 und 9.1 "Wenn eine Strafe für Zeitspiel verhängt wird, wird der Freistoß am Mittelpunkt ausgeführt." widersprechen sich hier Christian: Ich verstehe es so, dass 9.5. hier 9.1 "schlägt" wenn es in den letzten 3 min. passiert. ','Ein Spieler des blauen Teams verzögert das Spiel absichtlich 2 min vor Spielende. Du entscheidest ihm eine 2 min Feldverweis zu geben. Wie geht das Spiel weiter? Worauf musst du achten?','Es gibt Freistoß für das rote Team am Mittelpunkt.','Da der Feldverweis weniger als 3 min vor dem Spielende ausgesprochen wurde, gibt es einen 6,50 m für das rote Team.','Es geht weiter mit Bully.','Ich muss darauf achten, dass bei der Diskussion die Uhr angehalten wird, damit kein Nachteil für das blaue Team entsteht.',NULL,NULL,'2#4','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (131,'10','LJBF',NULL,'Ein Spieler verzögert das Spiel. Du unterstellst ihm Absicht, um den knappen Vorsprung "über die Zeit zu bringen". Was tust du?','Ich ermahne den Spieler.','Ich erteile dem Spieler einen 2 min Feldverweis.','Es geht weiter mit Freistoß für das andere Team.','Wenn dies innerhalb der letzten 3 min geschieht, gibt es statt dem Freistoß einen 6,50 m.',NULL,NULL,'2#3#4','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (132,'5','LJBF',NULL,'Ein blauer Spieler steht/pendelt auf der Stelle. Dabei stützt er sich auf dem Schläger ab. Ein roter Spieler will an ihm vorbei fahren und fährt über den Schläger vom blauen Spieler. Beide Spieler stürzen. Was entscheidest du?','Da beide Spieler gestürzt sind, gibt es Bully.','Es kann weiter gespielt werden, da beide gestürzt sind und keine Gefahr besteht.','Es gibt Freistoß für das rote Team (sofern kein Vorteil).','Es gibt einen Freistoß für blau, da dem pendelnden Spieler immer ausgewichen werden muss.',NULL,NULL,'3','8.2c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (133,'11','LJBF',NULL,'Was gilt bei einem Freistoß?','Der Ball darf gelupft oder geschlenzt werden.','Der Ball darf nicht gelupft oder geschlenzt werden, sondern nur direkt geschossen.','Der Spieler darf mit dem Ball am Schläger losfahren.','Die gegnerischen Spieler müssen mindestens 2 m Abstand einhalten.',NULL,NULL,'1#4','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (134,'11','LJB',NULL,'Wo wird ein Freistoß ausgeführt?','Wenn ein Team einen Freistoß in der gegnerischen Torzone erhält, wird der Freistoß vom nächstgelegenen Eckpunkt ausgeführt (Eckstoß).','Wenn ein Team einen Freistoß in der eigenen Torzone bekommt, wird der Freistoß 1 m vor der Torlinie ausgeführt (Torabstoß).','Der Freistoß wird immer da ausgeführt, wo der Ball im Moment des Pfiffes ist.','Ist der Freistoß in keiner der Torzonen, wird er am Ort des Regelverstoßes ausgeführt.',NULL,NULL,'1#2#4','9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (135,'11','LJB',NULL,'Wie viel Abstand müssen gegnerische Spieler bei einem Freistoß halten?','2 m','1 m','3 m','Es gilt kein besonderer Abstand.',NULL,NULL,'1','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (136,'11','LJBF',NULL,'Ein roter Spieler steht beim Freistoß des blauen Spielers mit seinem Einrad 2 m vom Freistoßpunkt entfernt. Mit seinem Schläger kommt er aber näher an den Ball. Was gilt hier?','Das ist erlaubt. Solange das Einrad 2 m vom Freistoßpunkt entfernt ist.','Das ist nicht erlaubt, Einrad und Schläger müssen mindestens 2 m entfernt sein.','Das ist erlaubt, solange der Spieler den Ball nicht berührt.',NULL,NULL,NULL,'2','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (137,'Videos','L',NULL,'Schaue Dir das Video an. Wie würdest du entscheiden?','Ich unterbreche das Spiel sofort und entscheide auf Freistoß für das blaue Team.','Es liegt kein Foulspiel vor.','Der blaue Spieler wurde gefoult. SUB.','Ich unterbreche das Spiel nicht sofort, sondern zeige mit der Hand in Spielrichtung Blau und rufe "Vorteil". Wenn Blau weiter im Ballbesitz bleibt rufe ich bald "Vorteil ende".','Kommt Blau nicht in Ballbesitz, pfeife ich ab und gebe Freistoß für Blau.',NULL,'3#4#5','10.6f',NULL,1,NULL,'frage_0137.mp4',NULL,NULL,'nein',NULL),
    (138,'2','LJBF',NULL,'Ein Schuss aufs Tor trifft den Schiedsrichter. Dann wird der Ball vom Schiedsrichter auch noch (natürlich unabsichtlich) ins Tor des roten Teams abgelenkt. Was nun?','Das Tor zählt. Der Ball ist regulär ins Tor gegangen. Der Schiedsrichter ist "Luft".','Das Tor zählt nicht. Der Schiedsrichter hat dem angreifenden Team einen Vorteil verschafft.','Es geht weiter mit Bully.','Das Tor zählt, da Eigentore immer zählen.',NULL,NULL,'2#3','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (139,'11','LJB',NULL,'Wo wird der Freistoß nach einem Ausball im Mittelfeld ausgeführt?','1 m von der Seitenlinie entfernt.','Direkt auf der Seitenlinie.','2 m von der Seitenlinie entfernt.','Dort wo der Ball ins Aus ging.','Da wo die Spieler den Ball wieder ins Feld gebracht haben.',NULL,'1#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (140,'2','LJB',NULL,'Vor dem Tor geht es heiß her. Zwei Spieler auf dem Feld verdecken Dir die Sicht auf das Geschehen. Was nun?','Da ich gerade eh nix sehen kann, kann ich auch ein wenig mit meinen Team hinter der Bande sprechen.','Der andere Schiedsrichter wird es ja sicher sehen, daher muss ich nichts tun.','Ich fordere die beiden Spieler auf, doch bitte ein wenig auf Seite zu fahren, damit ich sehen kann.','Ich versuche mich schnell und vorsichtig so zu bewegen, dass ich einen guten Blick auf die Situation habe.','Ich kann auch ein wenig nach vorne ins Spielfeld treten, um einen guten Blick zu haben, solange ich keine Spieler dabei behindere.',NULL,'4#5','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (141,'2','LJB',NULL,'Wie verhältst du dich während des Spiels?','Ich stehe möglichst immer in der Nähe der Mittellinie, damit ich beide Seiten des Spielfeldes sehen kann.','Damit ich möglichst wenig störe, stehe ich direkt an der Wand mit einem Fuß nach hinten an der Wand angelehnt und den Händen hinter dem Rücken verschränkt.','Ich bewege mich mit dem Ball auf und ab und versuche immer einen guten Blick auf Spiel zu haben.','Meine Körpersprache zeigt, dass ich aufmerksam bin. Ich habe die Pfeife bereit, um jederzeit eine Entscheidung treffen zu können.','Zwischendurch frage ich immer mal wieder meine Freunde außerhalb des Spielfeldes, was es so Neues gibt.',NULL,'3#4','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (142,'2','LJB',NULL,'Welche besonderen Aufgaben hast du als 1. Schiedsrichter?','Nur ich treffe alle wichtigen Entscheidungen.','Nur ich darf ein Foul pfeifen oder das Spiel unterbrechen.','Ich überstimme den 2. Schiedsrichter, falls wir unterschiedlicher Meinung sind.','Ich pfeife das Spiel nach Unterbrechungen wieder an.','Ich lasse den Ball zum Bully fallen.','Nur ich vergebe Zeitstrafen.','3#4#5','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (143,'2','LJBF',NULL,'Es gibt Freistoß. Er wird schnell ausgeführt, bevor das Spiel angepfiffen wurde. Was ist zu tun?','Ich unterbreche das Spiel und lasse den Freistoß wiederholen.','Wenn alle weiterspielen, kann das Spiel weitergehen.','Ich versuche das Spiel immer zügig anzupfeifen, wenn alle Spieler bereit sind.','Das ist ein Regelverstoß. Es gibt jetzt einen Freistoß für das andere Team.',NULL,NULL,'1#3','5.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (144,'Videos','L',NULL,'Schaue Dir das Video an. Wie beurteilst Du die Situation?','Der weiße Spieler wird gesubt. Ich unterbreche das Spiel sofort und entscheide auf Freistoß für das weiße Team, da es keinen Vorteil gibt.','Es liegt kein Foulspiel vor.','Der weiße Spieler wurde gefoult. SUB.','Die Situation ist unklar, daher unterbreche ich das Spiel und gebe ein Bully.',NULL,NULL,'2',NULL,'Der weiße Spieler ist über den eigenen Schläger gestürzt, der an der Wand eingeklemmt war.',1,NULL,'frage_0144.mp4',NULL,NULL,'nein',NULL),
    (145,'Videos','L','Rolf: Erst Vorteil abwarten, Blau hat kurz nach dem hohen Schläger eine gute Torchance (trifft den Ball aber nicht) Christian: Antworten (und Frage) muss hier umformuliert werden. ','Schaue Dir das Video an. Welchen Regelverstoß erkennst du? Welche Aussagen stimmen?','Es ist kein Regelverstoß zu sehen.','Es ist ein hoher Schläger vom gelben Spieler zu sehen.','Das Spiel läuft weiter, da niemand gefährdet wurde.','Es gibt Freistoß für Blau am Ort des Geschehens.','Es gibt Eckball für Blau, da es innerhalb von 6,50 m Bereich passiert ist.','Da das Spiel weiter läuft, unterbreche ich es energisch (mehrfaches Pfeifen) und ahnde den Regelverstoß.','2#5#6','8.10#9.1a',NULL,1,NULL,'frage_0002.mp4',NULL,NULL,'nein',NULL),
    (146,'8','LJB',NULL,'Der Ball ist im Aus. Wie geht es weiter?','Sobald der Ball das Spiel verlassen hat, pfeife ich und unterbreche das Spiel.','Ich entscheide, wer zuletzt am Ball war. Das andere Team bekommt einen Freistoß.','Ich zeige mit der Hand in Spielrichtung und kann dies auch laut sagen, z.B. "Freistoß für Blau".','Ich zeige solange in Spielrichtung, bis das Spiel wieder beginnt.','Sobald die Spieler bereit sind, pfeife ich das Spiel zügig an.','Wenn der Ball sofort wieder ins Feld zurück springt, kann das Spiel ohne Unterbrechung weitergehen.','1#2#3#4#5','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (147,'2','LJB',NULL,'Wie kannst du dazu beitragen, dass das Spiel bei Ausbällen zügig weitergeht?','Du hast Reservebälle dabei. Wenn der Ball über die Bande geschossen wurde, lege ich einen neuen Ball auf den Freistoßpunkt. Den hinausgeschossenen Ball lasse ich mir später zurückgeben.','Ich kommuniziere meine Entscheidung klar und deutlich und halte den Arm ausgestreckt in Spielrichtung, bis das Spiel wieder läuft.','Wenn ich mir unsicher bin, wer zuletzt am Ball war, frage ich kurz die Spieler. Gibt es nicht sofort eine Einigung, entscheide ich auf Bully.','Ich kann den Spielern helfen, indem ich den Ball an den richtigen Freistoßpunkt lege und die Entscheidung auch verbal kommuniziere, z.B. "Torab!".','Wenn der Ball aus dem Aus wieder zurück ins Feld springt, z.B. von der Wand hinter der Eckbank, kann ich das Spiel weiterlaufen lassen.','Nur als erster Schiedsrichter pfeife ich das Spiel wieder an, um "Doppelanpfiffe" zu verhindern.','1#2#3#4#6','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (148,'9','LJB',NULL,'Der Ball ist von hinten ins Tornetz geschossen worden und ist nicht mehr spielbar. Wer bekommt einen Freistoß?','Es gibt in jedem Fall Torab, unabhängig davon wer geschossen hat.','Es gibt in jedem Fall Eckball, unabhängig davon wer geschossen hat.','Entscheidend ist, wer den Ball zuletzt berührt hat. Es gibt Freistoß (Torab oder Eckball) gegen dieses Team.','Es gibt in diesem Fall immer ein Bully am Eckpunkt.','Wenn es unklar ist wer den Ball zuletzt berührt hat, entscheide ich auf Bully am Eckpunkt.',NULL,'3#5','7.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (149,'9','L','"vollständig über die Torlinie gelangt" steht nicht in den Regeln. Sollte man hinzufügen. ','Tor oder nicht? Das ist schon mal eine wichtige Frage... Was stimmt?','Das Tor zählt wenn der Ball vollständig über die Torlinie gelangt ist.','Das Tor zählt, wenn der Ball die Torlinie nur berührt hat.','Das Tor zählt, wenn der Ball vollständig auf der breiten Torlinie ist.','Auch wenn der Ball seitlich, z.B. durch ein Loch im Netz ins Tor gelangt ist, zählt das Tor.',NULL,NULL,'1','7.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (150,'6','LJBF',NULL,'Wann und wie darf ein Spieler den Schläger des anderen Spielers anheben?','Nur in der Nähe des Balles darf ein Spieler den Schläger des Gegners mit dem eigenen Schläger anheben.','Das ist überall auf dem Feld erlaubt, auch wenn der Ball  nicht in der Nähe ist.','Das Hochheben darf nicht mit übertriebener Härte erfolgen.','Der gegnerische Schläger darf nicht zu einem hohen Schläger angehoben werden.',NULL,NULL,'1#3#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (151,'8','LJBF',NULL,'Es gibt eine turbulente Szene vor dem roten Tor. Ein roter Spieler hat das Tor unabsichtlich verschoben. Was machst du?','Ich kann das Spiel unterbrechen und einen Freistoß (Eckball) für das blaue Team geben.','Ich lasse das Spiel in jedem Fall weiterlaufen.','Wenn es den Spielern gelingt das Tor schnell wieder richtig zu stellen ohne das Spiel zu beeinflussen, kann das Spiel weiterlaufen.','Wenn dadurch eine klare Torchance des blauen Teams verhindert wurde, gebe ich einen 6,50 m.',NULL,NULL,'1#3#4','8.6#8.1#9.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (152,'10','LJBF','Rolf: In der Frage steht nicht, dass es eine gute Torchance war. Also nicht unbedingt 6.50m. Evtl. nur Ecke. Christian: Ein Spieler der das Tor verschiebt eindeutig absichtlich um eine Situation zu "entschärfen" würde ich immer mit 6,50 m und 2 min bestrafen. Aber laut Regeln hat Rolf recht. Was eine gute und was nur eine Torchance ist? Dazu gibt die Regel keinen Hinweis. ','Eine turbulente Szene vor dem roten Tor: Ein roter Spieler hat das Tor verschoben. Du unterstellst ihm Absicht um eine Torchance des blauen Teams zu verhindern. Was entscheidest Du?','Ich ermahne den roten Spieler und entscheide auf Eckball für Blau.','Ich ermahne den roten Spieler. Es geht weiter mit Bully am Eckball wenn das Tor wieder richtig steht.','Ich entscheide auf 6,50 m für das blaue Team.','Ich gebe dem roten Spieler eine 2 min. Feldverweis wegen unsportlichen Verhaltens.',NULL,NULL,'3#4','8.6#9.2#9.5a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (153,'8','LJBF',NULL,'Eine turbulente Szene vor dem roten Tor: Ein angreifender blauer Spieler hat das Tor unabsichtlich verschoben. Blau ist in einer aussichtsreichen Spielsituation. Was entscheidest du?','Ich unterbreche das Spiel und geben ein Torab für das rote Team.','Ich lasse das Spiel weiter laufen, sofern der Torwart das Tor schnell wieder gerade stellen kann.','Ich unterbreche das Spiel und gebe einen Bully am Eckpunkt.','Ich unterbreche das Spiel und gebe einen Eckball.',NULL,NULL,'1','8.6','In diesem Fall sollte man das Spiel nicht weiterlaufen lassen. Blau hat einen Regelverstoß begangen (Verschieben des Tores). Daraus darf sich keine Vorteilssituation für Blau entwickeln. Daher ist Unterbrechung und Torab hier die beste Entscheidung.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (154,'10','LJBF','Rolf: Rot statt blau in Antwort 2 und 3?
Larissa: ja so ist es irgendwie komisch. Für mich stimmt 4 auch nicht, da ich in der Frage Vorteil gebe. Dann kann ich ja keinen Bully geben.  Rolf: Leider sind die offiziellen Regeln hier widersprüchlich: 1) "Wenn
ein Vorteil nicht erlangt wurde, sollte der Schiedsrichter die
ENTSPRECHENDE STRAFE für den ursprünglichen Regelverstoß AM
URSPRÜNGLICHEN ORT durchsetzen." 2) "...sollte das Spiel mit einem BULLY
AM ORT DES BALLBESITZWECHSELS wieder aufnehmen." Christian: Habe rot/blau verwechselung korrigiert. Ja, die Regel ist unklar. ','Du hast ein grob unsportliches und gefährliches Foul eines roten Spielers gesehen. Du entscheidest jedoch auf Vorteil und lässt das Spiel mit einer entsprechenden Ansage (Handzeichen plus "Vorteil") weiterlaufen. Was musst du jetzt tun?','Nichts. Das Spiel läuft wegen Vorteil ja weiter. Es gibt dann auch keinen Feldverweis.','Ich warte bis das rote Team in Ballbesitz kommt (Vorteil Ende), oder das Spiel unterbrochen wird. Dann gebe ich dem roten Spieler einen Feldverweis.','Ich warte bis das rote Team in Ballbesitz kommt (Vorteil Ende), oder das Spiel unterbrochen wird. Dann ermahne ich den roten Spieler.','Es geht weiter mit Bully am Ort des Ballbesitzwechsels.',NULL,NULL,'2#4','9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (155,'4','LJBF',NULL,'Wer darf bei einem Penaltyschießen wie oft antreten? Weißt du es?','Jeder Spieler darf bei einem Penaltyschießen grundsätzlich nur einmal schießen.','Ein Spieler darf mehrfach schießen. Es muss jedoch mindestens ein anderer Spieler dazwischen schießen.','Ein Spieler darf mehrfach schießen. Es müssen jedoch immer mindestens zwei andere Spieler dazwischen schießen.','Der Torwart darf nicht schießen.','Der Torwart darf bei jedem Schuss beliebig wechseln.',NULL,'3#5','5.2a','Ob der Torwart beliebig wechseln kann, ist in den Regeln nicht definiert, es wird aber üblicherweise erlaubt.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (156,'4','LJB',NULL,'Immer wieder spannend: Penaltyschießen. Wie läuft es ab?','Jedes Team bekommt zunächst drei Penaltys.','Ist der Spielstand nach 3 Penaltys noch Unentschieden, schießt jedes Team einen weiteren Penalty, bis eine Entscheidung herbeigeführt ist.','Jedes Team bekommt 5 Penaltys.','Wer zuerst trifft, hat gewonnen.',NULL,NULL,'1#2','5.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (157,'4','LJBF',NULL,'Was ist bei der Durchführung von einem Penalty zu beachten?','Für den Penaltyschussmüssen alle Spieler außer dem verteidigenden Torwart die entsprechende Spielfeldhälfte verlassen.','Der Torwart muss sich in der Nähe der Torlinie befinden, bis der Angreifer den Ball berührt.','Der Spieler muss in Bewegung Richtung Tor bleiben, eine Rückwärtsbewegung oder Anhalten ist nicht zulässig.','Wenn der Ball vom Torwart abprallt, hat der Angreifer immer noch einen Nachschuss.',NULL,NULL,'1#2#3','5.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (158,'4','LJBF',NULL,'Der Ball muss beim Penalty immer nach vorne bewegt werden, nie zurück. Stimmt das?','Ja.','Nein. Entscheidend ist, dass der angreifende Spieler sich immer nach vorne bewegt und nicht stehen bleibt/sich zurück bewegt.','Spieler und Ball dürfen sich vor und zurück bewegen.','Das liegt im Ermessen des Schiedsrichters.',NULL,NULL,'2','5.2','Achtung, diese Regel ist relativ neu.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (159,'4','LJBF',NULL,'Beim Penalty geht es zur Sache: Der ballführende Spieler wird vom Torwart gefoult. Was nun?','Der Penalty wird wiederholt.','Der Penalty wird als "nicht getroffen" gewertet.','Ich entscheide nach meinem Ermessen auf Straftor oder 6,50 m.','Das Penaltyschießen. wird abgebrochen.',NULL,NULL,'3','5.2b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (160,'8','LJB',NULL,'Der Ball wird erst von einem roten Spieler, dann direkt danach von einer blauen Spielerin aktiv mit der Hand geschlagen. Beide schlagen den Ball nur einmal. Dazwischen wird der Ball von niemandem berührt. Ist das erlaubt?','Nein, dies ist eine doppelte (mehrfache) aktive Ballberührung mit der Hand.','Nein, Freistoß für Rot (oder Vorteil).','Ja, da beide den Ball nur einmal aktiv spielen.','Ja. Weiterspielen.',NULL,NULL,'3#4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (161,'2','LJB',NULL,'Du hast aus versehen gepfiffen. Scheinbar hat es niemand gehört. Die Spieler spielen weiter. Was musst du tun?','Es hat ja (vielleicht) keiner gehört, also kann das Spiel weiter gehen.','Wenn der Spielfluss nicht wesentlich beeinflusst wurde, kann das Spiel weiter gehen.','Ich rufe laut "weiter" und mache das Handzeichen für "Kein Foul".','Ich pfeife mehrmals laut und unterbreche das Spiel. Ich entschuldige mich für den falschen Pfiff. Das Spiel geht weiter mit Bully oder Freistoß für das Team im Ballbesitz nach meinem Ermessen.',NULL,NULL,'4','10.6a#9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (162,'2','LJB',NULL,'Du bist zweiter Schiedsrichter. Der erste Schiedsrichter hat ein Foul gepfiffen und entscheidet auf Freistoß. Du denkst, dass das Foul möglicherweise die Kriterien für einen 6,50 m erfüllt. Was tust Du?','Der erste Schiedsrichter überstimmt mich. Daher sage ich nichts und halte mich raus.','Ich lasse die Zeit anhalten und bespreche die Situation mit dem ersten Schiedsrichter. Wir treffen dann gemeinsam eine Entscheidung.','Wenn keine gemeinsame Entscheidung zustande kommt, überstimmt der erste Schiedsrichter den Zweiten.','Da der erste Schiedsrichter erfahrener und älter ist als ist, halte ich mich raus.',NULL,NULL,'2#3','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (163,'8','LJB',NULL,'Der Ball wird von mehreren Spielern des gleichen Teams aktiv mit der Hand geschlagen. Jeder Spieler schlägt den Ball nur einmal. Dazwischen wird der Ball von niemandem berührt. Ist das erlaubt?','Nein, dies ist eine doppelte (mehrfache) aktive Ballberührung mit der Hand.','Nein, Freistoß für das andere Team (oder Vorteil).','Ja, da beide den Ball nur einmal aktiv spielen.','Ja. Weiterspielen.',NULL,NULL,'3#4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (164,'9','LJB',NULL,'Ein Freistoß aus dem Mittelfeld wird direkt aufs Tor geschossen. Der Ball geht ins Tor, ohne dass ihn ein anderer Spieler berührt.','Das Tor zählt nicht, da jeder Freistoß indirekt ist.','Das Tor zählt.','Es geht weiter mit Bully.','Es geht weiter mit Torab.',NULL,NULL,'1#4','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (165,'9','LJBF',NULL,'Fernschuss oder nicht? Worauf kommt es an?','Ob der Ball im Moment des Schusses vor oder hinter der Mittellinie war?','Ob das Einrad des Spielers im Moment des Schusses vor oder hinter der Mittellinie war?','Das Einrad des Spielers und der Ball müssen hinter der Mittellinie sein, damit das Tor zählt.','Auf die Position des Körpers des Spielers vor dem Schuss.',NULL,NULL,'1','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (166,NULL,'L',NULL,'Schaue Dir das Video genau an. Wie bewertest Du die Situation? Was würdest Du entscheiden?','Es liegt kein Foulspiel vor.','Hoher Schläger von Weiß - Eckball für Grün.','Hoher Schläger von Weiß - 6,50 m für Grün.','Vorteil für Grün. Daher weiterspielen.',NULL,NULL,'3',NULL,'Der grüne Spieler hat in dieser Situation eine klare Torchance, auch wenn er sich den Ball zunächst mit der Hand vorlegen möchte. Der weiße Spieler greift mit einem klaren "Hohen Schläger" von hinten auf den Körper des grünen Spielers ein. Damit stört er die direkte Torchance des grünen Spielers mit einem Foul. <BR> Nach Ende des Vorteils (die Torchance konnte nicht genutzt werden) ist somit auf 6,50 m für Grün zu entscheiden. <BR> Eine Bewertung des Fouls als "gefährliches Spiel" und 2 min Zeitstrafe liegt im Ermessen des Schiedsrichters.',1,NULL,'frage_0166.mp4','antwort_0166.jpg',NULL,'nein',NULL),
    (167,NULL,'L',NULL,'Schaue Dir das Video genau an. Wie bewertest Du die Situation? Was würdest Du entscheiden?','Das das Foul (SUB) abseits des Balles geschieht und das Spiel nicht beeinflusst - läuft das Spiel weiter.','Die grüne Spielerin foult den weißen Spieler - SUB.','Hoher Schläger von Grün.','Ich entscheide auf Hoher Schläger von Grün - Freistoß für Weiß.','Ich entscheide auf Sub von Grün - Freistoß für Weiß.',NULL,'2#3#4','8.10#8.3#10.6d','Diese Situation hat zwei Teile. Zunächst begeht die grüne Spielerin einen hohen Schläger beim Torschuss. Anschließend subt sie den weißen Spieler.  Das Sub erfolgt abseits des Spielgeschehens. Da Grün in Ballbesitz kommt und der weiße Spieler absteigen musste, kann man argumentieren, dass es trotzdem spielrelevant ist und mit einem Freistoß für Weiß zu ahnden ist. Es kommt jedoch zunächst 10.6 zur Anwendung: <BR> <i>Wenn zwei Regelverstöße direkt hintereinander auftreten, wird nur der erste bestraft.</i><BR> Der Schiedsrichter bestraft also den hohen Schläger beim Torschuss mit einem Freistoß für Weiß.',1,NULL,'frage_0167.mp4','antwort_0167.jpg',NULL,'nein',NULL),
    (168,'11','LJB',NULL,'Einen 6,50 m kann es nur geben, wenn das Foul eine direkte Torchance verhindert hat UND innerhalb der Torzone (6,50 m Bereich) erfolgt ist. Richtig oder Falsch?','Falsch.','Richtig.',NULL,NULL,NULL,NULL,'1',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (169,'7','LJB',NULL,'Ein roter Spieler stützt sich an der Wand ab, gleichzeitig spielt er den Ball im Zweikampf. Was entscheidest Du?','Es gibt Freistoß für das blaue Team.','Weiterspielen. Kurzzeitig darf man sich an der Wand abstützen.','Es gibt Bully.','Vorteilsentscheidung ist möglich.',NULL,NULL,'1#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (170,'7','LJB',NULL,'Eine rote Spielerin stützt sich kurzzeitig an der Wand ab, um nicht zu stürzen. Dabei greift sie nicht ins Spiel ein. Was entscheidest Du?','Es gibt Freistoß für das blaue Team.','Weiterspielen, kurzzeitiges Abstützen ist erlaubt.','Ich ermahne die rote Spielerin, sich nicht abzustützen.','Es geht weiter mit Bully.',NULL,NULL,'2','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (171,'7','LJBF',NULL,'Eine rote Spielerin stützt sich an der Wand ab, um nicht zu stürzen. Dann nutzt sie die Wand um sich abzudrücken und so einen Richtungswechsel vorzunehmen. Daraufhin erobert sie den Ball. Wie entscheidest Du?','Weiterspielen. Kurzzeitiges Festhalten an der Wand ist erlaubt.','Freistoß für Blau. Die Spielerin darf die Wand nicht zu ihrem Vorteil nutzen.','Weiterspielen. Aber ich ermahne sie bei nächster Gelegenheit.','Ich ermahne die Spielerin und es geht weiter mit Bully.',NULL,NULL,'2',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (172,'7','LJBF','Rolf: ich würde noch ergänzen, bei nächster Spielunterbrechung Torwart ermahnen. Christian: Das ist auch unklar. Den Torwart ermahnen für ein Vergehen, dass man nicht bestrafen kann? Man darf nicht nicht festhalten am Tor wenn der Ball auf der anderen Seite ist... es wird aber auch nicht bestraft?','Der rote Torwart hält sich am Tor fest, während das Spiel in der anderen Spielhälfte stattfindet und keine Gefahr für sein Tor droht. Unterbrichst Du das Spiel?','Das Spiel läuft weiter, da das Festhalten am Tor das Spiel nicht beeinflusst.','Der Torwart darf sich nicht am Tor festhalten. Daher unterbreche ich das Spiel und ermahne den Torwart.','Ich unterbreche das Spiel und gebe Eckball für Blau.','Ich unterbreche das Spiel und gebe Freistoß für Blau.',NULL,NULL,'1','5.3#8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (173,'7','LJBF',NULL,'Der rote Torwart hält sich am Tor fest, während Blau im Ballbesitz ist und angreift. Bisher kam es nicht zum Torschuss. Wie verhältst Du Dich?','Ich lasse das Spiel weiterlaufen (Vorteil).','Ich ermahne den Torwart bei nächster Gelegenheit, sich nicht am Tor festzuhalten, da es nicht reicht das Tor nur loszulassen um den Ball zu halten.','Ich unterbreche das Spiel und gebe Freistoß für Blau.','Ich unterbreche das Spiel, ermahne den Torwart. Es geht weiter mit Bully.',NULL,NULL,'1#2','5.3#8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (174,'11','LJB',NULL,'Worauf musst du bei der Durchführung eines 6,50 m achten?','Der Torwart darf nicht weiter als 50 cm von der Torlinie entfernt mit seinem Rad stehen.','Alle Spieler (außer dem Torwart und dem Schützen) müssen die Torzone verlassen.','Der Schuss darf nicht geschlenzt oder gelupft werden.','Wenn der Ball abprallt oder die Torlinie überquert, geht das Spiel weiter.',NULL,NULL,'1#2#4','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (175,'11','LJB',NULL,'Es gibt einen 6,50 m für das rote Team. Was trifft zu?','Nur der gefoulte Spieler des roten Teams darf den 6,50 m schießen.','Das angreifende rote Team bestimmt einen beliebigen Spieler, der den 6,50 m schießt.','Der blaue Torwart zum Zeitpunkt des Fouls muss Torwart bleiben.','Jeder blaue Spieler kann Torwart beim 6,50 m sein.',NULL,NULL,'2#4','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (176,'11','LJBF','Larissa: Ist das echt so? Ich dachte immer, dass da wie beim freistoß jemand anderes dran sein muss bevor ich den wieder berühren darf. Christian: Regel ist auch hier unklar. Vor einem Tor darf der Schütze nur einmal berühren. Überschreitet der Ball die Torlinie geht es "weiter". Unklar, ob der Schütze dann sofort eingreifen kann. ','Ein 6,50m für das rote Team! Der Schütze schießt am Tor vorbei. Der Ball geht gegen die Wand und kommt zurück vor das Tor. Der rote 6,50m Schütze schießt den Ball direkt wieder in das Tor, ohne das ihn jemand anderes berührt hat.','Das Tor zählt nicht, da der Schütze den Ball nur einmal berühren darf. ','Das Tor zählt. Wenn der Ball abprallt oder die verlängerte Torauslinie überschreitet, geht das Spiel weiter.','Es geht weiter mit Bully am Eckpunkt.','Es gibt Torab.',NULL,NULL,'2','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein','Nebenbei: "Der Ball soll mit dem Schläger geschlagen werden. Der Ball darf ebenfalls geschlenzt und gelupft werden." ist nur wirklich Quatsch. Soll-Vorschriften sind da ja Unsinn. Entweder darf ich schlenzen oder nicht.  (Christian). '),
    (177,'5','LJBF',NULL,'Zwei Spieler fahren im Kampf um den Ball in einem stumpfen Winkel aufeinander zu, sie kollidieren und beide stürzen. Welcher Grundsatz kann Dir helfen eine Entscheidung zu treffen?','Im Zweifel das Spiel immer weiterlaufen lassen.','Es wird der Spieler betraft, der den Kontakt anscheinend herbeigeführt hat.','Es wird immer der Spieler betraft, der am weitesten vom Ball entfernt ist.','Es wird immer der schnellere Spieler betraft.',NULL,NULL,'2','8.2e',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (178,'7','LJB',NULL,'Drei Spieler prallen zusammen und stürzen. Beide Teams sind beteiligt, der Ball liegt unter einem Einrad. Es ist nicht klar, wer daran Schuld ist.','Bully dort, wo der Ball liegt. An dem Bully nehmen alle drei beteiligten Spieler teil.','Freistoß gegen den Spieler, unter dessen Einrad der Ball liegt.','Bully dort, wo der Ball liegt. Die Teams suchen die beiden Spieler aus, die den Bully ausführen.','Bully dort, wo der Ball liegt. Ich suche zwei von den beteiligten Spielern aus, die den Bully ausführen sollen.',NULL,NULL,'3','9b#9.4','Einen Bully für 3 Spieler gibt es nicht.',1,NULL,NULL,NULL,NULL,'nein',NULL),
    (179,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','5 min Pause','6.50 m','Fünffaches Handspiel','Hoher Schläger',NULL,'3','10.7c',NULL,1,'frage_0179.png',NULL,NULL,NULL,'nein',NULL),
    (180,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','Freistoß','Tor','Fünffaches Handspiel','Hoher Schläger',NULL,'2','10.7a',NULL,1,'frage_0180.png',NULL,NULL,NULL,'nein',NULL),
    (181,'3','LJB',NULL,'Was bedeutet dieses Handzeichen?','Feldverweis für 2 min','5 min Pause','Mehr als 5 Spieler auf dem Feld','Fünffaches Handspiel','Bully',NULL,'5','10.7b',NULL,1,'frage_0181.png',NULL,NULL,NULL,'nein',NULL),
    (182,NULL,'L',NULL,'Schaue Dir das Video an. Wie entscheidest du?','Tor','Ecke','Torab','6.50 m','Keine Spielunterbrechung',NULL,'1','9.3','Am Anfang der Szene hält sich der Torwart am Tor fest. Das sollte wegen Vorteil noch nicht gepfiffen werden. Dann hält der Torwart einen Ball, der sicher ins Tor gegangen wäre. Dafür gibt es ein Straftor.',1,NULL,'frage_0182.mp4',NULL,NULL,'nein',NULL),
    (183,'2','LJB',NULL,'Es steht 8:7 für Blau und es sind noch 10 Sekunden zu spielen. Der Ball geht ins Aus und landet im Publikum. Du pfeifst eine Ecke für Rot. Wie verhälst du dich?','Ich warte ab. Jemand im Publikum wird den Ball schon wieder zurück werfen.','Es ist egal wie lange das Spiel noch geht, die Regeln zum Durchführen einer Ecke sind immer gleich.','Ich mache sofort Zeitstopp bis die Ecke ausgeführt werden kann. Schließlich soll Rot ja noch eine Chance erhalten ein Tor zu erzielen.','Die Zeit läuft weiter, aber ich beühme mich, den Ball so schnell wie möglich auf den Eckpunkt zu legen. Idealerweise habe ich einen Ersatzball in der Hand.',NULL,NULL,'2#4','5.7',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (184,'2','LJB',NULL,'Du bist erster Schiedsrichter. Während des Spiels bemerkst du, dass der zweite Schiedsrichter sich mit dem Publikum unterhält und nicht mehr aufmerksam dem Spiel folgt.','Ungeachtet dessen, was gerade im Spiel passiert, unterbreche ich das Spiel sofort und spreche mit ihm.','Kein Problem. Ich bin erster Schiedsrichter und darf alles alleine entscheiden.','Ich warte bis zur nächsten Unterbrechung, mache einen Zeitstopp und spreche mit ihm.','Er verhält sich nicht richtig. Als Schiedsrichter muss man aktiv dabei sein, mitlaufen und mit dem Blick dem Spiel folgen.',NULL,NULL,'3#4','10.6c#10.2a',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (185,'2','LJB',NULL,'Du möchtest das Spiel anpfeifen. Kurz vorher bemerkst du, dass der zweite Schiedsrichter dieselbe T-Shirt Farbe hat, wie eins der Teams.','Es ist notwendig, dass beide Schiedsrichter dieselbe Farbe tragen. Wir müssen uns vorher einigen welche Farbe wir tragen.','Das ist mir egal. Hauptsache die Teams unterscheiden sich in den Farben.','Ich pfeife nicht an, sondern mache ihn darauf aufmerksam. Wenn er sich umgezogen hat, beginnen wir das Spiel.','Ich pfeife trotzdem an. Er wird das schon noch selbst bemerken.',NULL,NULL,'3','10.2a','Es ist wünschenwert, aber nicht zwingend notwendig, dass beide Schiedsrichter dieselbe Farbe tragen.',NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (186,'2','LJB',NULL,'Wo befindest du dich während des Spiels?','Ich bleibe in Ballnähe, halte aber etwas Abstand.','Ich bleibe auf meiner Seite vor der Bande an der Mittellinie stehen.','Immer direkt am Ball. Je näher ich dran bin, desto besser.','Ich laufe an der Seitenlinie mit, weiche aber, falls nötig, zur Mitte aus.',NULL,NULL,'1#4','10.2a','Du solltest dich bewegen und mit dem Spiel mitlaufen. Aber nicht die Spieler stören. Es kann hilfreich sein das Spielgeschehen mit etwas Abstand zu betrachten, um einen Überblick zu haben.',NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (187,'1','LJB',NULL,'Was musst du beim Auswechseln von Spielern beachten?','Wechseln ist nur in der eigenen Hälfte erlaubt.','Die Spieler müssen es mir vorher mitteilen, wenn sie auswechseln möchten.','Solange er nicht ins Spiel eingreift, kann der neue Spieler kann das Feld schon einmal betreten und aufsteigen, auch wenn der andere Spieler noch auf dem Feld ist.','Auswechseln geht nur nach Spielunterbrechungen.',NULL,NULL,'1','4',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (188,'1','LJB',NULL,'Was musst du beim Auswechseln von Spielern beachten?','Es ist nicht nötig, dies dem Schiedsrichter vorher mitzuteilen.','Auswechseln eines Spielers ist jederzeit möglich.','Es kann an jeder Stelle gewechselt werden.','Der neue Spieler darf das Spielfeld erst betreten, nachdem der ausscheidende Spieler das Spielfeld vollständig verlassen hat.',NULL,NULL,'1#2#4','4',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL),
    (189,'4','LJBF','Ist vielleicht nicht 100% korrekt, wenn es nach dem ersten Schützen von Team A schon 3:0 steht Christian: In den Regeln steht nicht, dass das Penaltyschießen zuende ist, wenn es entschieden ist. Laut Regeln müssen immer mindestens 3 schießen. Vielleicht umformulieren?','Wie viele Spieler von jedem Team treten mindestens bei einem Penatlyschießen an?','1','2','3','4',NULL,NULL,'3','5.2a',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (190,'4','LJBF',NULL,'Ist es möglich, dass ein Spieler mehr als einen Schuss beim Penaltyschießen machen kann?','Ja, aber es muss mindestens ein anderer Spieler seines Teams einen Schuss machen, bevor derselbe Spieler nochmal schießen kann.','Ja, aber es müssen mindestens zwei andere Spieler seines Teams einen Schuss machen, bevor derselbe Spieler nochmal schießen kann.','Ja, aber maximal zwei mal.','Nein, jeder Spieler darf nur einmal schießen.',NULL,NULL,'2','5.2a',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (191,'4','LJBF',NULL,'Der Torwart darf während eines Penaltyschießens gewechselt werden.','Richtig',' Falsch',NULL,NULL,NULL,NULL,'1','4#5.2','Jeder Spieler kann zu jeder Zeit Torwart sein.',NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (192,'4','LJBF',NULL,'Was darf ein angeifender Spieler während eines Penalties?','Vorwärts fahren','Rückwarts fahren','Anhalten ','Pendeln',NULL,NULL,'1','5.2b',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (193,'4','LJBF',NULL,'Beim Penaltyschießen schießt der Spieler am Tor vorbei, der Ball kommt von der Wand zurück, prallt gegen den Schläger des Torwarts und geht ins Tor. Wie entscheidest du?','Kein Tor, beim Penaltyschießen können Abpraller nie zum Tor führen.','Tor, wenn der Ball vom Torwart abprallt, gilt das Tor.','Kein Tor, sobald der Ball die Torlinie überquert, wird der Schuss als komplett angesehen.','Tor, wäre der Ball an den Schläger des Schützen gesprungen, hätte das Tor nicht gezählt.',NULL,NULL,'3','5.2b',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (194,'4','LJBF',NULL,'Beim Penaltyschießen foult der Torwart den Schützen. Der Ball wäre nicht ins Tor gegangen. Wie entscheidest du?','Der Penalty ist vergeben.','Tor.','6,50m.','Wiederholung des Penalties.',NULL,NULL,'3','5.2b','Wird der Ballführende gefoult, gibt der Schiedsrichter nach Ermessen ein Tor oder einen 6,50 m. Das der Ball nicht rein gegangen wäre, ist der 6,5m hier die richtige Wahl.',NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (195,'4','LJBF',NULL,'Beim Penaltyschießen foult der Schütze den Torwart. Der Ball wäre ins Tor gegangen. Wie entscheidest du?','Der Penalty ist vergeben.','Tor.','6,50m.','Wiederholung des Penalties.',NULL,NULL,'1','5.2b','Wird der Torwart beim Penalty gefoult, ist dieser Penalty-Versuch vergeben.',NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (196,'9','LJB',NULL,'Ein Spieler setzt zum Torschuss an. Worauf musst Du in dieser Situation achten?','Welches Geschlecht er oder sie hat.','Wo sich der gegnerische Torwart befindet.','Ob sich ein anderer Spieler in der Nähe befindet.','Ob er eine bessere Anspielstation hätte.',NULL,NULL,'3',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (197,'6','LJBF','siehe auch Nr. 54','Ein blauer Spieler hakt seinen Schläger mit nach unten gedrehter Kelle beim Schläger eines vorbeifahrenden roten Spielers ein, so dass der im Fahren behindert wird und sich gerade noch auf dem Rad halten kann. Der rote Spieler führt den Ball. Wie entscheidest Du?','Absichtliches Foulspiel. Feldverweis für 2 Minuten gegen den blauen Spieler.','Foul. Freistoß für Rot. Ich verwarne den blauen Spieler für einen Feldverweis beim nächsten Vergehen.','Der Einsatz war offensichtlich nicht zu hart, weil der Spieler nicht gefallen ist.','Kein Foulspiel. Weiterspielen.',NULL,NULL,'2','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL),
    (198,'11','LJB','siehe auch Nr. 75','Was gilt für einen Freistoß in der deutschen Einradhockeyliga?','Ein Freistoß ist grundsätzlich direkt, darf also direkt ins Tor geschossen werden.','Ein Freistoß darf sowohl  geschlagen, geschlenzt oder gelupft ausgeführt werden.','Das nicht ausführende Team muss einen Mindestabstand von 2m einhalten (auch mit dem Schläger).','Ein Freistoß darf immer nur nach vorne, nicht nach hinten gespielt werden.',NULL,NULL,'2#3','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',' ');
