INSERT INTO schiri_test VALUES
    (1,'1','L','Welche Teile gehören zum Einrad?','Ball','Speiche','Sattel','Reifen','Schläger','Tor','2#3#4',NULL,'Einradteile sind im Einrad verbaut.',1,NULL,NULL,NULL,NULL,'nein','Ansgar Pölking
Max: ist mir zu trivial',NULL),
    (2,'8','L','Welches Foul lag im gezeigten Video vor?','nicht-regelkonforme Trikots der Teams.','SUB des gelben Spielers','Hoher Schläger des gelben Spielers.',NULL,NULL,NULL,'3','8.10',NULL,1,NULL,'frage_0002.mp4',NULL,NULL,'nein','Ansgar Pölking',NULL),
    (3,'8','L','Wie entscheidest du als Schiedsrichter im Video richtig?','Da ich nicht absolut sicher sein kann, ob der Spieler vom Schläger getroffen wurde, lasse ich weiterspielen.','Ich kann sicher davon ausgehen, dass der Spieler getroffen wurde und pfeife ab und gebe einen Freistoß.','Ich kann sicher davon ausgehen, dass der Spieler getroffen wurde und Rufe laut Vorteil und lasse den Vorteil ausspielen. Sollte das blaue Team am Ende des Videos den Ball verlieren, pfeife ich das ursprüngliche Vergehen ab.','Ich kann davon ausgehen, das der Spieler getroffen wurde. Aber da die Situation das weitere Spielgeschehen nicht beeinflusst, lasse ich das Spiel weiterlaufen.',NULL,NULL,'3',NULL,'Spieler dürfen nicht vom Schläger getroffen werden. Es ist mit gesunden Menschenverstand davon auszugehen, dass der Spieler getroffen wurde. Da der gefoulte Spieler weiter im Ballbesitz ist, gilt die Vorteilsregel.',1,NULL,'frage_0003.mp4',NULL,NULL,'nein','Ansgar Pölking',NULL),
    (4,'5','LF','Wie entscheidest du als Schiedsrichter auf Grundlage dieses Bildes richtig?','Ich pfeife ab und gebe einen Bully.','Ich gebe einen Freistoß für das rote Team.','Ich gebe einen Freistoß für das blaue Team.','Da der Körperkontakt von beiden hervorgerufen wurde, lass ich das Spiel weiterlaufen.',NULL,NULL,'1',NULL,'Einradhockey ist ein kontaktloser Sport. Damit harte Spiele nicht eskalieren, pfeife ich ab. Ich gebe einen Bully, da ich nicht klar erkennen konnte, wer den Körperkontakt herbeigeführt hatte.',1,'frage_0004.jpg',NULL,NULL,NULL,'nein','Ansgar Pölking',NULL),
    (5,'10','LF','Ein Spieler, der nach 2 min Feldverweis wieder am Spiel teilnimmt, wird sofort wieder durch unsportliches Verhalten auffällig.','Erneuter Feldverweis diesmal für 5 min.','Da er gerade schon eine Strafe hatte, kann er sich jetzt wieder einige Fehlverhalten erlauben bevor er erneut bestraft wird.','Es reicht aus, den Spieler zu bitten, sich besser zu benehmen.','Bei gewalttätigem Verhalten: Spieldauersperre.',NULL,NULL,'1#4','9.5ef',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (6,'11','LF','Der Torwart hält den Ball,  den ein gegnerischer Spieler Richtung Tor schießt. Dabei steht der Torwart in Tornähe mit den Füßen auf dem Boden. Der Ball wäre eventuell ins Tor gegangen. Du bist dir aber nicht 100% sicher. Was entscheidest Du?','6,50 m für die angreifende Mannschaft.','Straftor.','Tor ab.','Weiterspielen, da ich mir nicht sicher bin.',NULL,NULL,'1','9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (7,'8','LF','Der Torwart hält den Ball mit dem Schläger direkt unter der Latte, die Kelle ist dabei über Hüfthöhe. In direkter Tornähe befindet sich kein anderer Spieler.','Weiterspielen, da in direkter Tornähe das Schlägerende bis zur Höhe der Querlatte gehoben werden darf, wenn sich in diesem Bereich kein anderer Spieler befindet.','Hoher Schläger. Es gibt Straftor.','Hoher Schläger. Es gibt 6,50 m.','Hoher Schläger. Es gibt Eckball.',NULL,NULL,'1','8.10',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Hier haben wir eine gewisse Interpreatation drin, da in den Regeln nichts von anderen Spielern in der Nähre steht.   
Max: ja, sehe ich auch so. Besser: und kein anderer Spieler gefährdet wird.
Max: Wenn wir bei der richtigen eine Begründung in der Antwort schreiben sollten wir das auch bei den anderen machen','Christian: Stimmt. Die Sicherheit der Spieler hat Vorrang. Könnte man als Anregung für einen Ergänzung aufnehmen. '),
    (8,'8','LF','Der rote Torwart hält den Ball mit dem Schläger über der Latte. Der Ball wäre nicht ins Tor gegangen.','Hoher Schläger.','Weiterspielen, in Tornähe ist hoher Schläger erlaubt.','6,50m für die blaue Mannschaft.','Es geht weiter mit Ecke für die blaue Mannschaft.',NULL,NULL,'1#4','8.10','Auch in Tornähe darf der Schläger nicht über die Höhe der Querlatte gehoben werden.',1,NULL,NULL,NULL,NULL,'nein','Max: wenn der Ball evtl. wieder vor das Tor gedotzt wäre?','Vorteil möglich? '),
    (9,'11','LF','Ein roter Spieler wird beim Torschuss von einem blauen Spieler gefoult. Der rote Spieler kann den Ball trotzdem schießen bevor er den Boden berührt. Der Ball geht ins Tor.','Der Schiedsrichter erkennt auf Vorteil. Das Tor zählt.','6,50m. Foul bei klarer Torchance. Das Tor zählt nicht, da das Foul vor dem Tor erfolgte.','Freistoß. Tor zählt nicht.','Bully am Mittelkreis',NULL,NULL,'1','9a',NULL,1,NULL,NULL,NULL,NULL,'nein','Max: immer Begründung oder garnicht',NULL),
    (10,'11','LF','Ein roter Spieler wird beim Torschuss von einem blauen Spieler in dessen 6,50-Bereich gefoult. Dieser verhindert jedoch keine klare Torchance.','Ecke für Rot','6,50 m für Rot, da Foul im 6,50m Bereich. ','Weiterspielen','Vorteilentscheidung möglich',NULL,NULL,'1#4','9.2abc',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Ist ein Torschuss im 6,5 Bereich nicht immer eine klare Torchance? Rolf: Wenn der Stürmer in der Nähe des Eckpunktes gefoult wurde, hatte er vermutlich keine gute Torchance.','Christian: Ich finde den Einwand von Larissa berechtigt. Es ist sicher nicht 100% abzugrenzen was ein Torschuß ist und was ein "fester Schuß vors Tor." Dahinter steht die Frage wie eine "direkte Torchance" zu interpretieren ist? Jeder Schuß aufs Tor ist eine direkte Torchance? Hier hat die Regel eine große Schwäche. Die Frage finde ich so zu unklar. @Rolf: Auch aus spitzen Winkel kann man ins Tor schießen. Hinter der Torlinie ist es kaum ein Torschuß. '),
    (11,'11','L','Ein roter Spieler wird beim Torschuss kurz hinter der Mittellinie von einem blauen Spieler gefoult. Dieser verhindert dadurch eine klare (direkte) Torchance.','6,50m für Rot.','6,50m nur dann, wenn das Foul in der Torzone erfolgt.','Freistoß für Rot.','6,50 m nur dann, wenn kein Spieler mehr zwischen gefoultem Angreifer und Torwart stand. Sonst Freistoß.',NULL,NULL,'1','9.2abc',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,'Christian: Siehe Frage oben. '),
    (12,'7','LF','Ein roter Spieler schießt auf das Tor. Der Ball wird jedoch von einem auf dem Boden liegenden Einrad der blauen Mannschaft direkt vor dem Tor, in dem der Torwart steht, abgefangen.','6,50 für Rot. Da der Ball direkt auf das Tor gegangen wäre, der Torwart jedoch noch die Chance gehabt hätte ihn zu halten.','Straftor da der Ball wahrscheinlich ins Tor gegangen wäre.','Bully','Eckball',NULL,NULL,'1','9.3','In diesem Fall hat das auf dem Boden liegende Einrad kein sicheres Tor verhindert, da der Torwart den Ball noch hätte halten können. Verhindert wurde aber eine klare Torchance, daher 6,50m.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (13,'7','L','Ein roter Spieler schießt in Richtung blaues Tor. Der Ball wird jedoch von einem, neben dem Tor am Boden liegenden Einrad der blauen Mannschaft, abgefangen. Der Ball hätte das Tor sicher nicht getroffen.','Hindernis. Da der Ball auf keinen Falls ins Tor gegangen wäre gibt es Eckball für die rote Mannschaft.','Hindernis. Da das am Boden liegende Einrad einen Torschuss gestoppt hat gibt es 6,50m für die rote Mannschaft.','Weiterspielen, da der Ball nicht ins Tor gegangen wäre.','Vorteil Entscheidung ist möglich.',NULL,NULL,'1#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (14,'7','LF','Nach einem Torschuss eines roten Spielers prallt der Ball gegen ein auf dem Boden liegendes Einrad der gleichen Mannschaft. Das blaue Team kommt daraufhin an den Ball.','Hindernis.','Trotz Hindernis kann der Schiedsrichter das Spiel laufen lassen. Er entscheidet auf Vorteil für das blaue Team.','Wenn der Ball ins Tor gegangen wäre, gibt es 6,50 m.','Wenn der Ball sicher ins Tor gegangen wäre, gibt es Straftor für Blau.',NULL,NULL,'2','9a',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: für mich ist das situationsabhängig, ob das ein Vorteil ist. Kann der blaue Spieler direkt einen Konter fahren, dann ja. Nimmt ihm ein roter Spieler direkt wieder den Ball ab und macht ein Tor, dann nein.','Christian: Habs umformuliert'),
    (15,'7','L','Nach einem Torschuss eines roten Spielers prallt der Ball gegen ein vor dem Tor liegendes Einrad der gleichen Mannschaft. Ein roter Spieler kommt danach in Ballbesitz und schießt ein Tor.','Hindernis. Tor ab für die blaue Mannschaft.','6,50m. Da das am Boden liegende Einrad eine Torchance verhindert hat.','Straftor, da der Ball evtl. ins Tor gegangen wäre.','Das Tor zählt nicht.',NULL,NULL,'1#4',NULL,'Da das auf dem Boden liegende Einrad von der gleichen roten Mannschaft ist wie der Torschütze gibt es hier keinen Vorteil!',1,NULL,NULL,NULL,NULL,'nein','Larissa: Ich finde die Regel hier etwas uneindeutig, da sie nur von "Hindernis für das gegnerische Team" spricht.',NULL),
    (16,'8','L','Ein roter Spieler hat bei einem Torschuss außerhalb des gegnerischen 6,50-Bereichs einen hohen Schläger.','Weiterspielen. Beim Torschuss ist hoher Schläger erlaubt.','Hoher Schläger. Freistoß für die blaue Mannschaft am Ort des Schusses.','Hoher Schläger. Tor ab.','Falls der Ball ins Tor geht, zählt das Tor: Vorteil',NULL,NULL,'2','8.10#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (17,'8','L','Ein roter Spieler hat bei einem Torschuss innerhalb des gegnerischen 6,50-Bereichs einen hohen Schläger.','Hoher Schläger. Torab.','Beim Torschuss ist hoher Schläger erlaubt. Daher weiterspielen.','Hoher Schläger. Freistoß am Ort des Schusses.','Falls der Ball ins Tor geht, zählt das Tor. Vorteil','Geht der Balls ins Tor, zählt das Tor nicht.',NULL,'1#5','8.10#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (18,'11','L','Eine rote Spielerin will einen Freistoß (auch Torab oder Ecke) ausführen. Nach dem Anpfiff berührt sie den Ball zunächst leicht und schießt ihn mit einer zweiten Berührung zu ihrem Mitspieler.','Freistoß für das blaue Team.','Weiterspielen, dies ist erlaubt.','Passiert dies bei einem Tor ab, gibt es Eckball für die blaue Mannschaft.','Wenn die erste Berührung und sehr leicht war, kann man hier weiterspielen lassen.',NULL,NULL,'1#3','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: Wirklich immer nur Ecke, wenn das bei einem Torab passiert? Was ist, wenn ein gegnerischer Stürmer nach der 1. Berührung eine gute Torchance hat? Ist dann auch 6.50m möglich?','@rolf (Christian). Interessanter Aspekt. In den Regeln nicht geklärt. Da der Stürmer ja noch nicht am Ball gewesen sein kann (sonst keine doppelte Berührung) würde ich aber keine klare Torchance zuerkennen. '),
    (19,'8','LF','Ein roter Spieler hält den Schläger ohne das Ende abzudecken, während er nicht in der Nähe des Spielgeschehens ist.','Weiterspielen, bei nächster Unterbrechung Spieler darauf hinweisen und gegebenenfalls verwarnen.','Spiel sofort unterbrechen. Freistoß für den Gegner.','Dies ist erlaubt, sofern es nicht in der Nähe des Balles passiert.','2 min Zeitstrafe',NULL,NULL,'1','8.9','Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (20,'8','LF','Ein Spieler hält den Schläger ohne das Ende abzudecken, während er im Spielgeschehen ist und um den Ball kämpft. Was tust du?','Dies ist ein gefährliches Spiel. Ich unterbreche und gebe Freistoß für das andere Team.','Nächste Spielunterbrechung abwarten. Spieler dann ermahnen.','Automatischer 6,50m.','Vorteil möglich. Bei Vorteil den Spieler bei der nächsten Unterbrechung verwarnen.',NULL,NULL,'1#4','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (21,'11','L','Der blaue Torwart hält einen Schuss aufs Tor, während er sich am Tor festhält. Der Ball wäre mit Sicherheit ins Tor gegangen.','Straftor für das rote Team.','6,50m für das rote Team.','Tor ab.','Das Spiel läuft weiter. Der Torwart darf sich am Tor festhalten.',NULL,NULL,'1','5.3#9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (22,'7','L','Der blaue Torwart spielt den Ball neben dem Tor, während er sich am Tor festhält.','Ecke für Rot','Straftor','Bully','Weiterspielen. Solange er kein Tor verhindert, darf er sich am Tor festhalten.',NULL,NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (23,'8','LF','Ein roter Spieler hebt im Kampf um den Ball den Schläger über Hüfthöhe. Der blaue Spieler bleibt in Ballbesitz. Was tust Du?','Ich rufe "Vorteil" und lasse das Spiel laufen. Bei nächster Unterbrechung weise ich den Spieler auf das Foul hin.','Hoher Schläger. Ich unterbreche das Spiel sofort, Freistoß für Blau.','Bei Anfängern kann dies akzeptiert werden.','Im Kampf um den Ball ist hoher Schläger erlaubt. Weiterspielen.',NULL,NULL,'1','8.10#9a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (24,'9','L','Der Torwart berührt einen gegnerischen Fernschuss, bevor er ins Tor geht.','Das Tor zählt nicht, da Fernschuss.','Das Tor zählt.','Bully an der Mittellinie.','Es geht weiter mit Torab.',NULL,NULL,'2','7.2','Wird ein Fernschuss in der gegnerischen Hälfte abgefälscht, zählt dies nicht mehr als Fernschuss. Von welcher Mannschaft er dabei berührt wird, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (25,'8','LF','Ein roter Spieler wird gesubt, sein Einrad fällt auf den Ball. Der Ball ist nicht mehr spielbar.','Es gibt Freistoß für das rote Team. Nur der erste Verstoß (hier: Foul) wird bestraft.','Hier sind zwei Verstöße gleichzeitig. Es gibt Bully.','Der Schiedsrichter entscheidet, was wichtiger ist.','Die beiden Regelverstöße heben sich auf: weiterspielen.',NULL,NULL,'1','10.6d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (26,'6','LF','In einem Zweikampf schlagen sich zwei Gegner, die vorher noch nicht negativ aufgefallen sind, übertrieben hart auf die Schläger.','Spielunterbrechung. Beide Spieler verwarnen.','Weiterspielen.','Zeitstrafen für beide Spieler.','Weiter mit Bully.',NULL,NULL,'1#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (27,'6','LF','Ein Spieler schlägt im eigenen 6,50-Bereich im Zweikampf den Schläger seines Gegners so hoch, dass dieser über Hüfthöhe ist.','Übertriebene Härte. Ecke.','Weiterspielen. Hochschlagen des Schlägers ist erlaubt.','Torab.','Übertriebene Härte. Freistoß am Ort des Fouls.',NULL,NULL,'1','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (28,'10','L','Ein Spieler beleidigt einen Schiedsrichter.','Feldverweis für min. 5 min.','Weiterspielen, Ermahnung bei der nächsten Spielunterbrechung.','Freistoß für den Gegner.','Das muss der Schiedsrichter aushalten können, wenn er vorher eine Fehlentscheidung getroffen hat.',NULL,NULL,'1#3','8.5','Das Strafmaß für eine Beleidigung ist in den Regeln nicht explizit aufgeführt. Da es sich um eine grobe Unsportlichkeit handelt, sind im Ermessen der Schiedsrichter 5 min angemessen. ',1,NULL,NULL,NULL,NULL,'nein','Rolf: Da die 5 min hierfür nicht in den Regeln stehen, sollten wir das auch nicht in die Prüfung nehmen.','@rolf Anregunge für die Regelkommission? "Ein Spieler darf den Schiedsrichter nicht Beleidigen" (8.5) Ok. Aber eine Konsequenz ist nirgens formuliert??'),
    (29,'10','LF','Ein roter Spieler beleidigt einen blauen Spieler. Du hast es gehört. Was tust du?','Feldverweis für 5 Minuten.','Weiterspielen, bei nächster Unterbrechung ermahnen.','Es geht weiter mit Freistoß für das blaue Team.','Das sollen sie selber aushandeln, da hält sich der Schiedsrichter raus.',NULL,NULL,'1#3','8.5','Das Strafmaß für eine Beleidigung ist in den Regeln nicht explizit aufgeführt. Da es sich um eine grobe Unsportlichkeit handelt, sind im Ermessen der Schiedsrichter 5 min angemessen. ',1,NULL,NULL,NULL,NULL,'nein','Rolf: Da die 5 min hierfür nicht in den Regeln stehen, sollten wir das auch nicht in die Prüfung nehmen.',NULL),
    (30,'8','LF','Eine rote Spielerin bekommt den Ball an die Brust und schlägt ihn sofort mit der Hand weg. Was entscheidest Du?','Unerlaubte doppelte Körperberührung. Freistoß für Blau.','Kein Foul, trotz zweimaligem Körperkontakt. Zweimaliger Körperkontakt ist zulässig, wenn ein Kontakt (Brust) passiv war.','Gefährliches Spiel. Freistoß für Blau.','Vorteil Entscheidung möglich.',NULL,NULL,'2','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (31,'10','LF','Der rote ballführende Spieler wird von einer blauen Spielerin verfolgt. Der rote Ballführende drängt die blaue Spielerin an die Bande ab. Die blaue Spielerin prallt gegen die Bande.','Weiterspielen. Der Ballführende hat Vorfahrt.','Freistoß für die blaue Spielerin. Kein Spieler darf durch Erzwingen der Vorfahrt eine/n andere/n Spieler/in gefährden.','Wegen gefährlichem Spiel bekommt der rote Spieler eine 2 min Zeitstrafe.','Geschieht dies mit Absicht, bekommt der rote Spieler eine 5 min Feldverweis.',NULL,NULL,'2#4','8.2b#9.5de',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: hier ist wieder die Frage, wann ein Foul gefährlich ist. Dann wäre 3 auch richitg. 5 min wäre nur bei Absichtliches UND gefährliches Foul','Christian:  Richtig. 3 ist auch richtig. Auch hier könnte die Regel eindeutiger sein, dass "Absichtliches, gefährliches Foul" absichtliches UND gefährliches Foul bedeutet. '),
    (32,'1','LF','Ein Spieler schießt aufs Tor. Während der Ball in der Luft aber noch nicht im Tor ist, ertönt der Schlusspfiff.','Das Tor zählt, da der Schuss noch vor dem Schlusspfiff abgegeben wurde.','Kein Tor, da der Ball bei Spielende nicht im Tor war.','Wenn der Ball den Schläger beim Schlusspfiff verlassen hat, zählt das Tor.','Wenn alle der Meinung sind, dass das Tor zählen sollte, kann der Schiedsrichter es gelten lassen.','Entscheidend ist der Moment in dem das Schlusssignal beginnt (die Zeit abgelaufen ist).',NULL,'2#5',NULL,'Da die Regeln nichts anderes festlegen, endet das Spiel sofort, wenn die Zeit abgelaufen ist. Ist der Ball in diesem Moment nicht im Tor, zählt ein Tor nicht mehr.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (33,'6','LF','Ein roter Spieler ist nicht in Ballnähe. Ein blauer Spieler blockiert seinen Schläger, so dass der Spieler am Weiterfahren gehindert wird.','Der Schiedsrichter entscheidet auf Foul und unterbricht das Spiel sofern kein Vorteil vorliegt. Freistoß Rot.','Ermahnung des blauen Spielers wegen übertriebener Härte. Bei Vorteil: Ermahnung bei der nächsten Unterbrechung.','Weiterspielen. Blockieren des Schlägers ist erlaubt.','Der Freistoß wird am Ort des Fouls ausgeführt.','Der Freistoß da ausgeführt wo der Ball zum Zeitpunkt des Fouls war.',NULL,'1#2#4','8.1#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: ich hätte aus der Fragestellung keine übertriebene Härte gelesen','Christian: Aus 8.1. geht das meiner Meinung nach hervor. Wenn man "blockiert HART" schreiben würde, wäre es eindeutig. '),
    (34,'8','L','Nach einem Schuss aufs Tor steckt der Ball im Rad des blauen Torwarts. Wie entscheidest Du?','Es gibt Straftor für Rot.','Hängt davon ab wer geschossen hat. Torabstoß oder Eckball.','Ball im Rad bedeutet immer Freistoß für das andere Team. Da hier innerhalb des 6,5 m Bereich: Eckball für Rot.','Es gibt 6,50 m',NULL,NULL,'3','5.8',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (35,'8','L','Rot schießt auf das blaue Tor. Der Ball bleibt bei einem eigenen roten Mitspieler, der direkt vor dem Tor steht, im Rad stecken. Der Torwart steht noch dahinter.','Wenn der Ball ins Tor gegangen wäre gibt es Straftor – sonst Ecke.','Ball im Rad bedeutet immer Freistoß für das andere Team. Da innerhalb des 6,5 m Bereichs in diesem Fall: Torabstoß für Blau.','Bully am Eckpunkt.','6,50 m für Rot.',NULL,NULL,'2','5.8','Merke: Ball im Rad bedeutet immer Freistoß für das andere Team. Wer geschossen hat spielt keine Rolle!',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (36,'8','L','Mehrere Spieler sind ohne erkennbares Foul in der Torzone gestürzt. Der Ball liegt in der Mitte und ist nicht mehr spielbar.','Das Spiel geht weiter.','Spielunterbrechung durch den Schiedsrichter. Es gibt Bully am Eckpunkt.','Torabstoß.','Wenn mehrere Spieler gestürzt sind, wird das Spiel grundsätzlich unterbrochen.',NULL,NULL,'2','9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (37,'8','L','Mehrere Spieler sind ohne erkennbares Foul gestürzt. Der Ball liegt direkt neben den gestürzten Spielern. Was nun?','Das Spiel geht weiter solange der Ball spielbar ist und keine Verletzungen vorliegen.','Spielunterbrechung durch den Schiedsrichter. Es gibt Bully.','Wenn mehr als zwei Spieler vom Einrad abgestiegen sind, wird das Spiel immer unterbrochen.','Wenn mehr als vier Spieler vom Einrad absteigen, wird das Spiel grundsätzlich unterbrochen.',NULL,NULL,'1',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (38,'1','L','Nach einem Tor von Blau fährt die ballführende rote Mannschaft mit dem Ball über die Mittellinie. Die Torschützin der blauen Mannschaft befindet sich noch nicht wieder in der eigenen Hälfte.','Das Spiel geht weiter. Vorteil für die ballführende rote Mannschaft.','Der Schiedsrichter unterbricht das Spiel mit mehrfachem lauten Pfeifen. Das Spiel wird erst wieder aufgenommen wenn alle Spieler in ihrer Hälfte sind und der Schiedsrichter das Spiel frei gibt.','Der Schiedsrichter pfeift einmal um das Spiel zu unterbrechen. Hören die Spieler dies nicht und niemand beschwert sich, geht das Spiel weiter.','Sofern kein Nachteil entsteht, kann das Spiel weiterlaufen.',NULL,NULL,'2','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (39,'1','L','Nach einem Tor sind alle Spieler wieder in ihrer Spielhälfte. Der Schiedsrichter hat das Spiel noch nicht angepfiffen. Der ballführende Spieler fährt über die Mittellinie.','Da alle Spieler bereit waren kann das Spiel weiter gehen auch ohne Anpfiff.','Der Schiedsrichter pfeift mehrmals und unterbricht das Spiel. Alle Spieler müssen zurück in die eigene Hälfte und das Spiel wird neu eröffnet mit einem Anpfiff.','Wenn sich niemand beschwert kann das Spiel weiter gehen. Andernfalls wird es unterbrochen.','Der Schiedsrichter versucht diese Situation zu verhindern in dem er das Spiel zügig anpfeift nachdem alle Spieler in ihrer Hälfte und spielbereit sind.',NULL,NULL,'2#4','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (40,'1','L','Worauf musst du achten, wenn ein Tor gefallen ist und die Spieler auf dem Weg zurück in die eigene Hälfte sind?','Ich pfeife das Spiel  an, sobald alle Spieler in ihrer eigenen Hälfte und spielbereit sind, damit das Spiel zügig weitergehen kann.','Ich achte darauf, dass es zu keiner übermäßigen und unsportlichen Spielverzögerung kommt.','Nach jedem Tor warte ich eine Pause von etwa 20 Sekunden ab.',NULL,NULL,NULL,'1#2','5.6',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (41,'10','LF','Nach einem Führungstor kurz vor Spielschluss fahren der rote Torschütze oder seine Mitspieler sehr langsam zurück in die eigene Hälfte, offensichtlich um das Spiel zu verzögern. Wie entscheidest Du?','Das ist eine erlaubte Taktik.','Der Schiedsrichter unterbricht das Spiel und hält die Zeit an.','Es geht weiter mit Freistoß für Blau am Mittelpunkt.','Der Schiedsrichter gibt dem entsprechenden Spieler eine 2 min Zeitstrafe wegen Spielverzögerung.','Es geht weiter mit "Spielaufnahme nach Tor".',NULL,'2#3#4','9.5d#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Frage ist richtig, aber über den Satz "Wenn eine Strafe für Zeitspiel verhängt wird, wird der Freistoß am Mittelpunkt ausgeführt." sollten wir mal sprechen. Es kann ja auch Zeitstrafen geben,  wo das andere Team schon im 6,5 Raum ist. Da wäre freistoß am Mittelpunkt ein Nachteil.','Christian: Das kann ich mir nicht so richtig vorstellen. Wenn die angreifende Mannschaft im Ballbesitz ist, kann die verteidigende Mannschaft schlecht Zeitspiel machen. Das geht ja nur bei ruhendem Ball. '),
    (42,'1','LF','Ein roter Spieler liegt seit einiger Zeit verletzt am Boden, ohne das ein Foulspiel vorlag. Die blaue Mannschaft ist in Ballbesitz.','Ich warte, bis es eine Spielunterbrechung (z.B. Ausball gibt). Dann unterbreche ich und kümmere mich um den verletzten Spieler.','Ich unterbreche das Spiel sofort und halte die Zeit an.','Ich warte, bis der Ballbesitz gewechselt hat, dann unterbreche ich das Spiel.','Nach der Unterbrechung gibt es Freistoß für die zuletzt in Ballbesitz befindliche blaue Mannschaft.','Der Freistoß wird da ausgeführt, wo sich der Ball bei der Spielunterbrechung befand.',NULL,'2#4#5','10.6h',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (43,'2','L','Du hast ein Foul von Blau gesehen und pfeifst...dann merkst du, dass es einen Vorteil für die rote Mannschaft gibt und rufst "weiter".','Das Spiel geht weiter, da ich auf Vorteil erkannt habe.','Ich unterbreche das Spiel mit mehrfachem lauten Pfeifen. Die Foulentscheidung ist getroffen, entsprechend gibt es Freistoß.','Ich kann mich entscheiden, ob ich weiter spielen lasse oder doch das Spiel unterbreche.','Der Pfiff hat das Spiel in jedem Fall unterbrochen. Weiterspielen nach dem Pfiff ist nicht möglich.',NULL,NULL,'2#4','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (44,'2','L','Du pfeifst ein Foul, erkennst dann aber, dass du dich geirrt hast und lässt das Spiel weiterlaufen, da die meisten Spieler eh nicht auf den Pfiff reagiert haben.','Das Spiel geht weiter, da ich doch kein Foul pfeifen möchte.','Ich unterbreche das Spiel mit mehrfachem, lauten Pfeifen. Der Pfiff hat das Spiel unterbrochen. Ich entschuldige mich für meinen Fehler und gebe Freistoß für die Mannschaft im Ballbesitz oder Bully.','Tore, die kurz nach dem Pfiff gefallen sind, zählen.','Nur wenn alle weiterspielen, kann das Spiel weiter gehen ohne Unterbrechung.',NULL,NULL,'2','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (45,'8','L','Von der angreifenden roten Mannschaft springt der Ball im Mittelfeld ins Aus. Von einer Wand im Seitenaus prallt er wieder zurück ins Spielfeld. Die verteidigende Mannschaft ist nun in Ballbesitz. Das Spiel läuft weiter.','Der Ball war im Aus. Es gibt Seitenaus für Blau.','Ich erkenne auf Vorteil und lasse das Spiel laufen.','Wenn der Ball aus dem Aus sofort ins Spielfeld zurückspringt geht das Spiel grundsätzlich weiter.','Ich unterbreche das Spiel energisch (evtl. mit mehreren Pfiffen), um das Weiterspielen zu unterbrechen.',NULL,NULL,'1#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (46,'8','L','Der angreifenden roten Mannschaft springt der Ball im gegnerischen 6,50 m Bereich ins Aus. Von einer Wand (über der schrägen Bank in der Ecke) prallt er wieder zurück ins Spielfeld. Die angreifende Mannschaft kommt sofort wieder in Ballbesitz.','Der Ball war im Aus. Es gibt Torab.','Ich erkenne auf Vorteil und lasse das Spiel laufen.','Wenn der Ball aus dem Aus sofort ins Spielfeld zurückspringt geht das Spiel grundsätzlich weiter.','Bei Ausbällen gibt es keinen Vorteil.',NULL,NULL,'1#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (47,'8','L','Der Ball springt ins Aus. Keiner der Schiedsrichter konnte erkennen, welche Mannschaft zuletzt am Ball war.','Die Schiedsrichter fragen die beteiligten Spieler. Gibt es keine sofortige Einigung, entscheidet der Schiedsrichter auf Bully.','Die Schiedsrichter entscheiden nach eigenem Ermessen ohne die Spieler zu fragen.','Im Zweifel wird für die angreifende Mannschaft entschieden.','Können die beteiligten Spieler sich nicht einigen, wartet der Schiedsrichter solange bis eine Einigung erfolgt ist.',NULL,NULL,'1','10.6b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (48,'9','L','Ein Spieler schießt von vor der Mittellinie (er befindet sich in der eigenen Spielhälfte) ein Tor. Der Ball wird von keinem Spieler mehr berührt.','Kein Tor. Fernschuss.','Es geht weiter mit Torabstoß für die andere Mannschaft.','Es geht weiter mit Bully am Mittelpunkt.','Das Tor zählt, der Ball ist ja im Tor.',NULL,NULL,'1#2','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (49,'9','L','Ein Spieler setzt zum Torschuss an. Worauf muss Du in dieser Situation achten?','Ob der Schuss vor oder hinter der Mittellinie abgegeben wurde (möglicher Fernschuss)?','Ob der Schläger beim Ausholen oder Ausschwingen über die Hüfte gehalten wird (Hoher Schläger)?','Falls es ein Fernschuss ist: Wird der Ball abgefälscht?','Hat der Spieler eine gute Schusstechnik?',NULL,NULL,'1#2#3',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (50,'4','LF','Ein Finalspiel (nur beim 8er Turnier) endet unentschieden. Wie geht es weiter?','Es gibt zwei Sieger.','Sofortiges Penaltyschießen.','5 min Verlängerung ohne golden Goal. Anschließend Penaltyschießen. falls weiterhin unentschieden.','10 min Verlängerung ohne golden Goal. Anschließend Penaltyschießen. falls weiter unentschieden.',NULL,NULL,'3','5.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (51,'8','LF','Ein Spieler schlägt den Ball mit der Hand weg. Der Ball fliegt nach oben und trifft ihn direkt wieder auf der Schulter (ohne dass der Ball zwischenzeitlich vom Gegner oder vom eigenen Schläger berührt wird). Danach schlägt der Spieler den Ball mit dem Schläger weg. Was entscheidest Du?','Doppelte Körperberührung. Freistoß für die gegnerische Mannschaft.','Kein Regelverstoß trotz doppelter Körperberührung, da eine Berührung passiv war (Berührung an der Schulter). Das Spiel läuft weiter.','Bully am Eckpunkt.','Bei Berührungen mit dem Körper wird das Spiel grundsätzlich unterbrochen.',NULL,NULL,'2','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (52,'7','LF','Wann darf ein Spieler den Ball mit Schläger, Rad oder Körper spielen?','Er sitzt auf dem Einrad und berührt weder Wand noch Tor.','Er fällt von dem Einrad und berührt mit einem Fuß den Boden, mit dem anderen Fuß aber noch die Pedale.','Er steigt auf das Einrad und berührt mit einem Fuß die Pedale, mit dem anderen Fuß aber noch den Boden.','Er sitzt auf dem Einrad und lehnt am Tor.','Während er vom Boden auf das Einrad springt und den Boden verlassen hat, aber noch nicht mit den Füßen auf den Pedalen ist.',NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (53,'10','LF','Ein roter Spieler verletzt einen (blauen) Gegenspieler, Du unterstellst Ihm Absicht. Welche Möglichkeiten hast Du?','Feldverweis für mindestens 5 min oder eine Spielsperre für den Rest des Spieles muss für den roten Spieler ausgesprochen werden. Die rote Mannschaft spielt zu viert weiter.','Feldverweis für mindestens 5 min oder eine Spielsperre für den Rest des Spieles muss für den roten Spieler ausgesprochen werden. Die rote Mannschaft darf einen Ersatzspieler stellen.','Feldverweis für mindestens 2 min oder eine Spielsperre für den Rest des Spieles muss für den roten Spieler ausgesprochen werden. Die rote Mannschaft spielt zu viert weiter.','Ermahnung des roten Spielers und Androhung von Zeitstrafen. Freistoß für Blau.',NULL,NULL,'3','9.5','8.6.6 Absichtliche Fouls<BR> Absichtliche Fouls gelten als Unsportlichkeit und werden mit einem Feldverweis von mindestens 2 Minuten geahndet. <BR> <BR> Ligamodus: 5.8) Feldverweis (IUF-Regel 8.4.5)<BR> Die Einradhockeyregeln der IUF sehen Feldverweise von unterschiedlicher Dauer bei groben Regelverstößen und Unsportlichkeiten vor. Das Aussprechen von Feldverweise liegt im Ermessen des Schiedsrichters. Die folgende Aufzählung dient als Entscheidungshilfe für den Schiedsrichter:<BR> <BR>     2-Minuten-Feldverweis:<BR>         Unsportlichkeiten wie z.B. Wegschlagen am Boden liegender Einräder oder Schläger des Gegners<BR>         Wiederholte übertriebene Härte (z.B. ausgefahrene Ellenbogen, Schlägerangriff)<BR>         Wiederholtes Foulspiel (nach vorangegangener Ermahnung)<BR>         Wiederholter Schlägerangriff ohne das der Spielball in der Nähe ist<BR>         Schlägerwurf<BR>         (absichtlicher) SUB von hinten<BR>         Täuschung des Schiedsrichters (Schwalbe)<BR> <BR>     5-Minuten-Feldverweis:<BR>         Beleidigungen gegen Mit- oder Gegenspieler oder den Schiedsrichter<BR>         Tätlichkeit (z.B. Wegstoßen eines Spielers, wenn beide auf dem Boden stehen)<BR>         Wiederholte Vergehen, die bereits zu einer 2-Minuten-Zeitstrafen geführt haben<BR> <BR>     Feldverweis für die restliche Spieldauer (Spielsperre):<BR>         grobe Tätlichkeit<BR>         grobe Unsportlichkeit',1,NULL,NULL,NULL,NULL,'nein','Larissa: wenn ich nur die Infos aus der Frage habe, wäre das für mich nur 2min. Es ist nicht die Rede von Gefährlich oder wiederholt (dann 5min) oder gewalttätig (dann Spielsperre) Rolf: Für mich ist Verletzung gleichbedeutend mit "gefährlich". Also 5 min.','@Christian. Ich finde schon. Eine passierte Verletzung setzt ja eine Gefahr vorraus. Also die Gefahr ist zur Realität geworden. gefährlich UND absichtlich = 5 min. '),
    (54,'6','LF','Ein blauer Spieler dreht den Schläger eines roten Spielers mit nach unten gedrehter Kelle weg. Ist das erlaubt?','Nein, aber wenn kein Ball in der Nähe ist, wird der Verstoß nicht geahndet.','Nein, Hakeln ist verboten, Freistoß für rot.','Ja, wenn ein Ball in unmittelbare Nähe ist, ist das Schlägerhakeln erlaubt.','Ja, wenn der Spielball nicht in der Nähe ist, darf das ohne übertriebene Härte gemacht werden.',NULL,NULL,'2','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: gilt hier nicht auch "Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden." und damit 1?','Christian: Die Frage ist wirklich uneindeutig. Die Regeln sind echt nicht gut in diesem Bereich. Punkt 9 "Bei jeder Regelwidrigkeit muss der Schiedsrichter eine Strafe verhängen oder Vorteil geben." und Punkt: 8.1 "Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden." stehen in Konkurrenz. Insbesondere bei gefährlichem Spiel problematisch. Da der Spieler am Weiterfahren gehindert wird, würde ich von einem Einfluss aufs Spiel ausgehen. '),
    (55,'8','LF','Ein roter Spieler wird von einem blauen Spieler “ge- SUBt, das Sub beeinträchtigt aber nicht seine Fahrweise. Wie entscheidest Du?','Ein SUB ist immer ein Fehlverhalten und muss bestraft werden. Ich unterbreche das Spiel und gebe einen Freistoß für Rot.','Ich mache das Zeichen für "Sub" und rufe laut "Vorteil", sollte sich kein Vorteil ergeben, kann ich zeitnah nachträglich auf Freistoß entscheiden.','Ich warte den laufenden Angriff von roten Team ab, sollte kein Tor erzielt werden, pfeife ich ab und gebe einen Freistoß für Rot.','Ich tue so, als ob ich es nicht gesehen habe und mache gar nichts.',NULL,NULL,'2','8.3#10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: Warum ist Antwort 3 falsch?','Christian: Ich glaube sowohl Antwort 2 als auch 3 sind falsch: Punkt 8.1. "Um den Spielfluss zu erhalten, sollten Regelwidrigkeiten, die das Spielgeschehen nicht beeinflussen, nicht geahndet werden." Wenn das Sub also wirklich seine Fahrweise nicht beeinträchtigt, war es auch KEIN Foul? Also auch kein Vorteil sondern weiterspielen. '),
    (56,'1','L','Wie viele Spieler gehören mindestens pro Mannschaft auf das Spielfeld, wenn das Spiel beginnt?','5 Spieler','4 Spieler und ein Torwart','3 Spieler','4 Spieler','1 Spieler',NULL,'3','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (57,'1','L','Wie viele Spieler gehören normalerweise pro Mannschaft auf das Spielfeld?','5 Spieler','4 Spieler','3 Spieler','6 Spieler',NULL,NULL,'1','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (58,'10','LF','Ein roter Spieler wirft absichtlich seinen Schläger. <BR> Welche Konsequenzen hat das für den Spieler und sein Team?','Dies ist als gefährliches Spiel zu werten und mit einer 2 min Zeitstrafe zu ahnden.','Wenn dabei ein Spieler zusätzlich konkret gefährdet wird, ist es mit 5 min Feldverweis zu bestrafen.','6,50m für das blaue Team und mindestens 5 min Feldverweis für den roten Spieler.','Straftor für das blaue Team und mindestens 5 min Feldverweis für den roten Spieler.',NULL,NULL,'1#2','8.8#9.5de',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Antwort 1 ist so für mich nicht richtig, da Absicht schon in der Frage steht. Wenn dann auch noch gefährlich, wären es 5min. Rolf: Ich finde, das Wort "gefährlich" muss aus Antwort 1 entfernt werden. Denn dann wären es ja 5 min.','Christian: Wenn es Absichtlich (seht in der Frage) UND Gefährlich (beim Schlägerwurf immer zu unterstellen?) ist, sollte es immer 5 min. geben. Frage ist nicht eindeutig'),
    (59,'10','LF','Ein roter Spieler ist allein auf dem Weg zum Tor. Von hinten wirft ein gegnerischer (blauer) Spieler seinen Schläger. Dies hat aber keine Auswirkung auf den roten Spieler. Wie entscheidest Du?','Ich unterbreche das Spiel, erteile den blauen Spieler einen Feldverweis von 2 Minuten und gebe dem roten Team einen Freistoß.','Ich unterbreche das Spiel, erteile den blauen Spieler einen Feldverweis von 5 Minuten und gebe dem roten Team einen 6,50m.','Ich unterbreche nicht das Spiel, lasse Vorteil gelten, nach Ballverlust / Torschuss erteile ich dem blauen Spieler einen Feldverweis von 2 Minuten.','Ich unterbreche das Spiel, erteile den blauen Spieler einen Feldverweis von 2 Minuten und gebe dem roten Team einen 6,50 m.',NULL,NULL,'3','8.8#10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (60,'10','LF','Ein blauer Spieler fährt hinter dem ballführenden roten Spieler her. Er hat keine Chance an den Ball zu kommen und foult den roten Spieler. Der rote Spieler war in einer aussichtsreichen Situation ein Tor zu erzielen (direkte Schussbahn zum Tor). Wie handelst Du ?','Ich ermahne den blauen Spieler und gebe dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen 6,50m.','Ich ermahne den blauen Spieler und gebe dem roten Team einen 6,50 m.',NULL,NULL,'3','9.5d#9.2a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (61,'10','LF','Ein blauer Spieler fährt hinter der ballführenden roten Spielerin her. Er hat keine Chance an den Ball zu kommen und foult die rote Spielerin mit Absicht. Zwischen der roten Spielerin und dem blauen Tor standen noch zwei blaue Spieler.  Wie handelst Du ?','Ich ermahne den blauen Spieler und gebe dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen Freistoß.','Ich gebe dem blauen Spieler einen 2 min Feldverweis und dem roten Team einen 6,50m.','Ich gebe dem roten Team einen Freistoß.',NULL,NULL,'2','9.5d#9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (62,'5','LF','Ein blauer Spieler steht/pendelt und wird vom ballführenden roten Spieler umgefahren. Wer hat Vorfahrt, bzw. bekommt einen Freistoß?','Weil beide Spieler umsichtig spielen und sich ausweichen müssen, gibt es in diesem Falle einen Bully.','In diesem Falle hat der rote Spieler Vorfahrt, da er den Ball führt. Alle Spieler müssen Ihm Platz machen. Freistoß für das rote Team.','In diesem Fall hat der ballführende rote Spieler keine Vorfahrt, da der blaue Spieler steht /pendelt. Es gibt einen Freistoß für das blaue Team.','Das ist eine grobe Unsportlichkeit, einen  Feldverweis (2 min)  für den roten Spieler und einen Freistoß für das blaue Team.',NULL,NULL,'3','8.2c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (63,'5','LF','Zwei Spieler fahren parallel nebeneinander her. Sie sind auf gleicher Höhe. Der rote Spieler (ballführend) drängt den blauen Spieler (ohne Ball) ab.  Wer hat Vorfahrt, bzw. bekommt einen Freistoß?','Beide Spieler müssen umsichtig Spielen und sich ausweichen. In diesem Falle gibt es einen Bully.','In diesem Falle hat der rote Spieler Vorfahrt, und das Spiel geht ohne Unterbrechung weiter.','In diesem Fall hat der blaue Spieler Vorfahrt und es gibt einen Freistoß für das blaue Team.','Das ist eine grobe Unsportlichkeit, einen  Feldverweis (2 min)  für den roten Spieler und einen Freistoß für das blaue Team.',NULL,NULL,'2','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (64,'5','LF','Der rote Ballführende und eine blaue Spielerin fahren parallel nebeneinander her. Sie sind auf gleicher Höhe. Die blaue Spielerin (ohne Ball) drängt den roten Spieler (Ballführenden) ab. Wer hat Vorfahrt, bzw. bekommt einen Freistoß?','Beide Spieler müssen umsichtig Spielen und sich ausweichen. In diesem Falle gibt es einen Bully.','In diesem Falle hat die blaue Spielerin Vorfahrt, und das Spiel geht ohne Unterbrechung weiter.','In diesem Fall hat der ballführende rote Spieler Vorfahrt, Blau begeht ein Foul und es gibt einen Freistoß für das rote Team.','Das ist eine grobe Unsportlichkeit, einen Feldverweis (2 min) für die blaue Spielerin und einen Freistoß für das rote Team.',NULL,NULL,'3','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (65,'5','LF','Der Ballführender roter Spieler und ein blauer Spieler fahren parallel nebeneinander her. Der blaue Spieler ist in Fahrrichtung gesehen vor dem roten Spieler und drängt den roten Spieler (Ballführenden) ab. Es liegt keine Gefährdung vor.  Wer hat Vorfahrt, bzw. bekommt einen Freistoß?','Beide Spieler müssen umsichtig Spielen und sich ausweichen. In diesem Falle gibt es einen Bully.','In diesem Falle hat der blaue Spieler Vorfahrt, und das Spiel geht ohne Unterbrechung weiter.','In diesem Fall hat der ballführende rote Spieler Vorfahrt und es gibt einen Freistoß für das rote Team.','Das ist eine grobe Unsportlichkeit, es gibt einen  Feldverweis (2 min) für den blauen Spieler und einen Freistoß für das rote Team.',NULL,NULL,'2','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (66,'5','LF','Der Ballführende (rot) und der Gegner (blau) fahren parallel nebeneinander her. Der rote Spieler (mit Ball) ist in Fahrrichtung gesehen vor dem blauen Spieler (ohne Ball) und drängt den blauen Spieler ab.  Wer hat Vorfahrt, bzw. bekommt einen Freistoß?','Beide Spieler müssen umsichtig Spielen und sich ausweichen. In diesem Falle gibt es einen Bully.','In diesem Falle hat der rote Spieler Vorfahrt, und das Spiel geht ohne Unterbrechung weiter.','In diesem Fall hat der blaue Spieler Vorfahrt und es gibt einen Freistoß für das rote Team.','Das ist eine grobe Unsportlichkeit, einen Feldverweis (2 min)  für den roten Spieler und einen Freistoß für das blaue Team.',NULL,NULL,'2','8.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (67,'9','L','Ein gültiges Tor kann erzielt werden mit ...','...dem Schläger.','...der Hand.','...dem Kopf.','...dem Rad.','...dem Ellenbogen.','...dem Sattel.','1#3#4#6','7.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (68,'11','L','Ein 6,5m wird grundsätzlich immer bei einem Foul gegeben, wenn das Foul innerhalb der Torzone (6,50 m Bereich) vor dem Tor stattgefunden hat. Richtig oder Falsch?','Richtig.','Falsch.','Ein 6,50 m wird nur bei einem unsportlichen Foul gegeben.','Ein 6,50 m wird nur bei einem besonders "harten" Foul gegeben.',NULL,NULL,'2','9.2a','Ein Foul bei einer <i>direkten Torchance</i> gibt immer 6,50m, unabhängig davon wo das Foul erfolgte. Hier unterscheidet sich die Einradhockeyregel vom Fußball, wo es einen Elfmeter nur bei Foul innerhalb des Strafraums geben kann. Fouls ohne klare Torchance innerhalb des 6,50m Bereichs haben keinen 6,50m zur Folge. Die 6,50m Linie ist also kein "Strafraum".Ob das Foul "hart" war oder nicht, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (69,'11','L','Einen 6,50m kann es nur geben, wenn nur noch der Torwart vor dem gefoulten Spieler steht.  Richtig oder Falsch?','Richtig.','Falsch.',NULL,NULL,NULL,NULL,'2','9.2a','Eine <i>direkte Torchance</i> kann auch gegeben sein, wenn noch ein Spieler vor dem gefoulten steht, dieser jedoch eine direkte Torchance hatte. ',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (70,'10','LF','Bei einem Tempogegenstoß fährt ein roter Spieler alleine auf das blaue Tor zu. Der blaue Torwart verlässt das Tor und stoppt den Angreifer mit einem Foul, noch bevor dieser die 6,50m Linie erreicht hat.','Es gibt Freistoß, da das Foul noch vor der 6,50m Linie erfolgt ist.','Es gibt 6,50m, da das Foul eine klare Torchance verhindert hat.','Wenn das Foul absichtlich erfolgt ist ("Notbremse") bekommt der Torwart eine 2 min Zeitstrafe.','Der Torwart darf den Angreifer mit allen Mitteln stoppen.',NULL,NULL,'2#3','9.2a','Ein Foul bei einer <i>direkten Torchance</i> gibt immer 6,50m, unabhängig davon wo das Foul erfolgte. Hier unterscheidet sich die Einradhockeyregel vom Fußball, wo es einen Elfmeter nur bei Foul innerhalb des Strafraums geben kann. Fouls ohne klare Torchance innerhalb des 6,50m Bereichs haben keinen 6,50m zur Folge. Die 6,50m Linie ist also kein "Strafraum".Ob das Foul "hart" war oder nicht, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (71,'7','LF','Nach einem Torschuss einer roten Spielerin prallt der Ball gegen ein auf dem Boden liegendes Einrad der gleichen (roten) Mannschaft. Das blaue Team kommt daraufhin an den Ball. Der Schiedsrichter lässt den Vorteil laufen. Der blaue Spieler ist allerdings in einer bedrängten Situation vor dem eigenen Tor und kann den Ball nicht kontrollieren und verliert ihn sofort wieder.','Das Spiel läuft weiter, da auf Vorteil entschieden wurde.','Es ist keine echte Vorteilssituation entstanden. Der Schiedsrichter unterbricht das Spiel und entscheidet auf Hindernis und Freistoß.','Es gibt ein Bully.','Nach einer Vorteilsentscheidung für Blau darf das rote Team das Blaue nicht angreifen.',NULL,NULL,'2','10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (72,'8','LF','Ein Spieler schießt den Ball aus nächster Nähe ins Tor. Beim Torschuss schwingt er den Schläger über Hüfthöhe. Der Ball geht ins Tor. Der Ball ist im Tor bevor der Schläger über Hüfthöhe geschwungen wird.','Das Tor zählt, da der Ball zuerst im Tor war.','Das Tor zählt nicht, da der Torschuss illegitim war (Hoher Schläger).','Wenn der Schiedsrichter nicht sicher ist, kann er 6,50 m geben.','Es geht weiter mit Torab.',NULL,NULL,'2#4',NULL,'Dieser Fall ist nicht explizit in den Regeln geschrieben, wird einvernehmlich so gehandhabt. Ein Schuss muss legitim sein (kein hoher Schläger) damit das Tor zählt. ',1,NULL,NULL,NULL,NULL,'nein','Rolf: Schwierige Frage. Was ist, wenn der Torschütze NACH dem Abpfiff den Schläger vor Freude über Hüfthöhe hält? Wird das Tor dann auch aberkannt?
 Christian: Ja, schwierig. Die Regeln helfen hier echt nicht weiter, siehe der Konkurrenz die ich oben beschrieben habe. Gilt 8.1. auch für hoher Schläger? Das sollte dringend geklärt werden. Siehe die Erklärung',NULL),
    (73,'9','L','Eine rote Spielerin schießt aus der eigenen Hälfte auf das Tor. Der Schuss wird von einem Spieler der eigenen Mannschaft abgefälscht der in der gegnerischen Hälfte steht. Der Ball geht ins Tor.','Das Tor zählt.','Das Tor zählt nicht, da der Schuss aus der eigenen Hälfte abgegeben wurde: Fernschuss.','Bei Unsicherheit kann es 6,50 m geben.','Es geht weiter mit Torab.',NULL,NULL,'1','7.2','Entscheidend ist der <i>letzte Ballkontakt</i>, in diesem Fall also das Abfälschen des Schusses.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (74,'11','LF','Du erkennst auf 6,50m kurz vor Spielende. Während der Vorbereitungen zum 6,50m ist die Spielzeit zu Ende.','Die Spielzeit ist abgelaufen. Der 6,50m wird nicht mehr ausgeführt.','Der 6,50m wird noch ausgeführt. Wird er gehalten, läuft das Spiel noch für ca. 20 sec. weiter.','Der 6,50m wird noch ausgeführt. Anschließend ist das Spiel beendet. Ein Nachschuss zählt nicht mehr.',NULL,NULL,NULL,'3','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (75,'11','L','Was gilt bei einem Freistoß?','Ein Freistoß ist grundsätzlich indirekt, darf also nicht direkt ins Torgeschossen werden.','Ein Freistoß ist grundsätzlich direkt, darf also direkt ins Tor geschossen werden.','Ein Freistoß darf nicht in Richtung auf das Tor geschossen werden.','Ein Freistoß darf nur nach vorne, nicht nach hinten gespielt werden.',NULL,NULL,'1','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,' '),
    (76,'11','L','Ein Freistoß von Rot wird direkt aufs Tor geschossen. Der Ball wird von einem Spieler vor dem Tor abgefälscht und geht ins Tor. Ich kann nicht erkennen, ob er von einem roten oder blauen Spieler abgefälscht wurde.','Ich gebe das Tor. Es spielt keine Rolle wer den Schuss abgefälscht hat.','Ich versuche, durch Befragung der Spieler herauszufinden wer den Ball abgelenkt hat.','Ich entscheide auf Bully.','Im Zweifel für die Abwehrmannschaft. Das Tor zählt dann nicht, wenn es nicht eindeutig ist.',NULL,NULL,'1',NULL,'Ein Freistoß gilt, wenn er von <i>irgendeinem </i>Spieler abgefälscht wird. Er ist dann nicht mehr "Indirekt".',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (77,'9','LF','Auch das soll es wohl geben: Ein Fernschuss des roten Teams wird in der gegnerischen Spielhälfte vom Schiedsrichter abgefälscht und geht ins Tor! Was nun?','Das Tor zählt, da der Fernschuss abgefälscht wurde.','Das Tor zählt nicht. Das rote Team hätte durch die Berührung des Schiedsrichters einen klaren Vorteil.','Es geht weiter mit Bully.','Es geht weiter mit Freistoß für rot.',NULL,NULL,'2#3','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (78,'8','L','Der Ball geht im Mittelfeld über die Bande gegen den Tisch des Zeitnehmers und von dort wieder ins Spielfeld. Was tust du?','Das Spiel geht weiter. Der Tisch gehört zur Bande.','Sobald der Ball über die Bande geflogen ist, ist er im Aus. Das Spiel wird unterbrochen. Freistoß.','Wenn die Spieler ohne Probleme weiterspielen, kann das Spiel weiterlaufen. Sonst unterbreche ich das Spiel.','Wenn die Spieler trotz dem Pfiff weiter spielen, unterbreche ich das Spiel energisch mit mehreren Pfiffen.','Vorteil Entscheidung ist möglich.',NULL,'2#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (79,'8','L','Kunstschuss: Der Ball geht im Mittelfeld über die Bande und rollt auf der Bande ein paar Meter. Dann fällt er wieder ins Spielfeld. Die Spieler spielen weiter.','Der Ball war im Aus. Der Schiedsrichter unterbricht das Spiel. Es gibt Freistoß.','Wenn alle weiterspielen, kann das Spiel weiterlaufen.','Das Spiel geht weiter. Die Oberseite der Bande gehört noch zum Spielfeld.','Da der Ball nicht im Aus war, geht das Spiel weiter.',NULL,NULL,'1','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: das ist in den Regeln nicht klar definiert. Daher vielleicht nur als Lehrgangsfrage? Rolf: Sehe ich auch so. Christian: Leider fehlt auch hier in den Regeln eine genaue Beschreibung wo die Auslinie ist. ',NULL),
    (80,'9','LF','Wilhelm Tell wäre stolz: Ein Torschuss von rot geht gegen die Latte, von dort aus von hinten gegen den blauen Torwart und dann ins Tor. Allerdings hat der blaue Torwart bei der Ballberührung auf dem Boden gestanden. Wie entscheidest du?','Das Tor zählt nicht. Die Berührung des Torwarts war ein Hindernis, da er auf dem Boden stand. Es gibt Eckball für die rote Mannschaft.','Da der Ball ohne den Regelverstoß des Torwarts nicht ins Tor gegangen wäre gibt es Eckball für die rote Mannschaft.','Das Tor zählt. Vorteil.','Das Tor zählt nicht. Es gibt 6,50 m für Rot weile eine Torchance durch das Hindernis des Torwarts verhindert wurde.',NULL,NULL,'3','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (81,'2','L','Was ist richtig?','Ich muss immer aufmerksam das Spiel verfolgen.','Ich soll möglichst immer auf Ballhöhe sein.','Ich soll immer möglichst dicht an der Feldbegrenzung (Bande oder Wand) stehen und darf mich nicht ins Feld bewegen.','Ich sollte während des Spiels immer mal wieder auf mein Handy gucken, falls ich von der Ligaleitung per Whatsapp über wichtige Regeländerungen informiert werde.',NULL,NULL,'1#2','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (82,'2','LF','Rot will den Ball nach vorne spielen. Der Ball trifft Dich und wird dabei so abgefälscht, dass Blau in Ballbesitz kommt und eine Torchance hat. Was entscheidest Du?','Weiterspielen. Ich bin "Luft".','Das Spiel wird unterbrochen.','Freistoß für das rote Team.','Es geht weiter mit Bully am Ort wo ich getroffen wurde.',NULL,NULL,'2#4','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (83,'9','L','Worauf muss ich bei einem Torschuss achten?','Ob der Ball vor oder hinter der Mittellinie getroffen wird.','Ob der Schläger beim Ausholen über Hüfthöhe gehalten wird.','Ob der Schläger beim Ausschwingen über Hüfthöhe geführt wird.','Ob der Schuss vor oder hinter der 6,50m Linie abgegeben wird.',NULL,NULL,'1#2#3',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (84,'2','L','Woran muss ich als erster Schiedsrichter direkt vor Spieleröffnung denken?','Ich muss sich vergewissern, dass die Zeitnehmer und Anzeigentafel bereit sind.','Jedes Team hat genau 5 Spieler auf dem Feld.','Ich vergewissere mich, dass der zweite Schiedsrichter bereit ist.','Es sind nicht mehr als 5 Spieler pro Team auf dem Feld.',NULL,NULL,'1#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (85,'2','L','Ich sehe ein SUB, der gefoulte Spieler ruft laut "SUB!". Was ist jetzt zu tun?','Ich pfeife laut und unterbreche das Spiel sofort.','Ich zeige in die Spielrichtung der gefoulten Mannschaft. Wird kein Vorteil erreicht, unterbreche ich das Spiel mit einem Pfiff und mache das Handzeichen für SUB.','Ich rufe auch sofort laut "SUB!!"','Da der gefoulte Spieler das Foul angezeigt hat, kann es keinen Vorteil mehr geben. Ich unterbreche das Spiel sofort.',NULL,NULL,'2','10.6f',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (86,'2','L','Du als zweiter Schiedsrichter hast ein Foul gesehen, es gibt keinen Vorteil. Der erste Schiedsrichter hat das Spiel jedoch nicht unterbrochen. Was tust Du?','Da nur der erste Schiedsrichter Fouls pfeift, läuft das Spiel weiter. Nach Spielende kann ich den ersten Schiedsrichter auf den Fehler hinweisen.','Ich pfeife das Foul. Als zweiter Schiedsrichter kann ich dies genauso wie der erste Schiedsrichter tun.','Ich als zweiter Schiedsrichter pfeife besser keine Fouls, da der erste Schiedsrichter sich in der Regel besser auskennt.','Nach der Unterbrechung kann ich mich mit dem ersten Schiedsrichter kurz abstimmen.',NULL,NULL,'2#4','10.2','Beide Schiedsrichter können also das Spiel bei Regelverstößen unterbrechen. ',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (87,'2','L','Du als zweite Schiedsrichterin hast ein Foul gepfiffen. Der erste Schiedsrichter entscheidet jedoch auf Vorteil und signalisiert und ruft "Weiterspielen! Vorteil!".  Was hast du zu tun?','Hier überstimmt der erste Schiedsrichter dich. Das Spiel läuft weiter wegen Vorteilsentscheidung.','Der Pfiff von mir hat das Spiel unterbrochen. Damit ist keine Vorteilsentscheidung mehr möglich.','Ich pfeife mehrfach laut und deutlich und unterbreche das Spiel. Ich erkläre, dass der Pfiff das Spiel unterbrochen hat. Der Foulpfiff zählt.','Es geht weiter mit Bully.',NULL,NULL,'2#3','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (88,'2','L','Der erste Schiedsrichter hat einen Regelverstoß (Foul) gesehen und pfeift. Der zweite Schiedsrichter sieht dies jedoch anderes und ruft "Kein Foul! Weiterspielen!".','Es zählen grundsätzlich nur die Entscheidungen des 1. Schiedsrichters.','Der Pfiff des ersten Schiedsrichters hat das Spiel unterbrochen. Es ist kein Vorteil möglich.','Der erste Schiedsrichter unterbricht das Spiel mit mehrfachem Pfeifen. Einigen sich die Schiedsrichter darauf dass es keine Foul war, gibt es Bully.','Der erste Schiedsrichter kann nun entscheiden ob er das Spiel weiterlaufen lässt oder nicht.',NULL,NULL,'2#3','10.6a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (89,'2','LF','Welche Aussage stimmt?','Der erste Schiedsrichter trifft grundsätzlich alle Entscheidungen, da er die Regeln besser kennt.','Der erste Schiedsrichter trifft alle wichtigen Entscheidungen. Weniger wichtige kann auch der zweite Schiedsrichter treffen.','Ausnahmsweise kann der zweite Schiedsrichter auch einmal eine Entscheidung treffen. Er sollte aber nicht so laut pfeifen, damit das Spiel weitergehen kann, falls er falsch gelegen hat.','Erster und zweiter Schiedsrichter sind Partner. Beide können alle Entscheidungen treffen und pfeifen. Bei schwierigen oder unklaren Entscheidungen sprechen sie sich ab und finden eine gemeinsame Entscheidung.','Der zweite Schiedsrichter ist nur ein Berater des ersten Schiedsrichters.',NULL,'4','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (90,'2','L','Was bedeutet "Fair Play" für die Spieler im Umgang mit den Schiedsrichtern?','Tatsachenentscheidungen des Schiedsrichters sind zu akzeptieren.','Spieler dürfen grundsätzlich nicht mit dem Schiedsrichter sprechen.','Als Spieler Regelverstöße zuzugeben und auf falsche Entscheidung zu den eigenen Gunsten (z.B. bei Ausbällen) hinzuweisen, erleichtert dem Schiedsrichter die Aufgabe.','Spieler und Schiedsrichter sind Partner im Spiel. Diskussionen über strittige Entscheidungen sind respektvoll zu führen. Die Schiedsrichter dürfen nicht angeschrien oder beleidigt werden.','Nach dem Spiel: Kritik nur in angemessener und respektvoller Art. Niemals persönlich verletzend sprechen.','Immer daran denken: Auch Schiedsrichter dürfen (wie Spieler) auch Fehler machen.','1#3#4#5#6',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (91,'2','L','Warum sollte die Aufgabe von einem Schiedsrichter nicht von zwei oder drei Personen als Team übernommen werden?','Da sie sich gegenseitig ablenken können.','Entscheidungen (Pfiff oder nicht) müssen in kurzer Zeit getroffen werden, da bleibt keine Zeit zur Diskussion.','Gruppenschiedsrichter neigen dazu überhaupt keine Entscheidungen zu treffen, da es unklar ist wer die Entscheidung trifft.','Neulinge können erfahrene Schiedsrichter begleiten, es sollte aber klar sein wer der Schiedsrichter ist. Der begleitende Schiedsrichter soll außerhalb des Feldes stehen.',NULL,NULL,'1#2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (92,'9','L','Das rote Team hat ein Tor per Fernschuss erzielt. Worauf hast du zu achten?','Es geht weiter mit Torab für Blau.','Ich muss darauf achten, dass das Tor auch wirklich nicht auf der Anzeige erscheint.','Ich ermahne den Torschützen wegen unfairem Spiel.','Ich kommuniziere die Entscheidung deutlich mit Handzeichen und Verbal ("kein Tor! Fernschuss").','Es geht weiter mit Bully am Mittelkreis.',NULL,'1#2#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (93,'7','L','Ein roter Spieler schießt den Ball gegen ein am Boden liegendes Einrad der eigenen Mannschaft im Mittelfeld. Der Ball kommt zu ihm zurück und er spielt weiter. Du hast es gesehen, aber was entscheidest du?','Das Spiel läuft weiter. Vorteil für die ballführende Mannschaft.','Ich unterbriche das Spiel. Wegen Hindernis gibt es Freistoß für Blau.','Wenn alle weiterspielen kann das Spiel ohne Unterbrechung weitergehen.','Der Freistoß findet dort statt wo der Ball das am Boden liegende Einrad getroffen hat.',NULL,NULL,'2#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: für mich liegt die Betonung bei der Regel auf "gegenerisch", was hier nicht der Fall ist. Antwort 3 und 5 sind für mich gleich. Rolf: Antwort 4 ist auch richtig. Antwort 3 und 5 sind gleich, darum kann 5 entfernt werden. Christian: Ja',NULL),
    (94,'7','LF','Ein roter Spieler schießt den Ball gegen ein am Boden liegendes Einrad der gegnerischen Mannschaft im Mittelfeld. Der Ball kommt zu einem Spieler seiner Mannschaft zurück und er ist in aussichtsreicher Position.','Ich unterbreiche das Spiel. Wegen Hindernis gibt es Freistoß für die ballführende (rote) Mannschaft.','Ich entscheide auf Vorteil und lasse das Spiel laufen.','Ich muss in diesem Fall nichts machen.','Ich zeige in Spielrichtung der roten Mannschaft und rufe laut "Vorteil".',NULL,NULL,'2#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (95,'1','LF','Absatz 1 des Ligamodus: Oberstes Ziel der Deutschen Einradhockeyliga ist:','Jedem Einradhockeybegeisterten die Ausübung seiner Sportart in einem sportlichen Wettkampf zu ermöglichen, sowie das Einradhockey in Deutschland weiterzuentwickeln.','Auf die exakte Einhaltung der internationalen Einradhockeyregeln zu achten.','Einen sportlichen Wettkampf nach den Richtlinien der olympischen Sportverbände zu gewährleisten.','Einradhockey als Profisport zu etablieren.',NULL,NULL,'1',NULL,'Ligamodus. 1 Allgemeines<BR> Ihr oberstes Ziel ist es, jedem Einradhockeybegeisterten die Ausübung seiner Sportart in einem sportlichen Wettkampf zu ermöglichen sowie das Einradhockey in Deutschland weiterzuentwickeln.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (96,'2','L','Der Schiedsrichter ist ....','... der Gegner der Spieler.','... der Partner der Spieler.','... der Konkurrent der Spieler.','...halt irgendjemand der gerade Lust hat die Pfeife zu halten und die Tore anzuzeigen.',NULL,NULL,'2',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (97,'2','L','Bei einem Spiel stehen sich zwei Anfängerteams gegenüber. Worauf musst du besonders achten?','Auf die strikte und genaue Einhaltung aller Regeln.','Die Regeln dürfen je nach Fähigkeitsstand weniger strikt ausgelegt werden.','Ich erkläre den Spielern meine Entscheidungen und helfe so, das Spiel zu verstehen.','Der Schutz von jungen Spielern ist mir besonders wichtig.',NULL,NULL,'2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (98,'11','L','Der blaue Torwart hält den Ball, den eine rote Spielerin auf das Tor schießt. Der blaue Torwart steht dabei direkt vor dem Tor, mit den Füßen auf dem Boden. Der Ball wäre sicher ins Tor gegangen.','Ich entscheide auf Straftor für Rot.','Es gibt einen 6,50 m für die rote Mannschaft.','Weiterspielen.','Eckball für die rote Mannschaft.',NULL,NULL,'1','9.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (99,'8','LF','Der rote Torwart hält den Ball mit dem Schläger über der Latte.','Hoher Schläger. Ecke für die angreifende Mannschaft.','Weiterspielen, in Tornähe ist hoher Schläger erlaubt (sofern niemand gefährdet wird).','6,50m für die blaue Mannschaft.','Wenn niemand direkt gefährdet wurde, kann weitergespielt werden',NULL,NULL,'1','8.10','Auch in unmittelbarer Nähe zum eigenen Tor darf der Schläger nicht über die Querlatte des Tors gehoben werden.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (100,'3','L','Was bedeutet dieses Handzeichen?','SUB (Stick under Bike)','Kein Foul - weiterspielen','Vorteil läuft','Kein Tor!',NULL,NULL,'2','10.7d',NULL,1,'frage_0100.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (101,'3','L','Was bedeutet dieses Handzeichen?','Zeitstopp','Foul','Weiterspielen','Bully',NULL,NULL,'1','10.7e',NULL,1,'frage_0101.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (102,'3','L','Was bedeutet dieses Handzeichen?','Weiterspielen','Kein Tor','Tor','Zeitstopp',NULL,NULL,'3','10.7f',NULL,1,'frage_0102.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (103,'3','L','Was bedeutet dieses Handzeichen?','Tor','Bully','Zeitstopp','Kein Tor',NULL,NULL,'4','10.7g',NULL,1,'frage_0103.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (104,'3','L','Was bedeutet dieses Handzeichen?','SUB','Hindernis','Hoher Schläger','Zeitstopp',NULL,NULL,'3','10.7h',NULL,1,'frage_0104.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (105,'3','L','Was bedeutet dieses Handzeichen?','Körperkontakt','Hindernis','SUB und SIB','Kein Tor',NULL,NULL,'3','10.7i',NULL,1,'frage_0105.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (106,'3','L','Was bedeutet dieses Handzeichen?','Hindernis','Kein Foul','Zeitstopp','Körperkontakt',NULL,NULL,'1','10.7j',NULL,1,'frage_0106.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (107,'3','L','Was bedeutet dieses Handzeichen?','Zeitstopp','2  min Zeitstrafe','6,50 m','Hindernis','Körperkontakt',NULL,'5','10.7k',NULL,1,'frage_0107.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (108,'3','L','Was bedeutet dieses Handzeichen?','Spielbeginn in 2 min','2 min Pause','SUB','2 min Zeitstrafe','Zweifaches Handspiel',NULL,'4#5','10.7l',NULL,1,'frage_0108.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (109,'3','L','Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','5 min Pause','Mehr als 5 Spieler auf dem Feld','Fünffaches Handspiel','Hoher Schläger',NULL,'1','10.7m',NULL,1,'frage_0109.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (110,'11','LF','Ein roter Spieler wird beim Torschuss von einem blauen Spieler gefoult. Er schießt den Ball, nachdem er mit dem Fuß den Boden berührt hat. Der Ball geht ins Tor.','Ich erkenne auf Vorteil. Das Tor zählt.','6.50 m für die Mannschaft des gefoulten Spielers, sofern es sich um eine klare (direkte) Torchance handelte.','Das Tor zählt nicht, da der Schuss nicht regelgerecht war.','Ein Foul um ein Torschuss zu stoppen kann eine Unsportlichkeit sind und mit 2 min Zeitstrafe geahndet werden.',NULL,NULL,'2#3#4','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (111,'7','L','Ein roter Spieler ist ohne Fremdeinwirkung vom Einrad gefallen. Beim Aufsteigen steht er einem blauen Spieler im Weg, der zum Ball fahren möchte.','Kein Hindernis, der rote Spieler darf an der Stelle aufsteigen wo er gefallen ist.','Hindernis! Der rote Spieler muss dem Spielgeschehen zuerst ausweichen.','Freistoß für die blaue Mannschaft, sofern kein Vorteil.','Wenn eine klare Torchance verhindert wurde: 6,50 m',NULL,NULL,'2#3#4','8.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (112,'8','LF','Ein Spieler spielt den Ball mit der Hand. Dazu nimmt er die obere Hand kurzzeitig vom Schläger um den Ball zu spielen. Es wird niemand gefährdet.','Das obere Schlägerende muss immer bedeckt sein. Freistoß für den Gegner.','Weiterspielen. Dies ist kurzzeitig erlaubt, um den Ball mit der Hand zu spielen.','Verwarnung für den Spieler. Bei Wiederholung 2 min Feldverweis.',NULL,NULL,NULL,'2','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (113,'8','LF','Ein roter Spieler spielt den Ball mit der Hand. Dazu nimmt er die obere Hand kurzzeitig vom Schläger. Mit dem freien Schlägerende kommt er in die Nähe des Kopfes eines Gegenspielers.','Weiterspielen. Dies ist erlaubt um den Ball mit der Hand zu spielen.','Freistoß für die gegnerische Mannschaft, da dies eine Gefährdung darstellt.','Weiterspielen und den Spieler bei nächster Gelegenheit ermahnen.',NULL,NULL,NULL,'2','8.9',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (114,'7','LF','Der Torwart hält sich am Tor fest. Es kommt ein Schuss aufs Tor, der Torwart lässt kurz das Tor los um den Ball zu spielen. Der Ball wäre mit Sicherheit ins Tor gegangen.','Straftor','6,50 m','Torab','Im Anfängerbereich kann dies (nach vorheriger Absprache) zugelassen werden.',NULL,NULL,'1#4','5.3','Es reicht nicht aus, das Tor nur schnell für die Zeit loszulassen, während der Torwart am Spiel teilnimmt.',1,NULL,NULL,NULL,NULL,'nein','Rolf: Es gibt keine Regel, die das im Anfängerbereich erlaubt. Christian: richtig. Das ist informell und würde ich hier löschen. Vielleicht in die Erklärung aufnehmen?',NULL),
    (115,'9','LF','Der Torwart berührt einen gegnerischen Fernschuss, bevor er ins Tor geht.','Tor ab.','Tor zählt.','Bully an der Mittellinie.','6,50 m',NULL,NULL,'2','7.2','Wird ein Fernschuss in der gegnerischen Hälfte abgefälscht, zählt dies nicht mehr als Fernschuss. Von welcher Mannschaft er dabei berührt wird, spielt keine Rolle.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (116,'7','LF','Ein verteidigender blauer Spieler steht auf dem Boden und wird angeschossen. Der Ball geht von ihm direkt ins blaue Tor (Eigentor).','Vorteil! Das Eigentor zählt, auch wenn der Spieler auf dem Boden stand.','Das Tor zählt nicht, da es irregulär erzielt wurde (Spieler stand auf dem Boden).','Es gibt Bully.','Das Tor zählt nicht. Bei Eigentoren gibt es keinen Vorteil.',NULL,NULL,'1','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (117,'9','LF','Ein verteidigender Spieler spielt den Ball mit der Hand und schlägt in aus Versehen ins eigene Tor. Pech gehabt, oder illegales Tor? Was entscheidest Du?','Das Eigentor zählt, auch wenn es mit der Hand erzielt wurde.','Illegales Tor, da es mit der Hand erzielt wurde.','Ich kann das Tor gelten lassen, wenn es keine Absicht war.','Es geht weiter mit Torab.',NULL,NULL,'1','7.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (118,'9','LF','Na so was? Ein Rückpass des roten Teams aus der gegnerischen Hälfte landet im eigenen Tor, ohne dass ihn ein weiterer Spieler berührt hat. Die roten Spieler monieren, dass dieses (Eigen-)Tor als Fernschuss nicht gewertet wird. Was entscheidest Du?','Fernschuss, da das Tor aus der gegnerischen Hälfte erzielt wurde. Das Tor zählt nicht.','Das Tor zählt. Die Fernschussregel gilt nicht für Eigentore.','Es geht weiter mit Torab.','Es geht weiter mit Bully am Mittelkreis.',NULL,NULL,'2','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (119,'8','LF','Ein roter Spieler wird gesubt, sein Einrad fällt auf den Ball und berührt diesen. Hindernis oder Foul, was entscheidest Du?','Foul! Es gibt Freistoß für das rote Team. Der erste Verstoß zählt.','Hier sind zwei Verstöße gleichzeitig. Weiterspielen oder Bully.','Ich entscheide, was wichtiger ist.','Hindernis. Freistoß für das blaue Team.',NULL,NULL,'1','8.3#10.6d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (120,'10','LF','Ein Spieler ist wegen unsportlichen Verhaltens aufgefallen. Wie verhalte ich mich?','Erstmal abwarten, ob er sich in Zukunft besser verhält.','Ich unterbreche das Spiel und entscheide gemeinsam mit dem anderen Schiedsrichter über eine angemessene Strafe.','Während der Besprechung die Zeit anhalten.','Dem Spieler die Strafe und den Grund dafür erklären.','Dem Spieler und dem Anschreibetisch den Ablauf der Strafe erklären.',NULL,'2#3#4#5','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: Evtl. Vorteil? Christian: Ja. Antwort 2 umformulieren?',NULL),
    (121,'1','L','Und los geht es! Was hast du beim Bully zu Spielbeginn zu beachten?','Den Ball nicht über Hüfthöhe halten.','Den Ball gerade ins Spiel fallen lassen, nicht werfen.','Ich vergewissere mich, dass alle Spieler und der Anschreibetisch bereit sind.','Ich schaue schon vor dem Bully, wie ich mich am sichersten aus der Mitte des Spielfeldes zurückziehen kann.','Das Spiel beginnt mit meinem Pfiff, wenn der Ball den Boden berührt.','Alle nicht am Bully beteiligten Spieler müssen 2 m Mindestabstand haben.','1#2#3#4#5#6','9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (122,'8','L','Ein Ball steckt zwischen den Speichen eines Einrades der roten Mannschaft. Was entscheidest Du?','Wenn ich nicht weiß wer geschossen hat, entscheide ich auf Bully.','Entscheidend ist wer den Ball geschossen hat. Freistoß für die andere Mannschaft.','Egal wer geschossen hat: Es gibt Freistoß (Torab, Ecke) für die blaue Mannschaft.','Es gibt Freistoß für die rote Mannschaft, egal wer geschossen hat.',NULL,NULL,'3','5.8',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (123,'1','LF','Ein roter Spieler liegt seit einiger Zeit verletzt am Boden. Um den Ball wird weiter gekämpft. Was machst Du?','Ich warte bis ein Team klar in Ballbesitz ist oder der Ball im Aus ist. Dann unterbreche ich das Spiel und kümmere mich um den verletzten Spieler.','Ich unterbreche das Spiel sofort und kümmere mich um den verletzten Spieler.','Wenn der Ballbesitz im Moment der Unterbrechung unklar ist, wird das Spiel mit Bully fortgesetzt.','Nach der Unterbrechung gibt es Freistoß für Blau, da ein roter Spieler verletzt war.',NULL,NULL,'2#3','10.6h',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (124,'2','L','Wie solltest du im Spiel auftreten?','Ich sollte durch bestimmtes und ruhiges Auftreten überzeugen.','Entscheidungen werden auf Anfrage erläutert, sie werden jedoch nicht mit den Spielern ausdiskutiert.','In einer unklaren Situation kann ich die Spieler kurz befragen, bevor ich eine endgültige Entscheidung treffe.','Ich mache den Spielern klar, dass ich immer Recht habe und nie einen Fehler machen werde.',NULL,NULL,'1#2#3','10.6b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (125,'8','L','Du hast einen Regelverstoß (SUB) gesehen und das Spiel unterbrochen. Du entscheidest auf Freistoß für das blaue Team. Wie gehst du jetzt weiter vor?','Ich mache kurz das Handzeichen für SUB. Die Spieler wissen dann schon wie es weiter geht.','Ich mache klar und deutlich das Handzeichen für SUB und erkläre, wenn nötig, den Regelverstoß.','Anschließend zeige ich mit der einen Hand auf den Punkt von wo aus der Freistoß ausgeführt wird und mit der anderen Hand in die Spielrichtung des Freistoßes. Diese Position halte ich, bis das Spiel wieder läuft.','Wenn alle Spieler bereit sind, pfeife ich das Spiel zügig an, damit es zu keinen unnötigen Verzögerungen kommt.',NULL,NULL,'2#3#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (126,'8','L','Eine roter Spielerin will den Ball mit der Hand spielen. Dazu fängt sie den Ball kurzzeitig und wirft ihn weiter weg. Darf sie das?','Ja, eine aktive Ballberührung ist erlaubt.','Nein. Der Ball darf nicht gefangen oder eingeklemmt werden. Auch nicht kurzzeitig.','Wenn der Ball nur kurz gehalten wird, ist es erlaubt.','Ich unterbreche das Spiel. Es geht weiter mit Freistoß für die blaue Mannschaft.',NULL,NULL,'2#4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Antwort 4 passt hier irgendwie nicht so zur Frage, vielleicht findet man da noch was passenderes Christian: Ich finde es ok zu sagen wie es weitergeht. (auch wieder die Vorteilsfrage). ',NULL),
    (127,'1','L','Wie viele Spieler dürfen maximal gleichzeitig für ein Team auf dem Feld spielen?','3 Spieler','4 Spieler','5 Spieler','6 Spieler',NULL,NULL,'3','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (128,'1','LF','Ein Team hat das Spiel mit nur drei Spielern begonnen. Nun verletzt sich einer dieser drei Spieler und kann nicht weiter spielen. Kann das Team mit zwei Spielern weiter spielen?','Nein, es müssen immer mindestens drei Spieler auf dem Feld stehen. Das Spiel muss abgebrochen werden.','Ja, es reicht wenn zu Beginn des Spiels drei Spieler auf dem Feld sind.','Es ist egal wie viele Spieler auf dem Feld stehen.','Es kann nur weitergehen, falls ein Ersatzspieler von einem anderen Team ausgeliehen werden kann.',NULL,NULL,'2','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (129,'1','LF','Ein Team möchte einen Spieler auswechseln. Worauf hast du zu achten?','Der eingewechselte Spieler darf das Spielfeld überall betreten.','Der eingewechselte Spieler muss das Spielfeld in der eigenen Spielhälfte betreten.','Der eingewechselte Spieler muss das Spielfeld an der gleichen Stelle betreten, wo ausgewechselte Spieler es verlässt.','Der eingewechselte Spieler darf das Spielfeld erst betreten, wenn der ausgewechselte Spieler das Feld verlassen hat.','Auswechslungen müssen dem Schiedsrichter angezeigt werden.',NULL,'2#3#4','4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (130,'10','LF','Ein Spieler der blauen Mannschaft verzögert das Spiel absichtlich 2 min vor Spielende. Du entscheidest ihm eine 2 min Feldverweis zu geben. Wie geht das Spiel weiter? Worauf musst du achten?','Es gibt Freistoß für die rote Mannschaft am Mittelpunkt.','Da der Feldverweis weniger als 3 min vor dem Spielende ausgesprochen wurde, gibt es einen 6,50 m für die rote Mannschaft.','Es geht weiter mit Bully.','Ich muss darauf achten, dass bei der Diskussion die Uhr angehalten wird, damit kein Nachteil für das blaue Team entsteht.',NULL,NULL,'2#4','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: 9.5 und 9.1 "Wenn eine Strafe für Zeitspiel verhängt wird, wird der Freistoß am Mittelpunkt ausgeführt." widersprechen sich hier Christian: Ich verstehe es so, dass 9.5. hier 9.1 "schlägt" wenn es in den letzten 3 min. passiert. ',NULL),
    (131,'10','LF','Ein Spieler verzögert das Spiel. Du unterstellst ihm Absicht, um den knappen Vorsprung "über die Zeit zu bringen". Was tust du?','Ich ermahne den Spieler.','Ich erteile dem Spieler einen 2 min Feldverweis.','Es geht weiter mit Freistoß für das andere Team.','Wenn dies innerhalb der letzten 3 min geschieht, gibt es statt dem Freistoß einen 6,50 m',NULL,NULL,'2#3#4','9.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (132,'5','LF','Ein blauer Spieler steht/pendelt auf der Stelle. Dabei stützt er sich auf dem Schläger ab. Ein roter Spieler will an ihm vorbei fahren und fährt über den Schläger vom blauen Spieler. Beide Spieler stürzen. Was entscheidest du?','Da beide Spieler gestürzt sind, gibt es Bully.','Es kann weiter gespielt werden, da beide gestürzt sind und keine Gefahr besteht.','Es gibt Freistoß für die rote Mannschaft (sofern kein Vorteil).','Es gibt einen Freistoß für blau, da dem pendelnden Spieler immer ausgewichen werden muss.',NULL,NULL,'3','8.2c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (133,'11','LF','Was gilt bei einem Freistoß?','Der Ball darf gelupft oder geschlenzt werden.','Der Ball darf nicht gelupft oder geschlenzt werden, sondern nur direkt geschossen.','Der Spieler darf mit dem Ball am Schläger losfahren.','Die gegnerischen Spieler müssen mindestens 2 m Abstand einhalten.',NULL,NULL,'1#4','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (134,'11','L','Wo wird ein Freistoß ausgeführt?','Wenn ein Team einen Freistoß in der gegnerischen Torzone erhält, wird der Freistoß vom nächstgelegenen Eckpunkt ausgeführt (Eckstoß).','Wenn ein Team einen Freistoß in der eigenen Torzone bekommt, wird der Freistoß 1 m vor der Torlinie ausgeführt (Torabstoß).','Der Freistoß wird immer da ausgeführt, wo der Ball im Moment des Pfiffes ist.','Ist der Freistoß in keiner der Torzonen, wird er am Ort des Regelverstoßes ausgeführt.',NULL,NULL,'1#2#4','9.1a',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (135,'11','L','Wie viel Abstand müssen gegnerische Spieler bei einem Freistoß halten?','2 m','1 m','3 m','Es gilt kein besonderer Abstand.',NULL,NULL,'1','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (136,'11','LF','Ein roter Spieler steht beim Freistoß des blauen Spielers mit seinem Einrad 2 m vom Freistoßpunkt entfernt. Mit seinem Schläger kommt er aber näher an den Ball. Was gilt hier?','Das ist erlaubt. Solange das Einrad 2 m vom Freistoßpunkt entfernt ist.','Das ist nicht erlaubt, Einrad und Schläger müssen mindestens 2 m entfernt sein.','Das ist erlaubt, solange der Spieler den Ball nicht berührt.',NULL,NULL,NULL,'2','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (137,'Videos','L','Schaue Dir das Video an. Wie würdest du entscheiden?','Ich unterbreche das Spiel sofort und entscheide auf Freistoß für das blaue Team.','Es liegt kein Foulspiel vor.','Der blaue Spieler wurde gefoult. SUB.','Ich unterbreche das Spiel nicht sofort, sondern zeige mit der Hand in Spielrichtung Blau und rufe "Vorteil". Wenn Blau weiter im Ballbesitz bleibt rufe ich bald "Vorteil ende".','Kommt Blau nicht in Ballbesitz, pfeife ich ab und gebe Freistoß für Blau.',NULL,'3#4#5','10.6f',NULL,1,NULL,'frage_0137.mp4',NULL,NULL,'nein',NULL,NULL),
    (138,'2','LF','Aua. Ein Schuss aufs Tor trifft den Schiedsrichter. Dann wird der Ball vom Schiedsrichters auch noch (natürlich unabsichtlich) ins Tor der roten Mannschaft abgelenkt. Was nun?','Das Tor zählt. Der Ball ist regulär ins Tor gegangen. Der Schiedsrichter ist "Luft".','Das Tor zählt nicht. Der Schiedsrichter hat dem angreifenden Team einen Vorteil verschafft.','Es geht weiter mit Bully.','Das Tor zählt, da Eigentore immer zählen.',NULL,NULL,'2#3','10.6i',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (139,'11','L','Wo wird der Freistoß nach einem Ausball im Mittelfeld ausgeführt?','1 m von der Seitenlinie entfernt.','Direkt auf der Seitenlinie.','2 m von der Seitenlinie entfernt.','Dort wo der Ball ins Aus ging.','Da wo die Spieler den Ball wieder ins Feld gebracht haben.',NULL,'1#4','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (140,'2','L','Vor dem Tor geht es heiß her. Zwei Spieler auf dem Feld verdecken Dir die Sicht auf das Geschehen. Was nun?','Da ich gerade eh nix sehen kann, kann ich auch ein wenig mit meinen Team hinter der Bande sprechen.','Der andere Schiedsrichter wird es ja sicher sehen, daher muss ich nichts tun.','Ich fordere die beiden Spieler auf, doch bitte ein wenig auf Seite zu fahren, damit ich sehen kann.','Ich versuche mich schnell und vorsichtig so zu bewegen, dass ich einen guten Blick auf die Situation habe.','Ich kann auch ein wenig nach vorne ins Spielfeld treten, um einen guten Blick zu haben, solange ich keine Spieler dabei behindere.',NULL,'4#5','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (141,'2','L','Wie verhältst du dich während des Spiels?','Ich stehe möglichst immer in der Nähe der Mittellinie, damit ich beide Seiten des Spielfeldes sehen kann.','Damit ich möglichst wenig störe, stehe ich direkt an der Wand mit einem Fuß nach hinten an der Wand angelehnt und den Händen hinter dem Rücken verschränkt.','Ich bewege mich mit dem Ball auf und ab und versuche immer einen guten Blick auf Spiel zu haben.','Meine Körpersprache zeigt, dass ich aufmerksam bin. Ich habe die Pfeife bereit, um jederzeit eine Entscheidung treffen zu können.','Zwischendurch frage ich immer mal wieder meine Freunde außerhalb des Spielfeldes, was es so Neues gibt.',NULL,'3#4','10.6c',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (142,'2','L','Welche besonderen Aufgaben hast du als 1. Schiedsrichter?','Nur ich triffe alle wichtigen Entscheidungen.','Nur ich darf ein Foul pfeifen oder das Spiel unterbrechen.','Ich überstimme den 2. Schiedsrichter, falls wir unterschiedlicher Meinung sind.','Ich pfeife das Spiel nach Unterbrechungen wieder an.','Ich lasse den Ball zum Bully fallen.','Nur ich vergebe Zeitstrafen.','3#4#5','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (143,'2','LF','Es gibt Freistoß. Er wird schnell ausgeführt, bevor das Spiel angepfiffen wurde. Was ist zu tun?','Ich unterbreche das Spiel und lasse den Freistoß wiederholen.','Wenn alle weiterspielen, kann das Spiel weitergehen.','Ich versuche das Spiel immer zügig anzupfeifen, wenn alle Spieler bereit sind.','Das ist ein Regelverstoß. Es gibt jetzt einen Freistoß für die andere Mannschaft.',NULL,NULL,'1#3','5.5',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (144,'Videos','L','Schaue Dir das Video an. Wie beurteilst Du die Situation?','Der weiße Spieler wird gesubt. Ich unterbreche das Spiel sofort und entscheide auf Freistoß für das weiße Team, da es keinen Vorteil gibt.','Es liegt kein Foulspiel vor.','Der weiße Spieler wurde gefoult. SUB.','Die Situation ist unklar, daher unterbreche ich das Spiel und gebe ein Bully.',NULL,NULL,'2',NULL,'Der weiße Spieler ist über den eigenen Schläger gestürzt, der an der Wand eingeklemmt war.',1,NULL,'frage_0144.mp4',NULL,NULL,'nein',NULL,NULL),
    (145,'Videos','L','Schaue Dir das Video an. Welchen Regelverstoß erkennst du? Welche Aussagen stimmen?','Es ist kein Regelverstoß zu sehen.','Es ist ein hoher Schläger vom gelben Spieler zu sehen.','Das Spiel läuft weiter, da niemand gefährdet wurde.','Es gibt Freistoß für Blau am Ort des Geschehens.','Es gibt Eckball für Blau, da es innerhalb von 6,50 m Bereich passiert ist.','Da das Spiel weiter läuft, unterbreche ich es energisch (mehrfaches Pfeifen) und ahnde den Regelverstoß.','2#5#6','8.10#9.1a',NULL,1,NULL,'frage_0002.mp4',NULL,NULL,'nein','Rolf: Erst Vorteil abwarten, Blau hat kurz nach dem hohen Schläger eine gute Torchance (trifft den Ball aber nicht) Christian: Antworten (und Frage) muss hier umformuliert werden. ',NULL),
    (146,'8','L','Der Ball ist im Aus. Wie geht es weiter?','Sobald der Ball das Spiel verlassen hat, pfeife ich und unterbreche das Spiel.','Ich entscheide, wer zuletzt am Ball war. Die andere Mannschaft bekommt einen Freistoß.','Ich zeige mit der Hand in Spielrichtung und kann dies auch laut sagen, z.B. "Freistoß für Blau".','Ich zeige solange in Spielrichtung, bis das Spiel wieder beginnt.','Sobald die Spieler bereit sind, pfeife ich das Spiel zügig an.','Wenn der Ball sofort wieder ins Feld zurück springt, kann das Spiel ohne Unterbrechung weitergehen.','1#2#3#4#5','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (147,'2','L','Wie kannst du dazu beitragen, dass das Spiel bei Ausbällen zügig weitergeht?','Du hast Reservebälle dabei. Wenn der Ball über die Bande geschossen wurde, setzt ich einen neuen Ball an den Freistoßpunkt. Den alten Ball lasse ich mir geben, wenn das Spiel wieder läuft.','Ich kommuniziere meine Entscheidung klar und deutlich und halte den Arm ausgestreckt in Spielrichtung, bis das Spiel wieder läuft.','Wenn ich mir unsicher bin, wer zuletzt am Ball war, frage ich kurz die Spieler. Gibt es nicht sofort eine Einigung, entscheide ich auf Bully.','Ich kann den Spielern helfen, indem ich den Ball an den richtigen Freistoßpunkt lege und die Entscheidung auch verbal kommuniziere, z.B. "Torab!".','Wenn der Ball aus dem Aus wieder zurück ins Feld springt, z.B. von der Wand hinter der Eckbank, kann ich das Spiel weiterlaufen lassen.','Nur als erster Schiedsrichter pfeife das Spiel wieder an, um "Doppelanpfiffe" zu verhindern.','1#2#3#4#6','5.7',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (148,'9','L','Der Ball ist von hinten ins Tornetz geschossen worden und ist nicht mehr spielbar. Wer bekommt einen Freistoß?','Es gibt in jedem Fall Torab, unabhängig davon wer geschossen hat.','Es gibt in jedem Fall Eckball, unabhängig davon wer geschossen hat.','Entscheidend ist, wer den Ball zuletzt berührt hat. Es gibt Freistoß (Torab oder Eckball) gegen dieses Team.','Es gibt in diesem Fall immer ein Bully am Eckpunkt.','Wenn es unklar ist wer den Ball zuletzt berührt hat, entscheide ich auf Bully am Eckpunkt.',NULL,'3#5','7.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (149,'9','L','Tor oder nicht? Das ist schon mal eine wichtige Frage... Was stimmt?','Das Tor zählt wenn der Ball vollständig über die Torlinie gelangt ist.','Das Tor zählt, wenn der Ball die Torlinie nur berührt hat.','Das Tor zählt, wenn der Ball vollständig auf der breiten Torlinie ist.','Auch wenn der Ball seitlich, z.B. durch ein Loch im Netz ins Tor gelangt ist, zählt das Tor.',NULL,NULL,'1','7.3',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: "vollständig über die Torlinie gelangt" steht nicht in den Regeln
Larissa: Stimmt, das fehlt da. Für mich als Lehrgangsfrage aber okay Christian: Das ist konsens, steht aber wirklich nicht in den Regeln. Sollte man anregen. ',NULL),
    (150,'6','LF','Wann und wie darf ein Spieler den Schläger des anderen Spielers anheben?','Nur in der Nähe des Balles darf ein Spieler den Schläger des Gegners mit dem eigenen Schläger anheben.','Das ist überall auf dem Feld erlaubt, auch wenn der Ball  nicht in der Nähe ist.','Das Hochheben darf nicht mit übertriebener Härte erfolgen.','Der gegnerische Schläger darf nicht zu einem hohen Schläger angehoben werden.',NULL,NULL,'1#3#4','8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (151,'8','LF','Es gibt eine turbulente Szene vor dem roten Tor. Ein roter Spieler hat das Tor unabsichtlich verschoben. Was machst du?','Ich kann das Spiel unterbrechen und einen Freistoß (Eckball) für das blaue Team geben.','Ich lasse das Spiel in jedem Fall weiterlaufen.','Wenn es den Spielern gelingt das Tor schnell wieder richtig zu stellen ohne das Spiel zu beeinflussen, kann das Spiel weiterlaufen.','Wenn dadurch eine klare Torchance des blauen Teams verhindert wurde, gebe ich einen 6,50 m.',NULL,NULL,'1#3#4','8.6#8.1#9.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (152,'10','LF','Eine turbulente Szene vor dem roten Tor: Ein roter Spieler hat das Tor verschoben. Du unterstellst ihm Absicht um eine Torchance des blauen Teams zu verhindern. Was entscheidest Du?','Ich ermahne den roten Spieler und entscheide auf Eckball für Blau.','Ich ermahne den roten Spieler. Es geht weiter mit Bully am Eckball wenn das Tor wieder richtig steht.','Ich entscheide auf 6,50 m für das blaue Team.','Ich gebe dem roten Spieler eine 2 min. Feldverweis wegen unsportlichen Verhaltens.',NULL,NULL,'3#4','8.6#9.2#9.5a',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: In der Frage steht nicht, dass es eine gute Torchance war. Also nicht unbedingt 6.50m. Evtl. nur Ecke. Christian: Ein Spieler der das Tor verschiebt eindeutig absichtlich um eine Situation zu "entschärfen" würde ich immer mit 6,50 m und 2 min bestrafen. Aber laut Regeln hat Rolf recht. Was eine gute und was nur eine Torchance ist? Dazu gibt die Regel keinen Hinweis. ',NULL),
    (153,'8','LF','Eine turbulente Szene vor dem roten Tor: Ein angreifender blauer Spieler hat das Tor unabsichtlich verschoben. Blau ist in einer aussichtsreichen Spielsituation. Was entscheidest du?','Ich unterbreche das Spiel und geben ein Torab für das rote Team.','Ich lasse das Spiel weiter laufen, sofern der Torwart das Tor schnell wieder gerade stellen kann.','Ich unterbreche das Spiel und gebe einen Bully am Eckpunkt.','Ich unterbreche das Spiel und gebe einen Eckball.',NULL,NULL,'1','8.6','In diesem Fall sollte man das Spiel nicht weiterlaufen lassen. Blau hat einen Regelverstoß begangen (Verschieben des Tores). Daraus darf sich keine Vorteilssituation für Blau entwickeln. Daher ist Unterbrechung und Torab hier die beste Entscheidung.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (154,'10','LF','Du hast ein grob unsportliches und gefährliches Foul eines roten Spielers gesehen. Du entscheidest jedoch auf Vorteil und lässt das Spiel mit einer entsprechenden Ansage (Handzeichen plus "Vorteil") weiterlaufen. Was musst du jetzt tun?','Nichts. Das Spiel läuft wegen Vorteil ja weiter. Es gibt dann auch keinen Feldverweis.','Ich warte bis das rote Team in Ballbesitz kommt (Vorteil Ende), oder das Spiel unterbrochen wird. Dann gebe ich dem roten Spieler einen Feldverweis.','Ich warte bis das rote Team in Ballbesitz kommt (Vorteil Ende), oder das Spiel unterbrochen wird. Dann ermahne ich den roten Spieler.','Es geht weiter mit Bully am Ort des Ballbesitzwechsels.',NULL,NULL,'2#4','9',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: Rot statt blau in Antwort 2 und 3?
Larissa: ja so ist es irgendwie komisch. Für mich stimmt 4 auch nicht, da ich in der Frage Vorteil gebe. Dann kann ich ja keinen Bully geben.  Rolf: Leider sind die offiziellen Regeln hier widersprüchlich: 1) "Wenn
ein Vorteil nicht erlangt wurde, sollte der Schiedsrichter die
ENTSPRECHENDE STRAFE für den ursprünglichen Regelverstoß AM
URSPRÜNGLICHEN ORT durchsetzen." 2) "...sollte das Spiel mit einem BULLY
AM ORT DES BALLBESITZWECHSELS wieder aufnehmen." Christian: Habe rot/blau verwechselung korrigiert. Ja, die Regel ist unklar. ',NULL),
    (155,'4','LF','Wer darf bei einem Penaltyschießen wie oft antreten? Weißt du es?','Jeder Spieler darf bei einem Penaltyschießen grundsätzlich nur einmal schießen.','Ein Spieler darf mehrfach schießen. Es muss jedoch mindestens ein anderer Spieler dazwischen schießen.','Ein Spieler darf mehrfach schießen. Es müssen jedoch immer mindestens zwei andere Spieler dazwischen schießen.','Der Torwart darf nicht schießen.','Der Torwart darf bei jedem Schuss beliebig wechseln.',NULL,'3#5','5.2a','Ob der Torwart beliebig wechseln kann, ist in den Regeln nicht definiert, es wird aber üblicherweise erlaubt.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (156,'4','L','Immer wieder spannend: Penaltyschießen. Wie läuft es ab?','Jedes Team bekommt zunächst drei Penaltys.','Ist der Spielstand nach 3 Penaltys noch Unentschieden, schießt jedes Team einen weiteren Penalty, bis eine Entscheidung herbeigeführt ist.','Jedes Team bekommt 5 Penaltys.','Wer zuerst trifft, hat gewonnen.',NULL,NULL,'1#2','5.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (157,'4','LF','Was ist bei der Durchführung von einem Penalty zu beachten?','Für den Penaltyschussmüssen alle Spieler außer dem verteidigenden Torwart die entsprechende Spielfeldhälfte verlassen.','Der Torwart muss sich in der Nähe der Torlinie befinden, bis der Angreifer den Ball berührt.','Der Spieler muss in Bewegung Richtung Tor bleiben, eine Rückwärtsbewegung oder Anhalten ist nicht zulässig.','Wenn der Ball vom Torwart abprallt, hat der Angreifer immer noch einen Nachschuss.',NULL,NULL,'1#2#3','5.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (158,'4','LF','Der Ball muss beim Penalty immer nach vorne bewegt werden, nie zurück. Stimmt das?','Ja.','Nein. Entscheidend ist, dass der angreifende Spieler sich immer nach vorne bewegt und nicht stehen bleibt/sich zurück bewegt.','Spieler und Ball dürfen sich vor und zurück bewegen.','Das liegt im Ermessen des Schiedsrichters.',NULL,NULL,'2','5.2','Achtung, diese Regel ist relativ neu.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (159,'4','LF','Beim Penalty geht es zur Sache: Der ballführende Spieler wird vom Torwart gefoult. Was nun?','Der Penalty wird wiederholt.','Der Penalty wird als "nicht getroffen" gewertet.','In seinem Ermessen entscheidet der Schiedsrichter auf Straftor oder 6,50 m.','Das Penaltyschießen. wird abgebrochen.',NULL,NULL,'3','5.2b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (160,'8','L','Handball? Der Ball wird von mehreren Spielern der gleichen Mannschaft hintereinander und abwechselnd aktiv mit der Hand geschlagen. Zwischendurch wird der Ball von niemanden berührt. Ist das erlaubt?','Ja, da jeder Spieler den Ball nur einmal aktiv berührt. Weiterspielen.','Ja, aber nur zwei Berührungen sind erlaubt.','Nein, der Ball darf nur von einem Spieler mit der Hand berührt werden. Dann muss der Ball mit dem Schläger gespielt werden.','Das ist gefährliches Spiel.',NULL,NULL,'1','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Aus der Frage geht nicht hervor, dass jeder Spieler den Ball nur einemal berührt. Frage 163 ist fast gleich, aber eindeutiger. Daher diese Frage löschen? Christian: Ja löschen. ',NULL),
    (161,'2','L','Du hast aus versehen gepfiffen. Scheinbar hat es niemand gehört. Die Spieler spielen weiter. Was musst du tun?','Es hat ja (vielleicht) keiner gehört, also kann das Spiel weiter gehen.','Wenn der Spielfluss nicht wesentlich beeinflusst wurde, kann das Spiel weiter gehen.','Ich rufe laut "weiter" und mache das Handzeichen für "Kein Foul".','Ich pfeife mehrmals laut und unterbreche das Spiel. Ich entschuldige mich für den falschen Pfiff. Das Spiel geht weiter mit Bully oder Freistoß für die Mannschaft im Ballbesitz nach meinem Ermessen.',NULL,NULL,'4','10.6a#9.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (162,'2','L','Du bist zweiter Schiedsrichter. Der erste Schiedsrichter hat ein Foul gepfiffen und entscheidet auf Freistoß. Du denkst, dass das Foul möglicherweise die Kriterien für einen 6,50 m erfüllt. Was tust Du?','Der erste Schiedsrichter überstimmt den zweiten Schiedsrichter. Daher sage ich nichts und halte mich raus.','Ich lasse die Zeit anhalten und bespreche die Situation mit dem ersten Schiedsrichter. Wir treffen dann gemeinsam eine Entscheidung.','Wenn keine gemeinsame Entscheidung zustande kommt, überstimmt der erste Schiedsrichter den Zweiten.','Da der erste Schiedsrichter erfahrener und älter ist als ist, halte ich mich raus.',NULL,NULL,'2#3','10.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (163,'8','L','Der Ball wird von mehreren Spielern der gleichen Mannschaft aktiv mit der Hand geschlagen. Jeder Spieler schlägt den Ball nur einmal. Dazwischen wird der Ball von niemandem berührt. Ist das erlaubt?','Nein, dies ist eine doppelte (mehrfache) aktive Ballberührung mit der Hand.','Abpfiff, Freistoß für die andere Mannschaft.','Die ist erlaubt, da jeder Spieler den Ball nur einmal aktiv spielt.','Weiterspielen.',NULL,NULL,'3#4','5.4',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (164,'9','L','Ein Freistoß aus dem Mittelfeld wird direkt aufs Tor geschossen. Der Ball geht ins Tor, ohne dass ihn ein anderer Spieler berührt.','Das Tor zählt nicht, da jeder Freistoß indirekt ist.','Das Tor zählt.','Es geht weiter mit Bully.','Es geht weiter mit Torab.',NULL,NULL,'1#4','9.1b',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (165,'9','LF','Fernschuss oder nicht? Worauf kommt es an?','Ob der Ball im Moment des Schusses vor oder hinter der Mittellinie war?','Ob das Einrad des Spielers im Moment des Schusses vor oder hinter der Mittellinie war?','Das Einrad des Spielers und der Ball müssen hinter der Mittellinie sein, damit das Tor zählt.','Auf die Position des Körpers des Spielers vor dem Schuss.',NULL,NULL,'1','7.2',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (166,'Regeln2, Videos','LF','Schaue Dir das Video genau an. Wie bewertest Du die Situation? Was würdest Du entscheiden?','Es liegt kein Foulspiel vor.','Hoher Schläger von Weiß - Eckball für Grün.','Hoher Schläger von Weiß - 6,50 m für Grün.','Vorteil für Grün. Daher weiterspielen.',NULL,NULL,'3',NULL,'Der grüne Spieler hat in dieser Situation eine klare Torchance, auch wenn er sich den Ball zunächst mit der Hand vorlegen möchte. Der weiße Spieler greift mit einem klaren "Hohen Schläger" von hinten auf den Körper des grünen Spielers ein. Damit stört er die direkte Torchance des grünen Spielers mit einem Foul. <BR> Nach Ende des Vorteils (die Torchance konnte nicht genutzt werden) ist somit auf 6,50 m für Grün zu entscheiden. <BR> Eine Bewertung des Fouls als "gefährliches Spiel" und 2 min Zeitstrafe liegt im Ermessen des Schiedsrichters.',1,NULL,'frage_0166.mp4','antwort_0166.jpg',NULL,'nein',NULL,NULL),
    (167,'Regeln2, Videos','LF','Schaue Dir das Video genau an. Wie bewertest Du die Situation? Was würdest Du entscheiden?','Das das Foul (SUB) abseits des Balles geschieht und das Spiel nicht beeinflusst - läuft das Spiel weiter.','Die grüne Spielerin foult den weißen Spieler - SUB.','Hoher Schläger von Grün.','Ich entscheide auf Hoher Schläger von Grün - Freistoß für Weiß.','Ich entscheide auf Sub von Grün - Freistoß für Weiß.',NULL,'2#3#4','8.10#8.3#10.6d','Diese Situation hat zwei Teile. Zunächst begeht die grüne Spielerin einen hohen Schläger beim Torschuss. Anschließend subt sie den weißen Spieler.  Das Sub erfolgt abseits des Spielgeschehens. Da Grün in Ballbesitz kommt und der weiße Spieler absteigen musste, kann man argumentieren, dass es trotzdem spielrelevant ist und mit einem Freistoß für Weiß zu ahnden ist. Es kommt jedoch zunächst 10.6 zur Anwendung: <BR> <i>Wenn zwei Regelverstöße direkt hintereinander auftreten, wird nur der erste bestraft.</i><BR> Der Schiedsrichter bestraft also den hohen Schläger beim Torschuss mit einem Freistoß für Weiß.',1,NULL,'frage_0167.mp4','antwort_0167.jpg',NULL,'nein',NULL,NULL),
    (168,'11','L','Einen 6,50 m kann es nur geben, wenn das Foul eine direkte Torchance verhindert hat UND innerhalb der Torzone (6,50 m Bereich) erfolgt ist. Richtig oder Falsch?','Falsch.','Richtig.',NULL,NULL,NULL,NULL,'1',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (169,'7','L','Ein roter Spieler stützt sich an der Wand ab, gleichzeitig spielt er den Ball im Zweikampf. Was entscheidest Du?','Es gibt Freistoß für das blaue Team.','Weiterspielen. Kurzzeitig darf man sich an der Wand abstützen.','Es gibt Bully.','Vorteilsentscheidung ist möglich.',NULL,NULL,'1#4',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (170,'7','L','Eine rote Spielerin stützt sich kurzzeitig an der Wand ab, um nicht zu stürzen. Dabei greift sie nicht ins Spiel ein. Was entscheidest Du?','Es gibt Freistoß für das blaue Team.','Weiterspielen, kurzzeitiges Abstützen ist erlaubt.','Ich ermahne die rote Spielerin, sich nicht abzustützen.','Es geht weiter mit Bully.',NULL,NULL,'2','5.3',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (171,'7','LF','Eine rote Spielerin stützt sich an der Wand ab, um nicht zu stürzen. Dann nutzt sie die Wand um sich abzudrücken und so einen Richtungswechsel vorzunehmen. Daraufhin erobert sie den Ball. Wie entscheidest Du?','Weiterspielen. Kurzzeitiges Festhalten an der Wand ist erlaubt.','Freistoß für Blau. Die Spielerin darf die Wand nicht zu ihrem Vorteil nutzen.','Weiterspielen. Aber ich ermahne sie bei nächster Gelegenheit.','Ich ermahne die Spielerin und es geht weiter mit Bully.',NULL,NULL,'2',NULL,NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (172,'7','LF','Der rote Torwart hält sich am Tor fest, während das Spiel in der anderen Spielhälfte stattfindet und keine Gefahr für sein Tor droht. Unterbrichst Du das Spiel?','Das Spiel läuft weiter, da das Festhalten am Tor das Spiel nicht beeinflusst.','Der Torwart darf sich nicht am Tor festhalten. Daher unterbreche ich das Spiel und ermahne den Torwart.','Ich unterbreche das Spiel und gebe Eckball für Blau.','Ich unterbreche das Spiel und gebe Freistoß für Blau.',NULL,NULL,'1','5.3#8.1',NULL,1,NULL,NULL,NULL,NULL,'nein','Rolf: ich würde noch ergänzen, bei nächster Spielunterbrechung Torwart ermahnen. Christian: Das ist auch unklar. Den Torwart ermahnen für ein Vergehen, dass man nicht bestrafen kann? Man darf nicht nicht festhalten am Tor wenn der Ball auf der anderen Seite ist... es wird aber auch nicht bestraft?',NULL),
    (173,'7','LF','Der rote Torwart hält sich am Tor fest, während Blau im Ballbesitz ist und angreift. Bisher kam es nicht zum Torschuss. Wie verhältst Du Dich?','Ich lasse das Spiel weiterlaufen (Vorteil).','Ich ermahne de Torwart bei nächster Gelegenheit, sich nicht am Tor festzuhalten, da es nicht reicht das Tor nur loszulassen um den Ball zu halten.','Ich unterbreche das Spiel und gebe Freistoß für Blau.','Ich unterbreche das Spiel, ermahne den Torwart. Es geht weiter mit Bully.',NULL,NULL,'1#2','5.3#8.1',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (174,'11','L','Worauf musst du bei der Durchführung eines 6,50 m achten?','Der Torwart darf nicht weiter als 50 cm von der Torlinie entfernt mit seinem Rad stehen.','Alle Spieler (außer dem Torwart und dem Schützen) müssen die Torzone verlassen.','Der Schuss darf nicht geschlenzt oder gelupft werden.','Wenn der Ball abprallt oder die Torlinie überquert, geht das Spiel weiter.',NULL,NULL,'1#2#4','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (175,'11','L','Es gibt einen 6,50 m für das rote Team. Was trifft zu?','Nur der gefoulte Spieler der roten Mannschaft darf den 6,50 m schießen.','Das angreifende rote Team bestimmt einen beliebigen Spieler, der den 6,50 m schießt.','Der blaue Torwart zum Zeitpunkt des Fouls muss Torwart bleiben.','Jeder blaue Spieler kann Torwart beim 6,50 m sein.',NULL,NULL,'2#4','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (176,'11','LF','Ein 6,50m für das rote Team! Der Schütze schießt am Tor vorbei. Der Ball geht gegen die Wand und kommt zurück vor das Tor. Der rote 6,50m Schütze schießt den Ball direkt wieder in das Tor, ohne das ihn jemand anderes berührt hat.','Das Tor zählt nicht, da der Schütze den Ball nur einmal berühren darf. ','Das Tor zählt. Wenn der Ball abprallt oder die verlängerte Torauslinie überschreitet, geht das Spiel weiter.','Es geht weiter mit Bully am Eckpunkt.','Es gibt Torab.',NULL,NULL,'2','9.2d',NULL,1,NULL,NULL,NULL,NULL,'nein','Larissa: Ist das echt so? Ich dachte immer, dass da wie beim freistoß jemand anderes dran sein muss bevor ich den wieder berühren darf. Christian: Regel ist auch hier unklar. Vor einem Tor darf der Schütze nur einmal berühren. Überschreitet der Ball die Torlinie geht es "weiter". Unklar, ob der Schütze dann sofort eingreifen kann. ','Nebenbei: "Der Ball soll mit dem Schläger geschlagen werden. Der Ball darf ebenfalls geschlenzt und gelupft werden." ist nur wirklich Quatsch. Soll-Vorschriften sind da ja Unsinn. Entweder darf ich schlenzen oder nicht.  (Christian). '),
    (177,'5','LF','Zwei Spieler fahren im Kampf um den Ball in einem stumpfen Winkel aufeinander zu, sie kollidieren und beide stürzen. Welcher Grundsatz kann Dir helfen eine Entscheidung zu treffen?','Im Zweifel das Spiel immer weiterlaufen lassen.','Es wird der Spieler betraft, der den Kontakt anscheinend herbeigeführt hat.','Es wird immer der Spieler betraft, der am weitesten vom Ball entfernt ist.','Es wird immer der schnellere Spieler betraft.',NULL,NULL,'2','8.2e',NULL,1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (178,'7','LJ','Drei Spieler prallen zusammen und stürzen. Beide Teams sind beteiligt, der Ball liegt unter einem Einrad. Es ist nicht klar, wer daran Schuld ist.','Bully dort, wo der Ball liegt. An dem Bully nehmen alle drei beteiligten Spieler teil.','Freistoß gegen den Spieler, unter dessen Einrad der Ball liegt.','Bully dort, wo der Ball liegt. Die Teams suchen die beiden Spieler aus, die den Bully ausführen.','Bully dort, wo der Ball liegt. Der Schiedsrichter sucht zwei von den beteiligten Spielern aus, die den Bully ausführen sollen.',NULL,NULL,'3','9b#9.4','Einen Bully für 3 Spieler gibt es nicht.',1,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (179,'3','L','Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','5 min Pause','6.50 m','Fünffaches Handspiel','Hoher Schläger',NULL,'3','10.7c',NULL,1,'frage_0179.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (180,'3','L','Was bedeutet dieses Handzeichen?','Feldverweis für 5 min','Freistoß','Tor','Fünffaches Handspiel','Hoher Schläger',NULL,'2','10.7a',NULL,1,'frage_0180.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (181,'3','L','Was bedeutet dieses Handzeichen?','Feldverweis für 2 min','5 min Pause','Mehr als 5 Spieler auf dem Feld','Fünffaches Handspiel','Bully',NULL,'5','10.7b',NULL,1,'frage_0181.png',NULL,NULL,NULL,'nein',NULL,NULL),
    (182,NULL,'LJB','Schaue Dir das Video an. Wie entscheidest du?','Tor','Ecke','Torab','6.50 m','Keine Spielunterbrechung',NULL,'1','9.3','Am Anfang der Szene hält sich der Torwart am Tor fest. Das sollte wegen Vorteil noch nicht gepfiffen werden. Dann hält der Torwart einen Ball, der sicher ins Tor gegangen wäre. Dafür gibt es ein Straftor.',1,NULL,'frage_0182.mp4',NULL,NULL,'nein',NULL,NULL),
    (183,'2','LJB','Es steht 8:7 für Blau und es sind noch 10 Sekunden zu spielen. Der Ball geht ins Aus und landet im Publikum. Du pfeifst eine Ecke für Rot. Wie verhälst du dich?','Ich warte ab. Jemand im Publikum wird den Ball schon wieder zurück werfen.','Es ist egal wie lange das Spiel noch geht, die Regeln zum Durchführen einer Ecke sind immer gleich.','Ich mache sofort Zeitstopp bis die Ecke ausgeführt werden kann. Schließlich soll Rot ja noch eine Chance erhalten ein Tor zu erzielen.','Die Zeit läuft weiter, aber ich beühme mich, den Ball so schnell wie möglich auf den Eckpunkt zu legen. Idealerweise habe ich einen Ersatzball in der Hand.',NULL,NULL,'2#4','5.7',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (184,'2','LJB','Du bist erster Schiedsrichter. Während des Spiels bemerkst du, dass der zweite Schiedsrichter sich mit dem Publikum unterhält und nicht mehr aufmerksam dem Spiel folgt.','Ungeachtet dessen, was gerade im Spiel passiert, unterbreche ich das Spiel sofort und spreche mit ihm.','Kein Problem. Ich bin erster Schiedsrichter und darf alles alleine entscheiden.','Ich warte bis zur nächsten Unterbrechung, mache einen Zeitstopp und spreche mit ihm.','Er verhält sich nicht richtig. Als Schiedsrichter muss man aktiv dabei sein, mitlaufen und mit dem Blick dem Spiel folgen.',NULL,NULL,'3#4','10.6c#10.2a',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (185,'2','LJB','Du möchtest das Spiel anpfeifen. Kurz vorher bemerkst du, dass der zweite Schiedsrichter dieselbe T-Shirt Farbe hat, wie eins der Teams.','Es ist notwendig, dass beide Schiedsrichter dieselbe Farbe tragen. Wir müssen uns vorher einigen welche Farbe wir tragen.','Das ist mir egal. Hauptsache die Teams unterscheiden sich in den Farben.','Ich pfeife nicht an, sondern mache ihn darauf aufmerksam. Wenn er sich umgezogen hat, beginnen wir das Spiel.','Ich pfeife trotzdem an. Er wird das schon noch selbst bemerken.',NULL,NULL,'3','10.2a','Es ist wünschenwert, aber nicht zwingend notwendig, dass beide Schiedsrichter dieselbe Farbe tragen.',NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (186,'2','LJB','Wo befindest du dich während des Spiels?','Ich bleibe in Ballnähe, halte aber etwas Abstand.','Ich bleibe auf meiner Seite vor der Bande an der Mittellinie stehen.','Immer direkt am Ball. Je näher ich dran bin, desto besser.','Ich laufe an der Seitenlinie mit, weiche aber, falls nötig, zur Mitte aus.',NULL,NULL,'1#4','10.2a','Du solltest dich bewegen und mit dem Spiel mitlaufen. Aber nicht die Spieler stören. Es kann hilfreich sein das Spielgeschehen mit etwas Abstand zu betrachten, um einen Überblick zu haben.',NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (187,'1','LJB','Was musst du beim Auswechseln von Spielern beachten?','Wechseln ist nur in der eigenen Hälfte erlaubt.','Die Spieler müssen es mir vorher mitteilen, wenn sie auswechseln möchten.','Solange er nicht ins Spiel eingreift, kann der neue Spieler kann das Feld schon einmal betreten und aufsteigen, auch wenn der andere Spieler noch auf dem Feld ist.','Auswechseln geht nur nach Spielunterbrechungen.',NULL,NULL,'1','4',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (188,'1','LJB','Was musst du beim Auswechseln von Spielern beachten?','Es ist nicht nötig, dies dem Schiedsrichter vorher mitzuteilen.','Auswechseln eines Spielers ist jederzeit möglich.','Es kann an jeder Stelle gewechselt werden.','Der neue Spieler darf das Spielfeld erst betreten, nachdem der ausscheidende Spieler das Spielfeld vollständig verlassen hat',NULL,NULL,'1#2#4','4',NULL,NULL,NULL,NULL,NULL,NULL,'nein',NULL,NULL),
    (189,'4','LF','Wie viele Spieler von jedem Team treten mindestens bei einem Penatlyschießen an?','1','2','3','4',NULL,NULL,'3','5.2a',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Ist vielleicht nicht 100% korrekt, wenn es nach dem ersten Schützen von Team A schon 3:0 steht Christian: In den Regeln steht nicht, dass das Penaltyschießen zuende ist, wenn es entschieden ist. Laut Regeln müssen immer mindestens 3 schießen. Vielleicht umformulieren?',NULL),
    (190,'4','LF','Ist es möglich, dass ein Spieler mehr als einen Schuss beim Penaltyschießen machen kann?','Ja, aber es muss mindestens ein anderer Spieler seines Teams einen Schuss machen, bevor derselbe Spieler nochmal schießen kann.','Ja, aber es müssen mindestens zwei andere Spieler seines Teams einen Schuss machen, bevor derselbe Spieler nochmal schießen kann.','Ja, aber maximal zwei mal.','Nein, jeder Spieler darf nur einmal schießen.',NULL,NULL,'2','5.2a',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (191,'4','LF','Der Torwart darf während eines Penaltyschießens gewechselt werden.','Richtig',' Falsch',NULL,NULL,NULL,NULL,'1','4#5.2','Jeder Spieler kann zu jeder Zeit Torwart sein.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (192,'4','LF','Was darf ein angeifender Spieler während eines Penalties?','Vorwärts fahren','Rückwarts fahren','Anhalten ','Pendeln',NULL,NULL,'1','5.2b',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (193,'4','LF','Beim Penaltyschießen schießt der Spieler am Tor vorbei, der Ball kommt von der Wand zurück, prallt gegen den Schläger des Torwarts und geht ins Tor. Wie entscheidest du?','Kein Tor, beim Penaltyschießen können Abpraller nie zum Tor führen.','Tor, wenn der Ball vom Torwart abprallt, gilt das Tor.','Kein Tor, sobald der Ball die Torlinie überquert, wird der Schuss als komplett angesehen.','Tor, wäre der Ball an den Schläger des Schützen gesprungen, hätte das Tor nicht gezählt.',NULL,NULL,'3','5.2b',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (194,'4','LF','Beim Penaltyschießen foult der Torwart den Schützen. Der Ball wäre nicht ins Tor gegangen. Wie entscheidest du?','Der Penalty ist vergeben.','Tor','6,5m','Wiederholung des Penalties.',NULL,NULL,'3','5.2b','Wird der Ballführende gefoult, gibt der Schiedsrichter nach Ermessen ein Tor oder einen 6,50 m. Das der Ball nicht rein gegangen wäre, ist der 6,5m hier die richtige Wahl.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),
    (195,'4','LF','Beim Penaltyschießen foult der Schütze den Torwart. Der Ball wäre ins Tor gegangen. Wie entscheidest du?','Der Penalty ist vergeben.','Tor','6,5m','Wiederholung des Penalties.',NULL,NULL,'1','5.2b','Wird der Torwart beim Penalty gefoult, ist dieser Penalty-Versuch vergeben.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
